import { useState } from "react";
import { FilePlus, PlusCircle, ArrowLeft } from "lucide-react";

export default function Contabilidad() {
  const [vista, setVista] = useState("general");
  const [formularioActivo, setFormularioActivo] = useState(null);
  const [tipoGastoNuevo, setTipoGastoNuevo] = useState("");
  const [medioAmpliacion, setMedioAmpliacion] = useState("");
  const [categoriasUsadas, setCategoriasUsadas] = useState(["Combustible", "Transporte", "Personal"]);
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState(null);
  const [descripcion, setDescripcion] = useState("");
  const [fecha, setFecha] = useState("");
  const [monto, setMonto] = useState("");
  const [documento, setDocumento] = useState("");

  const [proyectos, setProyectos] = useState([
    {
      id: 1,
      nombre: "Las Pilas",
      destinado: 50000,
      gastado: 33400,
      estado: "activo",
      detalleVisible: false,
      gastos: [],
      aumentos: []
    },
    {
      id: 2,
      nombre: "Taxisco",
      destinado: 60000,
      gastado: 48500,
      estado: "activo",
      detalleVisible: false,
      gastos: [],
      aumentos: []
    }
  ]);

  const guardarGasto = () => {
    if (!proyectoSeleccionado || !tipoGastoNuevo || !descripcion || !fecha || !monto) return;

    const nuevoGasto = {
      fecha,
      descripcion,
      monto: parseFloat(monto),
      categoria: tipoGastoNuevo
    };

    setProyectos((prev) =>
      prev.map((p) =>
        p.nombre === proyectoSeleccionado
          ? {
              ...p,
              gastos: [...p.gastos, nuevoGasto],
              gastado: p.gastado + nuevoGasto.monto,
            }
          : p
      )
    );

    if (!categoriasUsadas.includes(tipoGastoNuevo)) {
      setCategoriasUsadas([...categoriasUsadas, tipoGastoNuevo]);
    }

    setFormularioActivo(null);
    setProyectoSeleccionado(null);
    setTipoGastoNuevo("");
    setDescripcion("");
    setFecha("");
    setMonto("");
  };

  const guardarAmpliacion = () => {
    if (!proyectoSeleccionado || !medioAmpliacion || !documento || !fecha || !monto) return;

    const nuevaAmpliacion = {
      fecha,
      descripcion: `Ampliaci√≥n por ${medioAmpliacion} - Doc: ${documento}`,
      monto: parseFloat(monto)
    };

    setProyectos((prev) =>
      prev.map((p) =>
        p.nombre === proyectoSeleccionado
          ? {
              ...p,
              aumentos: [...p.aumentos, nuevaAmpliacion],
              destinado: p.destinado + nuevaAmpliacion.monto,
            }
          : p
      )
    );

    setFormularioActivo(null);
    setProyectoSeleccionado(null);
    setMedioAmpliacion("");
    setDocumento("");
    setFecha("");
    setMonto("");
  };

    return (
    <div className="p-6 space-y-6">
      {proyectos.map((proyecto) => (
        <div key={proyecto.id} className="bg-white shadow rounded-xl p-6">
          <h2 className="text-xl font-bold text-gray-800 mb-2">üìÅ {proyecto.nombre}</h2>
          <p className="text-sm text-gray-700">üíµ Destinado: Q{proyecto.destinado.toFixed(2)}</p>
          <p className="text-sm text-gray-700">üì§ Gastado: Q{proyecto.gastado.toFixed(2)}</p>
          <p className="text-sm text-gray-700">üíö Restante: Q{(proyecto.destinado - proyecto.gastado).toFixed(2)}</p>

          {proyecto.aumentos.length > 0 && (
            <div className="mt-4">
              <h4 className="text-md font-bold text-green-700 mb-2">üíπ Ampliaciones de presupuesto:</h4>
              <ul className="list-disc ml-5 text-sm text-gray-700">
                {proyecto.aumentos.map((a, i) => (
                  <li key={i}>{a.fecha} - {a.descripcion}: Q{a.monto.toFixed(2)}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      ))}
    </div>
  );

}
