          {/* Botones */}
          <div className="flex justify-end gap-4 mt-4">
            <button
              onClick={() => {
                setMostrarFormulario(false);
                setNuevoProyecto({ nombre: "", descripcion: "" });
                setSupervisoresSeleccionados([]);
                setTrabajadoresSeleccionados([]);
                setTrabajos([{ nombre: "", unidades: "" }]);
