import { useEffect, useState } from "react";
import { supabase } from "../supabase";

export default function Proyectos() {
  const [proyectos, setProyectos] = useState([]);
  const [nuevoProyecto, setNuevoProyecto] = useState({ nombre: "", descripcion: "" });

  const obtenerProyectos = async () => {
    const { data, error } = await supabase.from("proyectos").select("*").order("id", { ascending: true });
    if (!error) setProyectos(data);
    else console.error("Error al obtener proyectos", error);
  };

  const agregarProyecto = async () => {
    if (!nuevoProyecto.nombre.trim()) return alert("Debes ingresar un nombre");
    const { error } = await supabase.from("proyectos").insert([nuevoProyecto]);
    if (!error) {
      setNuevoProyecto({ nombre: "", descripcion: "" });
      obtenerProyectos();
    } else {
      console.error("Error al agregar proyecto", error);
      alert("Error al guardar el proyecto");
    }
  };

  useEffect(() => {
    obtenerProyectos();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Gestión de Proyectos</h2>

      <div className="mb-6 bg-white p-4 rounded shadow">
        <h3 className="text-xl font-semibold mb-2">Agregar nuevo proyecto</h3>
        <input
          type="text"
          placeholder="Nombre del proyecto"
          className="border p-2 w-full mb-2 rounded"
          value={nuevoProyecto.nombre}
          onChange={(e) => setNuevoProyecto({ ...nuevoProyecto, nombre: e.target.value })}
        />
        <textarea
          placeholder="Descripción"
          className="border p-2 w-full mb-2 rounded"
          value={nuevoProyecto.descripcion}
          onChange={(e) => setNuevoProyecto({ ...nuevoProyecto, descripcion: e.target.value })}
        ></textarea>
        <button
          onClick={agregarProyecto}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Guardar Proyecto
        </button>
      </div>

      <div className="bg-white p-4 rounded shadow">
        <h3 className="text-xl font-semibold mb-2">Lista de proyectos</h3>
        <ul className="divide-y">
          {proyectos.map((proy) => (
            <li key={proy.id} className="py-2">
              <span className="font-bold">{proy.nombre}</span> – {proy.descripcion}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
