import React, { useState } from "react";
import { supabase } from "../supabase";

export default function ModificarTrabajos({ persona, onCerrar }) {
  const [modo, setModo] = useState("actual"); // actual o fecha-anterior
  const [fechaModificar, setFechaModificar] = useState("");
  const [datos, setDatos] = useState({
    horasExtras: "",
    unidadesMeta: "",
    unidadesBonificacion: "",
    bonificacionExtra: "",
    pagoExtraordinario: "",
    grupo: "",
  });

  const handleInputChange = (e) => {
    setDatos({
      ...datos,
      [e.target.name]: e.target.value,
    });
  };

  const handleGuardar = async () => {
    try {
      let filtro = { nombretrabajador: persona.nombrecompleto };
      if (modo === "fecha-anterior") {
        filtro.fechareporte = fechaModificar;
      } else {
        filtro.fechareporte = new Date().toISOString().slice(0, 10);
      }

      const { data, error } = await supabase
        .from("reportesdiarios")
        .update({
          horas_extras: parseFloat(datos.horasExtras || 0),
          metaestablecida: parseInt(datos.unidadesMeta || 0),
          cantidad: parseInt(datos.unidadesBonificacion || 0),
          extra: parseFloat(datos.bonificacionExtra || 0),
          pagoextraordinario: parseFloat(datos.pagoExtraordinario || 0),
          numerogrupo: datos.grupo || "",
        })
        .match(filtro);

      if (error) throw error;

      alert("¡Datos modificados correctamente!");
      onCerrar(); // Cierra el formulario después de guardar
    } catch (error) {
      console.error(error);
      alert("Error al guardar cambios.");
    }
  };

  return (
    <div className="bg-white p-4 rounded-xl shadow mt-4">
      <h4 className="font-semibold text-gray-800 mb-4">⚙️ Modificar Trabajos</h4>

      <div className="flex gap-2 mb-4">
        <button
          onClick={() => setModo("actual")}
          className={`px-3 py-1 rounded shadow ${
            modo === "actual" ? "bg-green-600 text-white" : "bg-gray-200"
          }`}
        >
          Modificar planilla actual
        </button>
        <button
          onClick={() => setModo("fecha-anterior")}
          className={`px-3 py-1 rounded shadow ${
            modo === "fecha-anterior" ? "bg-green-600 text-white" : "bg-gray-200"
          }`}
        >
          Modificar fecha anterior
        </button>
      </div>

      {modo === "fecha-anterior" && (
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Seleccionar fecha</label>
          <input
            type="date"
            value={fechaModificar}
            onChange={(e) => setFechaModificar(e.target.value)}
            className="border p-2 rounded w-full"
          />
        </div>
      )}

      {/* Campos del formulario */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
        <div>
          <label className="block text-sm font-medium mb-1">Horas extras</label>
          <input
            type="number"
            name="horasExtras"
            value={datos.horasExtras}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Unidades establecidas</label>
          <input
            type="number"
            name="unidadesMeta"
            value={datos.unidadesMeta}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Unidades hechas</label>
          <input
            type="number"
            name="unidadesBonificacion"
            value={datos.unidadesBonificacion}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Bonificación por unidades extras (Q)</label>
          <input
            type="number"
            name="bonificacionExtra"
            value={datos.bonificacionExtra}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Pago extraordinario (domingo)</label>
          <input
            type="number"
            name="pagoExtraordinario"
            value={datos.pagoExtraordinario}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Número de grupo</label>
          <input
            type="text"
            name="grupo"
            value={datos.grupo}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
      </div>

      {/* Botón guardar */}
      <div className="flex justify-center mt-4">
        <button
          onClick={handleGuardar}
          className="bg-green-600 text-white px-4 py-2 rounded-2xl shadow hover:bg-green-700 transition"
        >
          Guardar
        </button>
      </div>

      {/* Botón cerrar */}
      <div className="flex justify-center mt-2">
        <button
          onClick={onCerrar}
          className="bg-red-500 text-white px-4 py-1 rounded-2xl hover:bg-red-600 transition"
        >
          Cerrar
        </button>
      </div>
    </div>
  );
}
