import React, { useState, useEffect } from "react";
import { supabase } from "../supabase";

export default function ModificarTrabajos({ persona, onCerrar }) {
  const [fechaSeleccionada, setFechaSeleccionada] = useState("");
  const [datosFormulario, setDatosFormulario] = useState({
    salarioBase: "",
    horasExtras: "",
    unidadesEstablecidas: "",
    unidadesHechas: "",
    bonificacionExtra: "",
    pagoDomingo: "",
    grupo: "",
  });

  // Carga automática de datos actuales o de la fecha seleccionada
  useEffect(() => {
    const cargarDatos = async () => {
      if (fechaSeleccionada) {
        // Buscar datos por fecha
        const { data, error } = await supabase
          .from("reportesdiarios")
          .select("*")
          .eq("nombretrabajador", persona.nombrecompleto)
          .eq("fechareporte", fechaSeleccionada)
          .single();

        if (error || !data) {
          console.error(error);
          alert("No se encontraron datos para la fecha seleccionada.");
        } else {
          setDatosFormulario({
            salarioBase: persona.modalidad === "fijo" ? persona.salarioporquincena || "" : "",
            horasExtras: data.horas_extras || "",
            unidadesEstablecidas: data.metaestablecida || "",
            unidadesHechas: data.cantidad || "",
            bonificacionExtra: data.extra || "",
            pagoDomingo: data.pagoextraordinario || "",
            grupo: data.numerogrupo || "",
          });
        }
      } else {
        // Datos actuales de la planilla
        setDatosFormulario({
          salarioBase: persona.modalidad === "fijo" ? persona.salarioporquincena || "" : "",
          horasExtras: persona.horas_extras || "",
          unidadesEstablecidas: persona.metaestablecida || "",
          unidadesHechas: persona.cantidad || "",
          bonificacionExtra: persona.extra || "",
          pagoDomingo: persona.pagoextraordinario || "",
          grupo: persona.numerogrupo || "",
        });
      }
    };

    cargarDatos();
  }, [fechaSeleccionada, persona]);

  const handleGuardar = async () => {
    try {
      // Actualiza tabla de personal si es planilla actual
      const { error: errorPersonal } = await supabase
        .from("registrodepersonal")
        .update({
          salarioporquincena: parseInt(datosFormulario.salarioBase, 10) || 0,
          horas_extras: parseInt(datosFormulario.horasExtras, 10) || 0,
          metaestablecida: parseInt(datosFormulario.unidadesEstablecidas, 10) || 0,
          cantidad: parseInt(datosFormulario.unidadesHechas, 10) || 0,
          extra: parseFloat(datosFormulario.bonificacionExtra) || 0,
          pagoextraordinario: parseFloat(datosFormulario.pagoDomingo) || 0,
          numerogrupo: datosFormulario.grupo || "",
        })
        .eq("id", persona.id);

      if (errorPersonal) throw errorPersonal;

      // Si hay fecha seleccionada, actualiza también el reporte diario
      if (fechaSeleccionada) {
        const { error: errorReporte } = await supabase
          .from("reportesdiarios")
          .update({
            horas_extras: parseInt(datosFormulario.horasExtras, 10) || 0,
            metaestablecida: parseInt(datosFormulario.unidadesEstablecidas, 10) || 0,
            cantidad: parseInt(datosFormulario.unidadesHechas, 10) || 0,
            extra: parseFloat(datosFormulario.bonificacionExtra) || 0,
            pagoextraordinario: parseFloat(datosFormulario.pagoDomingo) || 0,
            numerogrupo: datosFormulario.grupo || "",
          })
          .eq("nombretrabajador", persona.nombrecompleto)
          .eq("fechareporte", fechaSeleccionada);

        if (errorReporte) throw errorReporte;
      }

      alert("¡Cambios guardados exitosamente!");
      onCerrar();
    } catch (error) {
      console.error(error);
      alert("Error al guardar los cambios.");
    }
  };

  return (
    <div className="bg-white rounded-xl shadow p-4 mt-4">
      <h4 className="text-lg font-bold mb-4 flex items-center gap-2">
        ⚙️ Modificar Trabajos de {persona.nombrecompleto}
      </h4>

      <div className="mb-4">
        <label className="block font-medium text-gray-700 mb-1">Seleccionar fecha (opcional)</label>
        <input
          type="date"
          value={fechaSeleccionada}
          onChange={(e) => setFechaSeleccionada(e.target.value)}
          className="border p-2 rounded w-full"
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        {persona.modalidad === "fijo" && (
          <div>
            <label className="block font-medium text-gray-700 mb-1">Salario base (Q)</label>
            <input
              type="number"
              value={datosFormulario.salarioBase}
              onChange={(e) =>
                setDatosFormulario({ ...datosFormulario, salarioBase: e.target.value })
              }
              className="border p-2 rounded w-full"
            />
          </div>
        )}

        <div>
          <label className="block font-medium text-gray-700 mb-1">Horas extras</label>
          <input
            type="number"
            value={datosFormulario.horasExtras}
            onChange={(e) =>
              setDatosFormulario({ ...datosFormulario, horasExtras: e.target.value })
            }
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium text-gray-700 mb-1">Unidades establecidas</label>
          <input
            type="number"
            value={datosFormulario.unidadesEstablecidas}
            onChange={(e) =>
              setDatosFormulario({ ...datosFormulario, unidadesEstablecidas: e.target.value })
            }
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium text-gray-700 mb-1">Unidades hechas</label>
          <input
            type="number"
            value={datosFormulario.unidadesHechas}
            onChange={(e) =>
              setDatosFormulario({ ...datosFormulario, unidadesHechas: e.target.value })
            }
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium text-gray-700 mb-1">Bonificación por unidades extras (Q)</label>
          <input
            type="number"
            value={datosFormulario.bonificacionExtra}
            onChange={(e) =>
              setDatosFormulario({ ...datosFormulario, bonificacionExtra: e.target.value })
            }
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium text-gray-700 mb-1">Pago extraordinario (domingo)</label>
          <input
            type="number"
            value={datosFormulario.pagoDomingo}
            onChange={(e) =>
              setDatosFormulario({ ...datosFormulario, pagoDomingo: e.target.value })
            }
            className="border p-2 rounded w-full"
          />
        </div>

        <div className="md:col-span-2">
          <label className="block font-medium text-gray-700 mb-1">Número de grupo</label>
          <input
            type="text"
            value={datosFormulario.grupo}
            onChange={(e) =>
              setDatosFormulario({ ...datosFormulario, grupo: e.target.value })
            }
            className="border p-2 rounded w-full"
          />
        </div>
      </div>

      <div className="flex gap-2 justify-center">
        <button
          onClick={handleGuardar}
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:bg-green-700"
        >
          Guardar
        </button>
        <button
          onClick={onCerrar}
          className="bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600"
        >
          Cerrar
        </button>
      </div>
    </div>
  );
}
