import { useState } from "react";

export default function Contabilidad() {
  const [vista, setVista] = useState("general");
  const [proyectos, setProyectos] = useState([
    {
      id: 1,
      nombre: "Las Pilas",
      destinado: 50000,
      gastado: 33400,
      estado: "activo",
      detalleVisible: false,
      gastos: [
        { fecha: "2025-05-01", descripcion: "Compra de paneles", monto: 15000 },
        { fecha: "2025-05-05", descripcion: "Pago de personal", monto: 12000 },
        { fecha: "2025-05-10", descripcion: "Transporte", monto: 6400 }
      ],
      aumentos: [
        { fecha: "2025-05-15", descripcion: "Ajuste por ampliaciÃ³n", monto: 5000 }
      ]
    },
    {
      id: 2,
      nombre: "Taxisco",
      destinado: 60000,
      gastado: 48500,
      estado: "activo",
      detalleVisible: false,
      gastos: [],
      aumentos: []
    }
  ]);

  const finalizarProyecto = (id) => {
    const confirmar = confirm("Â¿EstÃ¡s seguro de finalizar este proyecto? Ya no podrÃ¡ ser modificado ni recibir mÃ¡s registros.");
    if (!confirmar) return;
    setProyectos(prev => prev.map(p => p.id === id ? { ...p, estado: "finalizado" } : p));
  };

  const toggleDetalle = (id) => {
    setProyectos(prev =>
      prev.map(p =>
        p.id === id ? { ...p, detalleVisible: !p.detalleVisible } : p
      )
    );
  };

  return (
    <div className="p-6 space-y-6">
      {/* Tarjetas resumen */}
      {vista === "general" && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-green-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Total ingresos</p>
            <h2 className="text-xl font-bold text-green-700">Q0.00</h2>
          </div>
          <div className="bg-red-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Total egresos</p>
            <h2 className="text-xl font-bold text-red-700">Q0.00</h2>
          </div>
          <div className="bg-yellow-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Caja chica disponible</p>
            <h2 className="text-xl font-bold text-yellow-700">Q0.00</h2>
          </div>
          <div className="bg-blue-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Balance general</p>
            <h2 className="text-xl font-bold text-blue-700">Q0.00</h2>
          </div>
        </div>
      )}

      {/* Tarjeta de Proyectos en curso */}
      {vista === "general" && (
        <div className="flex justify-center mt-8">
          <div
            onClick={() => setVista("proyectos")}
            className="cursor-pointer bg-white shadow-lg rounded-2xl p-6 text-center max-w-xs hover:shadow-xl"
          >
            <p className="text-xl font-bold text-purple-700">ðŸ“¦ Proyectos en curso</p>
            <p className="text-sm text-gray-500 mt-2">Haz clic para ver el resumen por proyecto</p>
          </div>
        </div>
      )}

      {/* Vista de proyectos */}
      {vista === "proyectos" && (
        <>
          <div className="flex justify-end">
            <button
              onClick={() => setVista("general")}
              className="bg-purple-100 px-4 py-2 rounded-xl text-purple-800 hover:bg-purple-200"
            >
              ðŸ”™ Volver al balance general
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
            {proyectos.filter(p => p.estado !== "finalizado").map((proyecto) => (
              <div key={proyecto.id} className="bg-white shadow rounded-2xl p-4 relative">
                <button
                  onClick={() => finalizarProyecto(proyecto.id)}
                  className="absolute top-4 right-4 bg-red-100 text-red-700 px-3 py-1 rounded hover:bg-red-200"
                >
                  Finalizar Proyecto
                </button>

                <h3 className="text-lg font-bold text-gray-700">Proyecto: {proyecto.nombre}</h3>
                <p className="text-sm text-gray-600 mt-1">ðŸ’µ Destinado: Q{proyecto.destinado.toFixed(2)}</p>
                <p className="text-sm text-gray-600">ðŸ“¤ Gastado: Q{proyecto.gastado.toFixed(2)}</p>
                <p className="text-sm text-gray-600">ðŸ’š Restante: Q{(proyecto.destinado - proyecto.gastado).toFixed(2)}</p>

                <button
                  onClick={() => toggleDetalle(proyecto.id)}
                  className="mt-4 bg-blue-100 text-blue-700 px-3 py-1 rounded hover:bg-blue-200"
                >
                  {proyecto.detalleVisible ? "Ocultar Detalle" : "Mostrar Detalle"}
                </button>

                {proyecto.detalleVisible && (
                  <div className="mt-4 text-sm text-gray-700">
                    <h4 className="font-bold text-md">ðŸ“„ Gastos:</h4>
                    <ul className="list-disc ml-5 mb-2">
                      {proyecto.gastos.map((gasto, i) => (
                        <li key={i}>{gasto.fecha} - {gasto.descripcion}: Q{gasto.monto}</li>
                      ))}
                      {proyecto.gastos.length === 0 && <li>No hay gastos registrados.</li>}
                    </ul>
                    <h4 className="font-bold text-md">âž• Aumentos de presupuesto:</h4>
                    <ul className="list-disc ml-5">
                      {proyecto.aumentos.map((aumento, i) => (
                        <li key={i}>{aumento.fecha} - {aumento.descripcion}: Q{aumento.monto}</li>
                      ))}
                      {proyecto.aumentos.length === 0 && <li>No hay aumentos registrados.</li>}
                    </ul>
                  </div>
                )}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}
