import { useState } from 'react';
import Login from './Login';

function App() {
  const [usuario, setUsuario] = useState(null);
  const esAdmin = usuario?.usuario === "admin" && usuario?.contrasena === "admin123";
  if (!usuario) return <Login onLogin={setUsuario} />;

  if (usuario.rol === 'admin') return <h1 className='text-2xl font-bold text-center mt-20'>Bienvenido Administrador</h1>;
  if (usuario.rol === 'contabilidad') return <h1 className='text-2xl font-bold text-center mt-20'>Bienvenido Usuario de Contabilidad</h1>;
  if (usuario.rol === 'reportador') return <h1 className='text-2xl font-bold text-center mt-20'>Bienvenido Reportador</h1>;

  return <h1>Rol desconocido</h1>;
}

export default App;
