import { useState, useEffect } from 'react';
import { supabase } from '../supabase';

export default function Personal() {
  const [vista, setVista] = useState('planilla');
  const [personal, setPersonal] = useState([]);
  const [reportes, setReportes] = useState([]);
  const [historialVisible, setHistorialVisible] = useState(null);
  const [nuevoPersonal, setNuevoPersonal] = useState({
    nombre: '',
    puesto: '',
    proyecto: '',
    salario: '',
    meta: '',
    fecha: '',
    modalidad: 'dÃ­a',
    bonificacion: '',
    precioporcantidad: '',
    trabajometa: ''
  });

  useEffect(() => {
    obtenerPersonal();
    obtenerReportes();
  }, []);

  const obtenerPersonal = async () => {
    const { data, error } = await supabase
      .from('registrodepersonal')
      .select('*')
      .order('created_at', { ascending: false });
    if (!error) setPersonal(data);
  };

  const obtenerReportes = async () => {
    const { data, error } = await supabase
      .from('reportesdiarios')
      .select('*');
    if (!error) setReportes(data);
  };

  const handleAgregar = async () => {
    if (!nuevoPersonal.nombre || !nuevoPersonal.puesto || (!nuevoPersonal.salario && nuevoPersonal.modalidad === 'dÃ­a')) {
      alert('El nombre, puesto y salario/valor por unidad son obligatorios');
      return;
    }

    const { data: existente } = await supabase
      .from('registrodepersonal')
      .select('*')
      .eq('nombrecompleto', nuevoPersonal.nombre)
      .single();

    const meta = parseInt(nuevoPersonal.meta);
    const bonificacion = parseInt(nuevoPersonal.bonificacion);
    const salario = parseInt(nuevoPersonal.salario);
    const precioUnidad = parseFloat(nuevoPersonal.precioporcantidad);

    const registro = {
      proyecto: nuevoPersonal.proyecto,
      metaestablecida: meta,
      modalidad: nuevoPersonal.modalidad,
      bonificacion: bonificacion,
      fechadeingreso: nuevoPersonal.fecha || new Date().toISOString(),
      precioporcantidad: nuevoPersonal.modalidad === 'cantidad' ? precioUnidad : null,
      salariopordia: nuevoPersonal.modalidad === 'dÃ­a' ? salario : null,
      trabajometa: nuevoPersonal.trabajometa
    };

    if (existente) {
      await supabase.from('registrodepersonal').update(registro).eq('id', existente.id);
    } else {
      await supabase.from('registrodepersonal').insert([{
        nombrecompleto: nuevoPersonal.nombre,
        puesto: nuevoPersonal.puesto,
        ...registro
      }]);
    }

    setNuevoPersonal({
      nombre: '', puesto: '', proyecto: '', salario: '', meta: '', fecha: '',
      modalidad: 'dÃ­a', bonificacion: '', precioporcantidad: '', trabajometa: ''
    });

    setVista('planilla');
    obtenerPersonal();
  };

  const calcularRealizado = (nombre) => {
    return reportes
      .filter(r => r.nombretrabajador === nombre)
      .reduce((sum, r) => sum + r.cantidad, 0);
  };

  const calcularPago = (p) => {
    const realizado = calcularRealizado(p.nombrecompleto);
    const pago = p.modalidad === 'cantidad' && p.precioporcantidad
      ? realizado * p.precioporcantidad
      : p.salariopordia || 0;
    const bonif = realizado >= p.metaestablecida ? (p.bonificacion || 0) : 0;
    return { realizado, pago, bonif, total: pago + bonif };
  };

  return (
    <div className="mt-6 max-w-7xl mx-auto bg-white p-6 rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-6 text-center">ðŸ‘· GestiÃ³n de Personal</h2>

      <div className="flex justify-center gap-4 mb-6">
        <button onClick={() => setVista('formulario')} className={vista === 'formulario' ? 'bg-blue-600 text-white px-4 py-2 rounded' : 'bg-gray-200 px-4 py-2 rounded'}>Agregar personal</button>
        <button onClick={() => setVista('planilla')} className={vista === 'planilla' ? 'bg-blue-600 text-white px-4 py-2 rounded' : 'bg-gray-200 px-4 py-2 rounded'}>Planilla completa</button>
      </div>

      {vista === 'formulario' && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input type="text" placeholder="Nombre completo" value={nuevoPersonal.nombre} onChange={e => setNuevoPersonal({ ...nuevoPersonal, nombre: e.target.value })} className="border p-2 rounded" />
          <input type="text" placeholder="Puesto" value={nuevoPersonal.puesto} onChange={e => setNuevoPersonal({ ...nuevoPersonal, puesto: e.target.value })} className="border p-2 rounded" />
          <input type="text" placeholder="Proyecto" value={nuevoPersonal.proyecto} onChange={e => setNuevoPersonal({ ...nuevoPersonal, proyecto: e.target.value })} className="border p-2 rounded" />
          <select value={nuevoPersonal.modalidad} onChange={e => setNuevoPersonal({ ...nuevoPersonal, modalidad: e.target.value })} className="border p-2 rounded">
            <option value="dÃ­a">DÃ­a</option>
            <option value="cantidad">Cantidad</option>
          </select>
          {nuevoPersonal.modalidad === 'dÃ­a' && (
            <input type="number" placeholder="Salario por dÃ­a" value={nuevoPersonal.salario} onChange={e => setNuevoPersonal({ ...nuevoPersonal, salario: e.target.value })} className="border p-2 rounded" />
          )}
          {nuevoPersonal.modalidad === 'cantidad' && (
            <input type="number" placeholder="Precio por unidad" value={nuevoPersonal.precioporcantidad} onChange={e => setNuevoPersonal({ ...nuevoPersonal, precioporcantidad: e.target.value })} className="border p-2 rounded" />
          )}
          <input type="number" placeholder="Meta establecida" value={nuevoPersonal.meta} onChange={e => setNuevoPersonal({ ...nuevoPersonal, meta: e.target.value })} className="border p-2 rounded" />
          <input type="number" placeholder="BonificaciÃ³n" value={nuevoPersonal.bonificacion} onChange={e => setNuevoPersonal({ ...nuevoPersonal, bonificacion: e.target.value })} className="border p-2 rounded" />
          <input type="text" placeholder="Trabajo asignado (meta)" value={nuevoPersonal.trabajometa} onChange={e => setNuevoPersonal({ ...nuevoPersonal, trabajometa: e.target.value })} className="border p-2 rounded" />
          <input type="date" placeholder="Fecha de ingreso" value={nuevoPersonal.fecha} onChange={e => setNuevoPersonal({ ...nuevoPersonal, fecha: e.target.value })} className="border p-2 rounded" />
          <button onClick={handleAgregar} className="bg-green-600 text-white py-2 rounded col-span-2">Guardar Personal</button>
        </div>
      )}

      {vista === 'planilla' && (
        <table className="w-full mt-6 border text-sm">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">Nombre</th>
              <th className="border p-2">Puesto</th>
              <th className="border p-2">Proyecto</th>
              <th className="border p-2">Trabajo</th>
              <th className="border p-2">Meta</th>
              <th className="border p-2">Realizado</th>
              <th className="border p-2">Pago</th>
              <th className="border p-2">BonificaciÃ³n</th>
              <th className="border p-2">Total</th>
              <th className="border p-2">Ver detalle</th>
            </tr>
          </thead>
          <tbody>
            {personal.map((p, i) => {
              const { realizado, pago, bonif, total } = calcularPago(p);
              return (
                <tr key={i}>
                  <td className="border p-2">{p.nombrecompleto}</td>
                  <td className="border p-2">{p.puesto}</td>
                  <td className="border p-2">{p.proyecto}</td>
                  <td className="border p-2">{p.trabajometa}</td>
                  <td className="border p-2">{p.metaestablecida || '-'}</td>
                  <td className="border p-2">{realizado}</td>
                  <td className="border p-2">Q{pago.toFixed(2)}</td>
                  <td className="border p-2">Q{bonif.toFixed(2)}</td>
                  <td className="border p-2 font-bold">Q{total.toFixed(2)}</td>
                  <td className="border p-2">
                    <button onClick={() => setHistorialVisible(p.nombrecompleto)} className="text-blue-600 underline">ðŸ“„</button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}

      {historialVisible && (
        <div className="mt-6 bg-gray-100 p-4 rounded">
          <h3 className="font-bold text-lg mb-2">ðŸ“˜ Detalle de reportes de {historialVisible}</h3>
          {reportes
            .filter(r => r.nombretrabajador === historialVisible)
            .map((r, i) => (
              <p key={i}>ðŸ“… {r.fechareporte} â€” {r.cantidad} unidades â€” {r.comentario || "sin comentario"}</p>
            ))}
          <button onClick={() => setHistorialVisible(null)} className="mt-4 text-red-600 underline">Cerrar</button>
        </div>
      )}
    </div>
  );
}
