>
  <option value="">Selecciona un puesto</option>
  {puestosList.map((puesto, i) => (
    <option key={i} value={puesto.nombre}>{puesto.nombre}</option>
  ))}
</select>

        {/* Proyecto */}
        <select
  className="border p-2 rounded"
  value={trabajadorEnEdicion.proyecto}
  onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, proyecto: e.target.value })}
>
  <option value="">Selecciona un proyecto</option>
  {proyectosList.map((proy, i) => (
    <option key={i} value={proy.nombre}>{proy.nombre}</option>
  ))}
</select>

        {/* Salario por día */}
        <input
          type="number"
          className="border p-2 rounded"
          value={trabajadorEnEdicion.salariopordia}
