          placeholder="No. de grupo"
        />

        {/* Caja chica */}
        <select
          className="border p-2 rounded"
          value={trabajadorEnEdicion.caja_chica_asignada ? "si" : "no"}
          onChange={(e) =>
            setTrabajadorEnEdicion({
              ...trabajadorEnEdicion,
              caja_chica_asignada: e.target.value === "si",
            })
