            <tr>
              <th className="border p-2">Fecha</th>
              <th className="border p-2">Cantidad</th>
              <th className="border p-2">Viáticos</th>
              <th className="border p-2">Comentario</th>
              <th className="border p-2">Reportado por</th>
              <th className="border p-2">Pago meta diaria</th>
              <th className="border p-2">Pago del día</th>
              <th className="border p-2">Pago total</th>
              <th className="border p-2">Acciones</th>
            </tr>
          </thead>
          <tbody>
           

 {reportesPersona.map((r, idx) => {
  const sePresento = r.sepresentoatrabajar === "Sí";
const cantidad = r.cantidad || 0;
const salarioDia = r.salariopordia || 0;
const metaEstablecida = r.metaestablecida || 0;
const bonificacionUnidad = r.bonificacion || 0;
const pagoPorUnidad = r.precioporcantidad || 0;


  const pagoMeta = (sePresento && cantidad > metaEstablecida)
  ? (cantidad - metaEstablecida) * bonificacionUnidad
  : 0;

const pagoDia = sePresento
  ? (r.modalidad === "día" ? salarioDia : cantidad * pagoPorUnidad)
  : 0;

const total = pagoDia + pagoMeta + (r.viaticos || 0);

  return (
    <tr key={idx}>
      <td className="border p-2">{r.fechareporte}</td>
      <td className="border p-2">{cantidad}</td>
      <td className="border p-2">Q{(r.viaticos || 0).toFixed(2)}</td>
      <td className="border p-2">{r.comentario || "--"}</td>
      <td className="border p-2">{r.reportadopor || "Desconocido"}</td>
      <td className="border p-2">{pagoMeta > 0 ? `Q${pagoMeta.toFixed(2)}` : "--"}</td>
      <td className="border p-2">{pagoDia > 0 ? `Q${pagoDia.toFixed(2)}` : "--"}</td>
      <td className="border p-2 font-semibold text-green-700">
        Q{total.toFixed(2)}
      </td>
      <td className="border p-2">
  <button
    className="text-blue-500 hover:underline"
    onClick={() => setReporteEnEdicion(r)}
  >
    Editar
  </button>
</td>
    </tr>
  );
})}
{p.caja_chica_asignada && (
