      Asignado: <span className="font-semibold">Q{(p.monto_caja_chica || 0).toFixed(2)}</span> |{" "}
      Gastado: <span className="text-red-600 font-semibold">
        Q{gastosCajaChica
          .filter(g => g.nombre_trabajador === p.nombrecompleto)
          .reduce((sum, g) => sum + (g.costo || 0), 0).toFixed(2)}
      </span> |{" "}
      Disponible: <span className="text-green-700 font-semibold">
        Q{((p.monto_caja_chica || 0) -
            gastosCajaChica
              .filter(g => g.nombre_trabajador === p.nombrecompleto)
              .reduce((sum, g) => sum + (g.costo || 0), 0)).toFixed(2)}
      </span>
    </p>

    <div className="overflow-x-auto">
      <table className="min-w-full text-sm text-left text-gray-700">
        <thead className="bg-gray-100 text-xs uppercase font-semibold text-gray-600">
          <tr>
            <th className="px-4 py-3">Fecha</th>
            <th className="px-4 py-3">No. Factura</th>
            <th className="px-4 py-3">Proveedor</th>
            <th className="px-4 py-3">Comentario</th>
            <th className="px-4 py-3">Proyecto</th>
            <th className="px-4 py-3">Costo</th>
          </tr>
        </thead>
        <tbody>
            
          {gastosCajaChica.filter(g => g.nombre_trabajador === p.nombrecompleto).length === 0 ? (
            <tr>
              <td colSpan="6" className="text-center py-4 text-gray-500">Sin registros aún</td>
            </tr>
          ) : (
            gastosCajaChica
              .filter(g => g.nombre_trabajador === p.nombrecompleto)
              .map((gasto) => (
                <tr key={gasto.id} className="hover:bg-gray-50 transition-all border-b">
                  <td className="px-4 py-3">{gasto.fecha}</td>
                  <td className="px-4 py-3">{gasto.nofactura}</td>
                  <td className="px-4 py-3">{gasto.proveedor}</td>
                  <td className="px-4 py-3">{gasto.comentario}</td>
                  <td className="px-4 py-3">{gasto.proyecto}</td>
                  <td className="px-4 py-3 font-semibold text-green-700">Q{gasto.costo.toFixed(2)}</td>
                </tr>
              ))
          )}
        </tbody>
      </table>
    </div>
  </div>
)}



  {/* TOTAL ACUMULADO */}
  <tr className="bg-gray-100 font-bold">
  <td className="border p-2 text-right" colSpan={7}>Total acumulado</td>
  <td className="border p-2 text-right">
    Q{reportesPersona.reduce((sum, r) => {
      const cantidad = r.cantidad || 0;
      const sePresento = r.sepresentoatrabajar === "Sí";
      let salarioBase = 0;
      let bonificacion = 0;
      const viatico = sePresento ? (r.viaticos || 0) : 0;

      if (sePresento) {
        if (p.modalidad === 'cantidad') {
          salarioBase = cantidad * (p.precioporcantidad || 0);
        } else {
          salarioBase = p.salariopordia || 0;
          const meta = p.metaestablecida || 0;
          if (cantidad > meta) {
            const extra = cantidad - meta;
            bonificacion = extra * (p.bonificacion || 0);
          }
        }
      }

      return sum + salarioBase + bonificacion + viatico;
    }, 0).toFixed(2)}
  </td>
</tr>

</tbody>
</table>
        </div> {/* overflow-x-auto */}
    </div> {/* bg-gray-100 */}
  </td> {/* flex justify-center */}

        
      </td>
    </tr>

    <tr>
      <td colSpan="9" className="p-4">
        <AgregarReporte
             trabajador={p}
             usuario={usuario}
             onGuardar={obtenerReportes}
            />
