import { useState, useEffect } from 'react';
import { supabase } from '../supabase'; 
import { decode } from 'base64-arraybuffer';
import React from 'react';
import ReportarAsistencia from './ReportarAsistencia';

export default function RegistroPersonal() {
  const [vista, setVista] = useState('formulario');
  const [tipoPersonal, setTipoPersonal] = useState('');
  const [mostrarFormularioCajaChica, setMostrarFormularioCajaChica] = useState(false);
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fecha, setFecha] = useState('');
  const [monto, setMonto] = useState('');
  const [factura, setFactura] = useState('no');
  const [noFactura, setNoFactura] = useState('');
  const [fotoFactura, setFotoFactura] = useState(null);
  const [cajaChicaRestante, setCajaChicaRestante] = useState(1000); // Valor de ejemplo

  const [datos, setDatos] = useState({
    nombre: '',
    salarioQuincena: '',
    bonificacion: '',
    horaExtra: '',
    viaticos_diarios: '',
    fechaIngreso: '',
    dpi: '',
    telefono: '',
    fotoPapeleria: null,
  });
  const [personal, setPersonal] = useState([]);
  const [fotoAmpliada, setFotoAmpliada] = useState(null);
const usuario = {
  id: 4,
  usuario: 'super1',
  rol: 'supervisor'
};
console.log("usuario que se pasa a ReportarAsistencia", usuario);

    const handleChange = (e) => {
    const { name, value } = e.target;
    setDatos((prev) => ({ ...prev, [name]: value }));
  };


  const handleFoto = (e) => {
    setDatos((prev) => ({ ...prev, fotoPapeleria: e.target.files[0] }));
  };

  const handleGuardar = async () => {
    if (!datos.nombre.trim() || !tipoPersonal) {
      alert('Por favor, completa el nombre y selecciona el tipo de personal.');
      return;
    }

    let urlPapeleria = null;

    if (datos.fotoPapeleria) {
      const archivo = datos.fotoPapeleria;
      const extension = archivo.name.split('.').pop();
      const nombreArchivo = `${Date.now()}_${archivo.name}`;
      const rutaArchivo = `${nombreArchivo}`;

      const { error: errorUpload } = await supabase.storage
        .from('papeleria')
        .upload(rutaArchivo, archivo);

      if (errorUpload) {
        console.error(errorUpload);
        alert('Error al subir la papelería.');
        return;
      }

      const { data: { publicUrl } } = supabase
        .storage
        .from('papeleria')
        .getPublicUrl(rutaArchivo);

      urlPapeleria = publicUrl;
    }

    const nuevoPersonal = {
       nombrecompleto: datos.nombre.trim(),
        modalidad: tipoPersonal,
        salariopordia: tipoPersonal === 'temporal' ? parseFloat(datos.salarioQuincena || 0) : null,
        salarioporquincena: tipoPersonal === 'fijo' ? parseFloat(datos.salarioQuincena || 0) : null,
        bonificacion: tipoPersonal === 'fijo' ? parseFloat(datos.bonificacion || 0) : null, // Asegúrate que aquí diga 'bonificacion'
        pagoporhoraextra: parseFloat(datos.horaExtra || 0),
        viaticos_diarios: tipoPersonal === 'fijo' ? parseFloat(datos.viaticos_diarios || 0) : null,
        fechadeingreso: datos.fechaIngreso || new Date().toISOString().slice(0, 10),
        dpi: datos.dpi || '',
        telefono: datos.telefono || '',
        urlpapeleria: urlPapeleria,
          };

    const { error } = await supabase.from('registrodepersonal').insert([nuevoPersonal]);

    if (!error) {
      alert('¡Personal agregado correctamente!');
      setDatos({
        nombre: '',
        salarioQuincena: '',
        bonificacion: '',
        horaExtra: '',
        viaticos_diarios: '',
        fechaIngreso: '',
        dpi: '',
        telefono: '',
        fotoPapeleria: null,
      });
      setTipoPersonal('');
    } else {
      console.error(error);
      alert('Ocurrió un error al guardar el personal.');
    }
  };
const handleGuardarCajaChica = () => {
  // Aquí puedes procesar la información, validarla o enviarla al backend
  console.log({
    proyectoSeleccionado,
    descripcion,
    fecha,
    monto,
    factura,
    noFactura: factura === 'si' ? noFactura : null,
    fotoFactura: factura === 'si' ? fotoFactura : null,
  });

  // Reiniciar campos
  setProyectoSeleccionado('');
  setDescripcion('');
  setFecha('');
  setMonto('');
  setFactura('no');
  setNoFactura('');
  setFotoFactura(null);
  setMostrarFormularioCajaChica(false);

  alert('¡Caja Chica registrada exitosamente!');
};

  const obtenerPersonal = async () => {
    let consulta = supabase.from('registrodepersonal').select('*');

        if (usuario.rol === 'temporal') {
        consulta = consulta.eq('modalidad', 'temporal');
      }

    const { data, error } = await consulta;
    if (!error) {
      setPersonal(data);
    } else {
      console.error(error);
    }
  };

  useEffect(() => {
    if (vista === 'planilla' || vista === 'ver_personal') {
      obtenerPersonal();
    }
  }, [vista]);

  return (
  <div className="mt-6 max-w-7xl mx-auto bg-white/10 backdrop-blur-xl p-6 rounded-2xl">
    {/* Acciones rápidas y tarjetas principales */}
    <div className="flex flex-wrap gap-6 justify-start">
      {/* Tarjeta con los dos botones */}
      <div className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-6 w-72 text-center flex flex-col gap-4">
        <h3 className="text-xl font-bold text-gray-800 mb-2">⚡ Acciones rápidas</h3>
        <button
            onClick={() => setVista('asistencia')}
              className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
            >

          <span>✏️</span> Reportar Asistencia
        </button>
        
        <button
          onClick={() => setMostrarFormularioCajaChica(!mostrarFormularioCajaChica)}
          className="flex items-center justify-center gap-2 bg-blue-600 text-white font-semibold px-4 py-2 rounded shadow hover:bg-blue-700 transition"
        >
          <span>💰</span> Caja Chica
        </button>
      </div>

      {/* Tarjetas principales */}
      <div className="flex flex-wrap gap-6 flex-1 justify-center items-center">
  <div
    onClick={() => setVista('formulario')}
    className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer flex flex-col items-center justify-center text-center"
  >
    <h3 className="text-xl font-bold text-gray-800 mb-2">➕ Agregar Personal</h3>
    <p className="text-gray-600 text-sm">Formulario para ingresar trabajadores</p>
  </div>
  <div
    onClick={() => setVista('planilla')}
    className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer flex flex-col items-center justify-center text-center"
  >
    <h3 className="text-xl font-bold text-gray-800 mb-2">📋 Ver Planilla</h3>
    <p className="text-gray-600 text-sm">Visualizar el personal registrado</p>
  </div>
  <div
    onClick={() => setVista('ver_personal')}
    className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer flex flex-col items-center justify-center text-center"
  >
    <h3 className="text-xl font-bold text-gray-800 mb-2">👤 Ver Personal</h3>
    <p className="text-gray-600 text-sm">Listado completo de trabajadores</p>
  </div>
</div>

    </div>
{mostrarFormularioCajaChica && (
  <div className="bg-white/90 p-6 rounded-2xl shadow-xl max-w-md mx-auto mt-4 relative">
    <h2 className="text-xl font-bold text-gray-800 mb-4">💰 Registro de Caja Chica</h2>

    {/* Caja chica restante */}
    <div className="absolute top-2 right-4 text-xs bg-green-100 text-green-800 font-semibold rounded px-2 py-1">
      Q{cajaChicaRestante} restante
    </div>

    <div className="grid grid-cols-1 gap-4">
      <select
        value={proyectoSeleccionado}
        onChange={(e) => setProyectoSeleccionado(e.target.value)}
        className="border p-2 rounded"
      >
        <option value="">Selecciona un proyecto</option>
        <option value="Proyecto A">Proyecto A</option>
        <option value="Proyecto B">Proyecto B</option>
        {/* Agrega más proyectos si lo deseas */}
      </select>

      <input
        type="text"
        placeholder="Descripción"
        value={descripcion}
        onChange={(e) => setDescripcion(e.target.value)}
        className="border p-2 rounded"
      />

      <input
        type="date"
        value={fecha}
        onChange={(e) => setFecha(e.target.value)}
        className="border p-2 rounded"
      />

      <input
        type="number"
        placeholder="Monto"
        value={monto}
        onChange={(e) => setMonto(e.target.value)}
        className="border p-2 rounded"
      />

      <div className="flex items-center gap-4">
        <span className="font-semibold">Factura contable:</span>
        <label className="flex items-center gap-1">
          <input
            type="radio"
            name="factura"
            value="si"
            checked={factura === 'si'}
            onChange={() => setFactura('si')}
          />
          Sí
        </label>
        <label className="flex items-center gap-1">
          <input
            type="radio"
            name="factura"
            value="no"
            checked={factura === 'no'}
            onChange={() => setFactura('no')}
          />
          No
        </label>
      </div>

      {/* Solo mostrar si factura es sí */}
      {factura === 'si' && (
        <>
          <input
            type="text"
            placeholder="No. Factura"
            value={noFactura}
            onChange={(e) => setNoFactura(e.target.value)}
            className="border p-2 rounded"
          />
          <input
            type="file"
            onChange={(e) => setFotoFactura(e.target.files[0])}
            className="border p-2 rounded"
          />
        </>
      )}

      <button
        onClick={handleGuardarCajaChica}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
      >
        Guardar
      </button>
    </div>
  </div>
)}

    {/* Formulario */}
    {vista === 'formulario' && (
      <div className="bg-white/60 p-6 rounded-xl shadow-md max-w-3xl mx-auto mt-6">
        <h2 className="text-xl font-bold mb-2 text-center text-gray-800">📝 Registro de Personal</h2>
        {!tipoPersonal ? (
          <div className="text-center">
            <p className="text-gray-700 font-medium mb-4">¿Qué tipo de personal deseas registrar?</p>
            <div className="flex justify-center gap-6">
              <button
                onClick={() => setTipoPersonal('fijo')}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
              >
                Personal Fijo
              </button>
              <button
                onClick={() => setTipoPersonal('temporal')}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
              >
                Personal Temporal
              </button>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
            <input
              type="text"
              placeholder="Nombre completo"
              name="nombre"
              value={datos.nombre}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            <input
              type="number"
              placeholder={tipoPersonal === 'fijo' ? 'Salario por quincena' : 'Salario por día'}
              name="salarioQuincena"
              value={datos.salarioQuincena}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            {tipoPersonal === 'fijo' && (
              <>
                <input
                  type="number"
                  placeholder="Bono por dìa trabajado"
                  name="bonificacion"
                  value={datos.bonificacion}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="number"
                  placeholder="Viáticos"
                  name="viaticos_diarios"
                  value={datos.viaticos_diarios}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
              </>
            )}
            <input
              type="number"
              placeholder="Pago por hora extra"
              name="horaExtra"
              value={datos.horaExtra}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            <input
              type="date"
              name="fechaIngreso"
              value={datos.fechaIngreso}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            <input
              type="text"
              placeholder="No. DPI"
              name="dpi"
              value={datos.dpi}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            <input
              type="text"
              placeholder="Teléfono"
              name="telefono"
              value={datos.telefono}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Papelería</label>
              <input
                type="file"
                onChange={handleFoto}
                className="border p-2 rounded w-full"
              />
            </div>
            <div className="flex justify-between mt-6 col-span-full">
              <button
                onClick={() => setTipoPersonal('')}
                className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 transition"
              >
                Volver
              </button>
              <button
                onClick={handleGuardar}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
              >
                Guardar personal
              </button>
            </div>
          </div>
        )}
      </div>
    )}
  {vista === 'asistencia' && (
  <ReportarAsistencia
    usuario={usuario}
    onCerrar={() => setVista('formulario')} // o la vista que desees volver a mostrar
  />
)}
    {/* Planilla */}
    {vista === 'planilla' && (
      <div className="overflow-x-auto bg-white/70 backdrop-blur-md p-6 rounded-xl shadow-xl mt-6">
        <h3 className="text-xl font-bold mb-4 text-gray-800">📋 Planilla de Personal</h3>
        <table className="min-w-full border border-gray-300 text-sm text-center">
          <thead className="bg-gray-200 text-gray-700">
            <tr>
              <th className="px-4 py-2 border">Nombre</th>
              <th className="px-4 py-2 border">Modalidad</th>
              <th className="px-4 py-2 border">Salario</th>
              <th className="px-4 py-2 border">Bono por dìa trabajado</th>
              <th className="px-4 py-2 border">Hora extra</th>
              <th className="px-4 py-2 border">Viáticos</th>
              <th className="px-4 py-2 border font-semibold text-green-800">Total</th>
            </tr>
          </thead>
          <tbody>
            {personal.map((p) => {
              const salario = p.modalidad === 'fijo' ? p.salarioporquincena || 0 : p.salariopordia || 0;
              const bono = p.bonificacion || 0;
              const horaExtra = p.pagoporhoraextra || 0;
              const viaticos_diarios = p.viaticos_diarios || 0;
              const total = salario + bono + horaExtra + viaticos_diarios;
              return (
                <tr key={p.id} className="border-b">
                  <td className="px-4 py-2 border">{p.nombrecompleto}</td>
                  <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
                  <td className="px-4 py-2 border">Q{salario}</td>
                  <td className="px-4 py-2 border">Q{bono}</td>
                  <td className="px-4 py-2 border">Q{horaExtra}</td>
                  <td className="px-4 py-2 border">Q{viaticos_diarios}</td>
                  <td className="px-4 py-2 border font-bold text-green-700">Q{total}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    )}

    {/* Ver Personal */}
    {vista === 'ver_personal' && (
      <div className="overflow-x-auto bg-white/70 backdrop-blur-md p-6 rounded-xl shadow-xl mt-6">
        <h3 className="text-xl font-bold mb-4 text-gray-800">👤 Todos los Trabajadores</h3>
        <table className="min-w-full border border-gray-300 text-sm text-center">
          <thead className="bg-gray-200 text-gray-700">
            <tr>
              <th className="px-4 py-2 border">Nombre</th>
              <th className="px-4 py-2 border">Modalidad</th>
              <th className="px-4 py-2 border">Salario</th>
              <th className="px-4 py-2 border">Bono por dìa trabajado</th>
              <th className="px-4 py-2 border">Hora extra</th>
              <th className="px-4 py-2 border">Viáticos</th>
              <th className="px-4 py-2 border">DPI</th>
              <th className="px-4 py-2 border">Teléfono</th>
              <th className="px-4 py-2 border">Ingreso</th>
              <th className="px-4 py-2 border">Papeles</th>
            </tr>
          </thead>
          <tbody>
            {personal.map((p) => (
              <tr key={p.id} className="border-b">
                <td className="px-4 py-2 border">{p.nombrecompleto}</td>
                <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
                <td className="px-4 py-2 border">
                  {p.modalidad === 'fijo' ? `Q${p.salarioporquincena || 0}` : `Q${p.salariopordia || 0}`}
                </td>
                <td className="px-4 py-2 border">{p.bonificacion || '-'}</td>
                <td className="px-4 py-2 border">{p.pagoporhoraextra || '-'}</td>
                <td className="px-4 py-2 border">{p.viaticos_diarios || '-'}</td>
                <td className="px-4 py-2 border">{p.dpi || '-'}</td>
                <td className="px-4 py-2 border">{p.telefono || '-'}</td>
                <td className="px-4 py-2 border">
                  {p.fechadeingreso ? p.fechadeingreso.split('T')[0] : '-'}
                </td>
                <td className="px-4 py-2 border">
                  {p.urlpapeleria ? (
                    <button
                      onClick={() => window.open(p.urlpapeleria, '_blank')}
                      className="bg-blue-100 hover:bg-blue-200 text-blue-800 font-medium px-2 py-1 rounded-full text-xs transition"
                    >
                      Ver Papeles
                    </button>
                  ) : (
                    '-'
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )}
  </div>
);

}
