import { useState, useEffect } from "react";
import { supabase } from "../supabase";
import { Pencil, Trash2, Save, X, Plus, Search, Filter } from "lucide-react";

export default function Proyectos() {
  const [proyectos, setProyectos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [nuevoProyecto, setNuevoProyecto] = useState({ nombre: "", descripcion: "" });
  const [editando, setEditando] = useState(null); // ID del proyecto en edición
  const [editado, setEditado] = useState({ nombre: "", descripcion: "" });

  const obtenerProyectos = async () => {
    const { data, error } = await supabase.from("proyectos").select("*").order("id", { ascending: true });
    if (!error) setProyectos(data);
    else console.error("Error al obtener proyectos", error);
  };

  const agregarProyecto = async () => {
    if (!nuevoProyecto.nombre.trim()) return alert("El nombre es obligatorio.");
    const { error } = await supabase.from("proyectos").insert([nuevoProyecto]);
    if (!error) {
      setNuevoProyecto({ nombre: "", descripcion: "" });
      setMostrarFormulario(false);
      obtenerProyectos();
    } else {
      console.error("Error al agregar proyecto", error);
    }
  };

  const eliminarProyecto = async (id) => {
    if (!confirm("¿Estás seguro de eliminar este proyecto?")) return;
    const { error } = await supabase.from("proyectos").delete().eq("id", id);
    if (!error) obtenerProyectos();
    else alert("Error al eliminar");
  };

  const guardarEdicion = async (id) => {
    const { error } = await supabase
      .from("proyectos")
      .update(editado)
      .eq("id", id);
    if (!error) {
      setEditando(null);
      obtenerProyectos();
    } else {
      console.error(error);
      alert("Error al guardar");
    }
  };

  useEffect(() => {
    obtenerProyectos();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Encabezado y búsqueda */}
      <div className="flex items-center justify-between mb-6">
        <button
          className="flex items-center gap-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded shadow"
          onClick={() => setMostrarFormulario(!mostrarFormulario)}
        >
          <Plus size={16} /> Nuevo proyecto
        </button>

        <div className="flex items-center gap-2">
          <div className="relative">
            <input
              type="text"
              placeholder="Buscar proyecto..."
              className="pl-10 pr-4 py-2 border rounded-lg bg-white shadow-sm focus:outline-none"
            />
            <Search size={16} className="absolute left-3 top-2.5 text-gray-400" />
          </div>
          <button className="p-2 border rounded-lg bg-white shadow-sm hover:bg-gray-50">
            <Filter size={16} />
          </button>
        </div>
      </div>

      {/* Formulario nuevo proyecto */}
      {mostrarFormulario && (
        <div className="bg-white p-4 rounded-xl shadow mb-6 max-w-xl">
          <input
            type="text"
            placeholder="Nombre del proyecto"
            value={nuevoProyecto.nombre}
            onChange={(e) => setNuevoProyecto({ ...nuevoProyecto, nombre: e.target.value })}
            className="w-full mb-2 p-3 border rounded-lg focus:outline-none"
          />
          <textarea
            placeholder="Descripción"
            value={nuevoProyecto.descripcion}
            onChange={(e) => setNuevoProyecto({ ...nuevoProyecto, descripcion: e.target.value })}
            className="w-full mb-3 p-3 border rounded-lg resize-none focus:outline-none"
          />
          <button
            onClick={agregarProyecto}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          >
            Guardar proyecto
          </button>
        </div>
      )}

      {/* Lista de tarjetas */}
      <div className="grid md:grid-cols-2 gap-4">
        {proyectos.map((p) => (
          <div key={p.id} className="bg-white p-4 rounded-xl shadow hover:shadow-md transition relative">
            {editando === p.id ? (
              <>
                <input
                  type="text"
                  className="border w-full rounded mb-2 p-2"
                  value={editado.nombre}
                  onChange={(e) => setEditado({ ...editado, nombre: e.target.value })}
                />
                <textarea
                  className="border w-full rounded p-2"
                  value={editado.descripcion}
                  onChange={(e) => setEditado({ ...editado, descripcion: e.target.value })}
                />
                <div className="flex justify-end gap-2 mt-2">
                  <button onClick={() => guardarEdicion(p.id)} className="bg-blue-600 text-white px-3 py-1 rounded flex items-center gap-1">
                    <Save size={14} /> Guardar
                  </button>
                  <button onClick={() => setEditando(null)} className="bg-gray-300 px-3 py-1 rounded flex items-center gap-1">
                    <X size={14} /> Cancelar
                  </button>
                </div>
              </>
            ) : (
              <>
                <div className="text-lg font-semibold text-gray-800">{p.nombre}</div>
                <div className="text-sm text-gray-500 mb-2">{p.descripcion || "Sin descripción"}</div>
                <div className="flex gap-2 absolute top-2 right-2">
                  <button
                    onClick={() => {
                      setEditando(p.id);
                      setEditado({ nombre: p.nombre, descripcion: p.descripcion });
                    }}
                    className="text-blue-600 hover:text-blue-800"
                  >
                    <Pencil size={18} />
                  </button>
                  <button
                    onClick={() => eliminarProyecto(p.id)}
                    className="text-red-600 hover:text-red-800"
                  >
                    <Trash2 size={18} />
                  </button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
