// App.jsx
import React from "react";
import { DollarSign, ArrowUpCircle, Heart, HeartHandshake } from "lucide-react";

function ResumenFinanciero({ produccion, gastado, utilidad, liquidez }) {
  return (
    <div className="flex flex-col gap-2 p-4 rounded-xl shadow bg-white text-gray-800 max-w-xs">
      <div className="flex items-center gap-2">
        <DollarSign className="text-green-600" />
        <span>Producci√≥n: Q{produccion.toFixed(2)}</span>
      </div>
      <div className="flex items-center gap-2">
        <ArrowUpCircle className="text-red-600" />
        <span>Gastado: Q{gastado.toFixed(2)}</span>
      </div>
      <div className="flex items-center gap-2">
        <Heart className="text-green-600" />
        <span>Utilidad: Q{utilidad.toFixed(2)}</span>
      </div>
      <div className="flex items-center gap-2">
        <HeartHandshake className="text-green-600" />
        <span>Liquidez: Q{liquidez.toFixed(2)}</span>
      </div>
    </div>
  );
}

export default function App() {
  // Ejemplo de datos (puedes cargarlos de tu base de datos o backend)
  const produccion = 10000;
  const gastado = 6000;
  const utilidad = produccion - gastado;
  const liquidez = 2000;

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-100">
      <ResumenFinanciero
        produccion={produccion}
        gastado={gastado}
        utilidad={utilidad}
        liquidez={liquidez}
      />
    </div>
  );
}
