// src/utils.js
import { supabase } from "./supabase";

export async function guardarLog(usuario, accion, descripcion = "") {
  if (!usuario || !usuario.nombre) {
    console.warn("No hay usuario válido");
    return;
  }

  try {
    console.log("Intentando guardar log:", { usuario: usuario.nombre, accion, descripcion });

    const { error } = await supabase.from("logs").insert({
      usuario: usuario.nombre,
      accion: accion,
      descripcion: descripcion,
    });

    if (error) {
      console.error("Error insertando log:", error.message);
    }
  } catch (err) {
    console.error("Error inesperado:", err);
  }
}
