import { useState, useEffect } from "react";
import { supabase } from "../../supabase";

export default function PrecioTrabajos() {
  const [proyectos, setProyectos] = useState([]);
  const [trabajosPorProyecto, setTrabajosPorProyecto] = useState({});

  useEffect(() => {
    const cargarProyectosYTrabajos = async () => {
      const { data: proyectosData } = await supabase.from("proyectos").select("*");

      if (!proyectosData) {
        setProyectos([]);
        return;
      }

      setProyectos(proyectosData);

      const trabajosData = {};
      for (const proyecto of proyectosData) {
        const { data: trabajos } = await supabase
          .from("proyectos_trabajos")
          .select("*")
          .eq("proyecto_id", proyecto.id);

        const trabajosConInstaladas = await Promise.all(
          (trabajos || []).map(async (trabajo) => {
            const { data: reportes } = await supabase
              .from("reportes_diarios")
              .select("cantidad_instalada")
              .eq("proyecto_id", proyecto.id)
              .eq("nombre_trabajo", trabajo.nombre_trabajo);

            const unidadesInstaladas = reportes
              ? reportes.reduce((sum, r) => sum + (r.cantidad_instalada || 0), 0)
              : 0;

            return {
              ...trabajo,
              unidades_instaladas: unidadesInstaladas,
              total_a_instalar: trabajo.unidades_totales * 0, // o el precio_unitario si lo agregÃ¡s
              total_instalado: unidadesInstaladas * 0, // o el precio_unitario si lo agregÃ¡s
            };
          })
        );

        trabajosData[proyecto.id] = trabajosConInstaladas;
      }

      setTrabajosPorProyecto(trabajosData);
    };

    cargarProyectosYTrabajos();
  }, []);

  return (
    <div className="p-4">
      {proyectos && proyectos.length > 0 ? (
        proyectos.map((proyecto) => (
          <div
            key={proyecto.id}
            className="bg-white rounded-lg shadow p-4 mb-4"
          >
            <h3 className="text-lg font-bold text-center text-purple-700 mb-2">
              {proyecto.nombre}
            </h3>

            {trabajosPorProyecto[proyecto.id] &&
            trabajosPorProyecto[proyecto.id].length > 0 ? (
              trabajosPorProyecto[proyecto.id].map((trabajo) => (
                <div
                  key={trabajo.id}
                  className="mt-4 p-4 bg-gray-50 rounded flex flex-wrap justify-around"
                >
                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">
                      Nombre del Trabajo
                    </p>
                    <p className="text-lg font-bold text-gray-800">
                      {trabajo.nombre_trabajo}
                    </p>
                  </div>

                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">
                      ðŸ”¹ Unidades a Instalar
                    </p>
                    <p className="text-lg font-bold text-gray-800">
                      {trabajo.unidades_a_instalar ?? 0}
                    </p>
                  </div>

                  {/* âœ… Precio por Unidad como tarjeta centrada */}
                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">
                      ðŸ’µ Precio por Unidad
                    </p>
                    <p className="text-lg font-bold text-green-700">
                      Q
                      {trabajo.precio_unitario !== undefined &&
                      trabajo.precio_unitario !== null
                        ? trabajo.precio_unitario.toFixed(2)
                        : "0.00"}
                    </p>
                  </div>

                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">
                      ðŸ”¹ Total a Instalar
                    </p>
                    <p className="text-lg font-bold text-gray-800">
                      Q
                      {trabajo.total_a_instalar !== undefined &&
                      trabajo.total_a_instalar !== null
                        ? trabajo.total_a_instalar.toFixed(2)
                        : "0.00"}
                    </p>
                  </div>

                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">
                      ðŸ”¹ Unidades Instaladas
                    </p>
                    <p className="text-lg font-bold text-gray-800">
                      {trabajo.unidades_instaladas ?? 0}
                    </p>
                  </div>

                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">
                      ðŸ”¹ Total Instalado
                    </p>
                    <p className="text-lg font-bold text-gray-800">
                      Q
                      {trabajo.total_instalado !== undefined &&
                      trabajo.total_instalado !== null
                        ? trabajo.total_instalado.toFixed(2)
                        : "0.00"}
                    </p>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-sm mt-2">
                No hay trabajos para este proyecto.
              </p>
            )}
          </div>
        ))
      ) : (
        <p className="text-gray-500 text-sm mt-4">
          No hay proyectos disponibles.
        </p>
      )}
    </div>
  );
}
