import { useState } from "react";

export default function Trabajos() {
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [trabajos, setTrabajos] = useState([]);
  const [nuevoTrabajo, setNuevoTrabajo] = useState({
    nombre: "",
    precioUnidad: "",
    bonificacion: "",
    meta: ""
  });

  const agregarTrabajo = () => {
    if (!nuevoTrabajo.nombre || !nuevoTrabajo.precioUnidad) return;

    const trabajoConId = {
      ...nuevoTrabajo,
      id: Date.now()
    };

    setTrabajos([...trabajos, trabajoConId]);
    setNuevoTrabajo({ nombre: "", precioUnidad: "", bonificacion: "", meta: "" });
    setMostrarFormulario(false);
  };

  const eliminarTrabajo = (id) => {
    setTrabajos(trabajos.filter(t => t.id !== id));
  };

  const editarTrabajo = (trabajo) => {
    setNuevoTrabajo(trabajo);
    setMostrarFormulario(true);
    setTrabajos(trabajos.filter(t => t.id !== trabajo.id)); // Lo quita temporalmente para reemplazarlo al guardar
  };

  return (
    <div className="p-6">
      {/* Tarjeta de Agregar */}
      <div className="flex justify-center mb-6">
        <div
          className="bg-white rounded-2xl p-6 shadow-md w-72 text-center cursor-pointer hover:shadow-lg"
          onClick={() => setMostrarFormulario(!mostrarFormulario)}
        >
          <p className="text-2xl text-purple-700 font-bold">‚ûï Agregar Trabajo</p>
          <p className="text-sm text-gray-600">Formulario para ingresar nuevos trabajos</p>
        </div>
      </div>

      {/* Formulario */}
      {mostrarFormulario && (
        <div className="bg-white shadow-lg rounded-2xl p-6 mb-6 max-w-4xl mx-auto">
          <h2 className="text-xl font-bold mb-4 text-purple-800">üõ†Ô∏è Nuevo Trabajo</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <input
              type="text"
              placeholder="Trabajo"
              className="p-2 border rounded-xl"
              value={nuevoTrabajo.nombre}
              onChange={(e) => setNuevoTrabajo({ ...nuevoTrabajo, nombre: e.target.value })}
            />
            <input
              type="number"
              placeholder="Precio por Unidad"
              className="p-2 border rounded-xl"
              value={nuevoTrabajo.precioUnidad}
              onChange={(e) => setNuevoTrabajo({ ...nuevoTrabajo, precioUnidad: e.target.value })}
            />
            <input
              type="number"
              placeholder="Bonificaci√≥n"
              className="p-2 border rounded-xl"
              value={nuevoTrabajo.bonificacion}
              onChange={(e) => setNuevoTrabajo({ ...nuevoTrabajo, bonificacion: e.target.value })}
            />
            <input
              type="number"
              placeholder="Meta asignada"
              className="p-2 border rounded-xl"
              value={nuevoTrabajo.meta}
              onChange={(e) => setNuevoTrabajo({ ...nuevoTrabajo, meta: e.target.value })}
            />
          </div>

          <button
            onClick={agregarTrabajo}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Guardar Trabajo
          </button>
        </div>
      )}

      {/* Tabla */}
      <div className="bg-white shadow-lg rounded-2xl p-6 max-w-6xl mx-auto">
        <h3 className="text-lg font-bold mb-4 text-gray-800">üìë Lista de Trabajos</h3>
        <table className="min-w-full border text-sm text-center">
          <thead className="bg-purple-100">
            <tr>
              <th className="p-2 border">Trabajo</th>
              <th className="p-2 border">Precio/U</th>
              <th className="p-2 border">Bonificaci√≥n</th>
              <th className="p-2 border">Meta</th>
              <th className="p-2 border">‚öôÔ∏è</th>
            </tr>
          </thead>
          <tbody>
            {trabajos.map((t) => (
              <tr key={t.id} className="hover:bg-gray-100">
                <td className="p-2 border">{t.nombre}</td>
                <td className="p-2 border">Q{t.precioUnidad}</td>
                <td className="p-2 border">Q{t.bonificacion}</td>
                <td className="p-2 border">{t.meta}</td>
                <td className="p-2 border flex justify-center gap-2">
                  <button
                    onClick={() => editarTrabajo(t)}
                    className="text-blue-600 hover:text-blue-800 text-lg"
                    title="Editar"
                  >
                    ‚úèÔ∏è
                  </button>
                  <button
                    onClick={() => eliminarTrabajo(t.id)}
                    className="text-red-600 hover:text-red-800 text-lg"
                    title="Eliminar"
                  >
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
