import { useEffect, useState } from "react";
import { supabase } from "../../supabase";
import { guardarLog } from "../../utils"; // ðŸ‘ˆ Importar el log

export default function CalculosFinancieros({ usuario, proyectoId }) {
  const [produccion, setProduccion] = useState(0);
  const [gastado, setGastado] = useState(0);
  const [ingresos, setIngresos] = useState(0);

  // âœ… Calcular ProducciÃ³n basada en reportes reales
  useEffect(() => {
    const calcularProduccion = async () => {
      // 1ï¸âƒ£ Obtener trabajos del proyecto con precio unitario
      const { data: trabajos, error: errorTrabajos } = await supabase
        .from("proyectos_trabajos")
        .select("nombre_trabajo, precio_unitario")
        .eq("proyecto_id", proyectoId);

      if (errorTrabajos) {
        console.error("Error obteniendo trabajos:", errorTrabajos);
        return;
      }

      // 2ï¸âƒ£ Obtener reportes diarios (unidades instaladas reales)
      const { data: reportes, error: errorReportes } = await supabase
        .from("reportesdiarios")
        .select("trabajorealizado, cantidad")
        .eq("proyecto", proyectoId);

      if (errorReportes) {
        console.error("Error obteniendo reportes:", errorReportes);
        return;
      }

      // 3ï¸âƒ£ Sumar unidades instaladas por trabajo
      const sumasPorTrabajo = {};
      reportes?.forEach((r) => {
        const trabajo = r.trabajorealizado;
        if (!sumasPorTrabajo[trabajo]) sumasPorTrabajo[trabajo] = 0;
        sumasPorTrabajo[trabajo] += r.cantidad || 0;
      });

      // 4ï¸âƒ£ Calcular ProducciÃ³n
      let total = 0;
      trabajos?.forEach((trabajo) => {
        const instaladas = sumasPorTrabajo[trabajo.nombre_trabajo] || 0;
        const precioUnitario = trabajo.precio_unitario ?? 0;
        total += instaladas * precioUnitario;
      });

      setProduccion(total);
    };

    if (proyectoId) {
      calcularProduccion();
    }
  }, [proyectoId]);

  // âœ… Calcular Gastado
  useEffect(() => {
    const calcularGastado = async () => {
      const { data: gastos, error } = await supabase
        .from("gastos_proyectos")
        .select("monto")
        .eq("proyecto_id", proyectoId);

      if (error) {
        console.error("Error obteniendo gastos:", error);
        return;
      }

      const total = gastos
        ? gastos.reduce((sum, gasto) => sum + (gasto.monto ?? 0), 0)
        : 0;

      setGastado(total);
    };

    if (proyectoId) {
      calcularGastado();
    }
  }, [proyectoId]);

  // âœ… Calcular Ingresos
  useEffect(() => {
    const calcularIngresos = async () => {
      const { data: ampliacionesData, error } = await supabase
        .from("ampliaciones_proyectos")
        .select("monto, medio")
        .eq("proyecto_id", proyectoId);

      if (error) {
        console.error("Error obteniendo ingresos:", error);
        return;
      }

      const total = ampliacionesData
        ? ampliacionesData
            .filter((a) => {
              const medio = a.medio ? a.medio.toLowerCase() : "";
              return medio === "transferencia" || medio === "deposito";
            })
            .reduce((sum, a) => sum + (a.monto ?? 0), 0)
        : 0;

      setIngresos(total);
    };

    if (proyectoId) {
      calcularIngresos();
    }
  }, [proyectoId]);

  // ðŸ‘‰ Registrar log
  useEffect(() => {
    if (usuario && proyectoId) {
      guardarLog(
        usuario,
        "Consulta resumen financiero",
        `El usuario consultÃ³ el resumen financiero del proyecto ID: ${proyectoId}`
      );
    }
  }, [usuario, proyectoId]);

  const utilidad = produccion - gastado;
  const liquidez = ingresos - gastado;

  return (
    <div className="bg-white rounded-lg shadow p-4 mt-4">
      <h3 className="text-xl font-bold text-purple-700 text-center mb-4">
        Resumen Financiero
      </h3>

      <div className="flex flex-wrap justify-between gap-6 mt-2">
        <div className="flex flex-col items-center flex-1 min-w-[100px]">
          <p className="text-sm font-semibold text-gray-700">ðŸ’µ ProducciÃ³n</p>
          <p className="text-lg font-bold text-green-700">
            Q{produccion.toFixed(2)}
          </p>
        </div>

        <div className="flex flex-col items-center flex-1 min-w-[100px]">
          <p className="text-sm font-semibold text-gray-700">ðŸ“¤ Gastado</p>
          <p className="text-lg font-bold text-red-700">
            Q{gastado.toFixed(2)}
          </p>
        </div>

        <div className="flex flex-col items-center flex-1 min-w-[100px]">
          <p className="text-sm font-semibold text-gray-700">ðŸ’° Ingresos</p>
          <p className="text-lg font-bold text-blue-700">
            Q{ingresos.toFixed(2)}
          </p>
        </div>

        <div className="flex flex-col items-center flex-1 min-w-[100px]">
          <p className="text-sm font-semibold text-gray-700">ðŸ’š Utilidad</p>
          <p className="text-lg font-bold text-green-700">
            Q{utilidad.toFixed(2)}
          </p>
        </div>

        <div className="flex flex-col items-center flex-1 min-w-[100px]">
          <p className="text-sm font-semibold text-gray-700">ðŸ’š Liquidez</p>
          <p className="text-lg font-bold text-green-700">
            Q{liquidez.toFixed(2)}
          </p>
        </div>
      </div>
    </div>
  );
}
