import { useState } from 'react';

export default function RegistroPersonal() {
  const [vista, setVista] = useState('formulario');
  const [tipoPersonal, setTipoPersonal] = useState('');
  const [datos, setDatos] = useState({
    nombre: '',
    salarioQuincena: '',
    bonoAsistencia: '',
    horaExtra: '',
    viaticos: '',
    fechaIngreso: '',
    dpi: '',
    telefono: '',
    fotoPapeleria: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDatos((prev) => ({ ...prev, [name]: value }));
  };

  const handleFoto = (e) => {
    setDatos((prev) => ({ ...prev, fotoPapeleria: e.target.files[0] }));
  };

  const handleGuardar = () => {
    alert('Datos listos para guardar (ver consola)');
    console.log('Datos a guardar:', datos);
  };

  return (
    <div className="mt-6 max-w-7xl mx-auto bg-white/10 backdrop-blur-xl p-6 rounded-2xl">
      {/* Tarjetas flotantes */}
      <div className="flex flex-wrap justify-center gap-6 mb-6">
        <div
          onClick={() => setVista('formulario')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">➕ Agregar Personal</h3>
          <p className="text-gray-600 text-sm">Formulario para ingresar trabajadores</p>
        </div>
        <div
          onClick={() => setVista('planilla')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">📋 Ver Planilla</h3>
          <p className="text-gray-600 text-sm">Visualizar el personal registrado</p>
        </div>
      </div>

      {/* Formulario estético */}
      {vista === 'formulario' && (
        <div className="bg-white/60 p-6 rounded-xl shadow-md max-w-3xl mx-auto">
          <h2 className="text-xl font-bold mb-4 text-center text-gray-800">📝 Registro de Personal</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="Nombre completo"
              name="nombre"
              value={datos.nombre}
              onChange={handleChange}
              className="border p-2 rounded"
            />
            <select
              value={tipoPersonal}
              onChange={(e) => setTipoPersonal(e.target.value)}
              className="border p-2 rounded"
            >
              <option value="">Selecciona tipo de personal...</option>
              <option value="fijo">Personal Fijo</option>
              <option value="temporal">Personal Temporal</option>
            </select>

            {/* Personal Fijo */}
            {tipoPersonal === 'fijo' && (
              <>
                <input
                  type="number"
                  placeholder="Salario por quincena"
                  name="salarioQuincena"
                  value={datos.salarioQuincena}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="number"
                  placeholder="Bono asistencia por día"
                  name="bonoAsistencia"
                  value={datos.bonoAsistencia}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="number"
                  placeholder="Horas extra"
                  name="horaExtra"
                  value={datos.horaExtra}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="number"
                  placeholder="Viáticos"
                  name="viaticos"
                  value={datos.viaticos}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="date"
                  name="fechaIngreso"
                  value={datos.fechaIngreso}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="text"
                  placeholder="No. DPI"
                  name="dpi"
                  value={datos.dpi}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="text"
                  placeholder="Teléfono"
                  name="telefono"
                  value={datos.telefono}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Papelería</label>
                  <input
                    type="file"
                    onChange={handleFoto}
                    className="border p-2 rounded w-full"
                  />
                </div>
              </>
            )}

            {/* Personal Temporal */}
            {tipoPersonal === 'temporal' && (
              <>
                <input
                  type="number"
                  placeholder="Salario por día"
                  name="salarioQuincena"
                  value={datos.salarioQuincena}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="number"
                  placeholder="Horas extra"
                  name="horaExtra"
                  value={datos.horaExtra}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="text"
                  placeholder="No. DPI"
                  name="dpi"
                  value={datos.dpi}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <input
                  type="date"
                  name="fechaIngreso"
                  value={datos.fechaIngreso}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Papelería</label>
                  <input
                    type="file"
                    onChange={handleFoto}
                    className="border p-2 rounded w-full"
                  />
                </div>
                <input
                  type="text"
                  placeholder="Teléfono"
                  name="telefono"
                  value={datos.telefono}
                  onChange={handleChange}
                  className="border p-2 rounded"
                />
              </>
            )}
          </div>
          <button
            onClick={handleGuardar}
            className="mt-4 bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700 transition"
          >
            Guardar personal
          </button>
        </div>
      )}

      {vista === 'planilla' && (
        <div className="text-center text-gray-700 font-medium py-10">
          Aquí irá la vista de la planilla. (Mantener el diseño actual o personalizarlo).
        </div>
      )}
    </div>
  );
}
