import { useState, useEffect } from "react";
import { supabase } from "../../supabase";

export default function PrecioTrabajos() {
  const [proyectos, setProyectos] = useState([]);
  const [trabajosPorProyecto, setTrabajosPorProyecto] = useState({});
  const [editandoId, setEditandoId] = useState(null);
  const [nuevoPrecio, setNuevoPrecio] = useState("");

  useEffect(() => {
    const cargarProyectosYTrabajos = async () => {
      const { data: proyectosData } = await supabase.from("proyectos").select("*");
      if (!proyectosData) {
        setProyectos([]);
        return;
      }

      setProyectos(proyectosData);

      const trabajosData = {};
      for (const proyecto of proyectosData) {
        const { data: trabajos } = await supabase
          .from("proyectos_trabajos")
          .select("*")
          .eq("proyecto_id", proyecto.id);

        const trabajosConInstaladas = await Promise.all(
          (trabajos || []).map(async (trabajo) => {
            const { data: reportes } = await supabase
              .from("reportes_diarios")
              .select("cantidad_instalada")
              .eq("proyecto_id", proyecto.id)
              .eq("nombre_trabajo", trabajo.nombre_trabajo);

            const unidadesInstaladas = reportes
              ? reportes.reduce((sum, r) => sum + (r.cantidad_instalada || 0), 0)
              : 0;

            return {
              ...trabajo,
              unidades_instaladas: unidadesInstaladas,
              total_a_instalar: trabajo.unidades_totales * trabajo.precio_unitario,
              total_instalado: unidadesInstaladas * trabajo.precio_unitario,
            };
          })
        );

        trabajosData[proyecto.id] = trabajosConInstaladas;
      }

      setTrabajosPorProyecto(trabajosData);
    };

    cargarProyectosYTrabajos();
  }, []);

  const handleGuardarPrecio = async (trabajoId) => {
    const { error } = await supabase
      .from("proyectos_trabajos")
      .update({ precio_unitario: parseFloat(nuevoPrecio) })
      .eq("id", trabajoId);

    if (!error) {
      // Recargar datos
      const updatedTrabajos = { ...trabajosPorProyecto };
      Object.keys(updatedTrabajos).forEach((proyectoId) => {
        updatedTrabajos[proyectoId] = updatedTrabajos[proyectoId].map((trabajo) =>
          trabajo.id === trabajoId
            ? { ...trabajo, precio_unitario: parseFloat(nuevoPrecio) }
            : trabajo
        );
      });
      setTrabajosPorProyecto(updatedTrabajos);
      setEditandoId(null);
      setNuevoPrecio("");
    } else {
      alert("Error al actualizar el precio");
    }
  };

  return (
    <div className="p-4">
      {proyectos && proyectos.length > 0 ? (
        proyectos.map((proyecto) => (
          <div
            key={proyecto.id}
            className="bg-white rounded-lg shadow p-4 mb-4"
          >
            <h3 className="text-lg font-bold text-center text-purple-700 mb-2">
              {proyecto.nombre}
            </h3>

            {trabajosPorProyecto[proyecto.id] &&
            trabajosPorProyecto[proyecto.id].length > 0 ? (
              trabajosPorProyecto[proyecto.id].map((trabajo) => (
                <div
                  key={trabajo.id}
                  className="mt-4 p-4 bg-gray-50 rounded flex flex-wrap justify-around items-center"
                >
                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">Nombre del Trabajo</p>
                    <p className="text-lg font-bold text-gray-800">{trabajo.nombre_trabajo}</p>
                  </div>

                  {/* Precio por Unidad */}
                  <div className="flex flex-col items-center flex-1 min-w-[100px]">
                    <p className="text-sm font-semibold text-gray-700">ðŸ’µ Precio por Unidad</p>
                    {editandoId === trabajo.id ? (
                      <input
                        type="number"
                        step="0.01"
                        value={nuevoPrecio}
                        onChange={(e) => setNuevoPrecio(e.target.value)}
                        className="border border-gray-300 rounded px-2 py-1 text-center"
                      />
                    ) : (
                      <p className="text-lg font-bold text-green-700">
                        Q
                        {trabajo.precio_unitario !== undefined && trabajo.precio_unitario !== null
                          ? trabajo.precio_unitario.toFixed(2)
                          : "0.00"}
                      </p>
                    )}
                  </div>

                  {/* Botones de Modificar y Cerrar */}
                  <div className="flex flex-col items-center flex-1 min-w-[100px] mt-2">
                    {editandoId === trabajo.id ? (
                      <>
                        <button
                          onClick={() => handleGuardarPrecio(trabajo.id)}
                          className="bg-green-600 text-white px-2 py-1 rounded mb-1"
                        >
                          Guardar
                        </button>
                        <button
                          onClick={() => setEditandoId(null)}
                          className="bg-red-500 text-white px-2 py-1 rounded"
                        >
                          Cerrar
                        </button>
                      </>
                    ) : (
                      <button
                        onClick={() => {
                          setEditandoId(trabajo.id);
                          setNuevoPrecio(trabajo.precio_unitario ?? "");
                        }}
                        className="bg-blue-600 text-white px-2 py-1 rounded"
                      >
                        Modificar
                      </button>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-sm mt-2">
                No hay trabajos para este proyecto.
              </p>
            )}
          </div>
        ))
      ) : (
        <p className="text-gray-500 text-sm mt-4">
          No hay proyectos disponibles.
        </p>
      )}
    </div>
  );
}
