import { useState } from 'react';
import { supabase } from '../supabase';

export default function AgregarReporte({ trabajador, usuario, onGuardar }) {
  const [presento, setPresento] = useState('si');
  const [metaDiaria, setMetaDiaria] = useState('');
  const [comentario, setComentario] = useState('');
  const [guardando, setGuardando] = useState(false);

  const guardarReporte = async () => {
    if (presento === 'si' && !metaDiaria) {
      alert('Debes ingresar la meta diaria.');
      return;
    }

    const cantidad = presento === 'si' ? parseInt(metaDiaria) : 0;
    const viaticos = presento === 'si' ? (trabajador.viaticos_diarios || 0) : 0;

    setGuardando(true);

    const { error } = await supabase.from("reportesdiarios").insert([
      {
        fechareporte: new Date().toISOString().slice(0, 10),
        nombretrabajador: trabajador.nombrecompleto,
        cantidad,
        viaticos,
        comentario,
        reportadopor: usuario?.usuario || 'Desconocido'
      }
    ]);

    setGuardando(false);

    if (error) {
      alert('Error al guardar el reporte');
      console.error(error);
    } else {
      alert('Reporte guardado con éxito');
      setMetaDiaria('');
      setComentario('');
      onGuardar();
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow border mt-4">
      <h4 className="font-semibold mb-2">Agregar reporte diario</h4>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div>
          <label className="block mb-1">Se presentó a trabajar:</label>
          <select value={presento} onChange={e => setPresento(e.target.value)} className="w-full border p-2 rounded">
            <option value="si">Sí</option>
            <option value="no">No</option>
          </select>
        </div>

        {presento === 'si' && (
          <div>
            <label className="block mb-1">Meta diaria alcanzada:</label>
            <input
              type="number"
              value={metaDiaria}
              onChange={e => setMetaDiaria(e.target.value)}
              className="w-full border p-2 rounded"
            />
          </div>
        )}

        <div>
          <label className="block mb-1">Comentario:</label>
          <input
            type="text"
            value={comentario}
            onChange={e => setComentario(e.target.value)}
            className="w-full border p-2 rounded"
          />
        </div>
      </div>

      <button
        onClick={guardarReporte}
        disabled={guardando}
        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        {guardando ? 'Guardando...' : 'Guardar Reporte'}
      </button>
    </div>
  );
}
