          <p className="text-sm text-gray-500">No hay reportes aún.</p>
        ) : (
         <table className="w-full text-sm border mt-2">
  <thead className="bg-gray-200">
    <tr>
  <th className="border p-2">Fecha</th>
  <th className="border p-2">Cantidad</th>
  <th className="border p-2">Viáticos</th>
  <th className="border p-2">Comentario</th>
  <th className="border p-2">Reportado por</th>
  <th className="border p-2">Pago meta diaria</th>
