        </div>
      )}

      {/* Vista de proyectos */}
      {vista === "proyectos" && (
        <>
          <div className="flex justify-end">
            <button
              onClick={() => setVista("general")}
              className="bg-purple-100 px-4 py-2 rounded-xl text-purple-800 hover:bg-purple-200"
            >
              ðŸ”™ Volver al balance general
