import { useState, useEffect } from 'react';
import { supabase } from '../supabase';
import React from 'react';
import AgregarReporte from './AgregarReporte';

export default function Personal({ usuario }) {
  const [vista, setVista] = useState('planilla');
  const [personal, setPersonal] = useState([]);
  const [reportes, setReportes] = useState([]);
  const [gastosCajaChica, setGastosCajaChica] = useState([]);
  const [historialVisible, setHistorialVisible] = useState(null);

  useEffect(() => {
    obtenerPersonal();
    obtenerReportes();
    obtenerGastos();
  }, []);

  const obtenerPersonal = async () => {
    const { data, error } = await supabase.from('registrodepersonal').select('*');
    if (!error) setPersonal(data);
  };

  const obtenerReportes = async () => {
    const { data, error } = await supabase.from('reportesdiarios').select('*');
    if (!error) setReportes(data);
  };

  const obtenerGastos = async () => {
    const { data, error } = await supabase.from('gastos_cajachica').select('*');
    if (!error) setGastosCajaChica(data);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6 text-center">Gesti√≥n de Personal</h1>
      <table className="w-full border text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th className="p-2 border">Nombre</th>
            <th className="p-2 border">Puesto</th>
            <th className="p-2 border">Proyecto</th>
            <th className="p-2 border">Caja chica</th>
            <th className="p-2 border">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {personal.map((p, i) => {
            const mostrar = historialVisible === p.nombrecompleto;
            const reportesPersona = reportes.filter(r => r.nombretrabajador === p.nombrecompleto);
            const gastosPersona = gastosCajaChica.filter(g => g.nombre_trabajador === p.nombrecompleto);
            const totalGastado = gastosPersona.reduce((sum, g) => sum + (g.costo || 0), 0);
            const disponible = (p.monto_caja_chica || 0) - totalGastado;

            return (
              <React.Fragment key={i}>
                <tr className="bg-white">
                  <td className="border p-2">{p.nombrecompleto}</td>
                  <td className="border p-2">{p.puesto}</td>
                  <td className="border p-2">{p.proyecto}</td>
                  <td className="border p-2">{p.caja_chica_asignada ? 'S√≠' : 'No'}</td>
                  <td className="border p-2 text-center">
                    <button onClick={() => setHistorialVisible(mostrar ? null : p.nombrecompleto)} className="text-blue-600 underline">
                      {mostrar ? 'Ocultar' : 'Ver detalle'}
                    </button>
                  </td>
                </tr>
                {mostrar && (
                  <tr>
                    <td colSpan="5" className="bg-gray-50 p-4">
                      <h3 className="font-semibold mb-2">Reportes diarios</h3>
                      <table className="w-full text-sm border mb-4">
                        <thead className="bg-gray-200">
                          <tr>
                            <th className="border p-2">Fecha</th>
                            <th className="border p-2">Cantidad</th>
                            <th className="border p-2">Vi√°ticos</th>
                            <th className="border p-2">Comentario</th>
                          </tr>
                        </thead>
                        <tbody>
                          {reportesPersona.length === 0 ? (
                            <tr><td colSpan="4" className="text-center p-2">Sin reportes</td></tr>
                          ) : reportesPersona.map((r, idx) => (
                            <tr key={idx}>
                              <td className="border p-2">{r.fechareporte}</td>
                              <td className="border p-2">{r.cantidad}</td>
                              <td className="border p-2">Q{(r.viaticos || 0).toFixed(2)}</td>
                              <td className="border p-2">{r.comentario || "--"}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>

                      {p.caja_chica_asignada && (
                        <div className="bg-white shadow rounded-xl p-4 mt-4">
                          <h4 className="font-bold text-gray-700 mb-2">üßæ Gastos de Caja Chica</h4>
                          <p className="text-sm mb-2 text-gray-600">
                            Asignado: Q{(p.monto_caja_chica || 0).toFixed(2)} | Gastado: Q{totalGastado.toFixed(2)} | Disponible: Q{disponible.toFixed(2)}
                          </p>
                          <table className="w-full text-sm border">
                            <thead className="bg-gray-100">
                              <tr>
                                <th className="p-2 border">Fecha</th>
                                <th className="p-2 border">Factura</th>
                                <th className="p-2 border">Proveedor</th>
                                <th className="p-2 border">Comentario</th>
                                <th className="p-2 border">Proyecto</th>
                                <th className="p-2 border">Costo</th>
                              </tr>
                            </thead>
                            <tbody>
                              {gastosPersona.length === 0 ? (
                                <tr><td colSpan="6" className="text-center p-2">Sin registros</td></tr>
                              ) : gastosPersona.map((gasto) => (
                                <tr key={gasto.id}>
                                  <td className="p-2 border">{gasto.fecha}</td>
                                  <td className="p-2 border">{gasto.nofactura}</td>
                                  <td className="p-2 border">{gasto.proveedor}</td>
                                  <td className="p-2 border">{gasto.comentario}</td>
                                  <td className="p-2 border">{gasto.proyecto}</td>
                                  <td className="p-2 border text-green-700 font-semibold">Q{gasto.costo.toFixed(2)}</td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                        </div>
                      )}
                      <div className="mt-4">
                        <AgregarReporte trabajador={p} usuario={usuario} onGuardar={obtenerReportes} />
                      </div>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
