import { useState, useEffect } from 'react';
import { supabase } from '../supabase';
import React from 'react';
import AgregarReporte from './AgregarReporte';

export default function Personal({ usuario }) {
  // --------------------- ESTADOS PRINCIPALES ---------------------
  const [vista, setVista] = useState('planilla');
  const [personal, setPersonal] = useState([]);
  const [reportes, setReportes] = useState([]);
  const [puestosList, setPuestosList] = useState([]);
  const [proyectosList, setProyectosList] = useState([]);
  const [historialVisible, setHistorialVisible] = useState(null);
  const [nuevoPuesto, setNuevoPuesto] = useState('');
  const [nuevoProyecto, setNuevoProyecto] = useState('');
  const [gastosCajaChica, setGastosCajaChica] = useState([]);
  const [reporteEnEdicion, setReporteEnEdicion] = useState(null);
  const [trabajadorEnEdicion, setTrabajadorEnEdicion] = useState(null);
  const [modoEdicion, setModoEdicion] = useState('');
  const [sePresento, setSePresento] = useState("Sí");
  const [cantidad, setCantidad] = useState("");
  const [viaticos, setViaticos] = useState("");
  const [comentario, setComentario] = useState("");
  const [grupo, setGrupo] = useState("");
  

  // Estado del formulario para nuevo personal
  const [nuevoPersonal, setNuevoPersonal] = useState({
    nombre: '',
    puesto: '',
    proyecto: '',
    salario: '',
    meta: '',
    bonificacion: '',
    trabajometa: '',
    fecha: '',
    modalidad: 'día',
    precioporcantidad: '',
    cajaChica: 'no',
    montoCajaChica: '',
    viaticosDiarios: ''
  });

  // --------------------- EFECTOS INICIALES ---------------------
  useEffect(() => {
    const fetchGastos = async () => {
      const { data, error } = await supabase
        .from("gastos_cajachica")
        .select("*")
        .order("fecha", { ascending: false });
      if (!error) setGastosCajaChica(data);
    };
    fetchGastos();
  }, []);

  useEffect(() => {
    fetchPuestos();
    fetchProyectos();
    obtenerPersonal();
    obtenerReportes();
  }, []);

  const fetchPuestos = async () => {
    const { data, error } = await supabase.from('puestos').select('*').order('nombre', { ascending: true });
    if (!error) setPuestosList(data);
  };

  const fetchProyectos = async () => {
    const { data, error } = await supabase.from('proyectos').select('*').order('nombre', { ascending: true });
    if (!error) setProyectosList(data);
  };

  const obtenerPersonal = async () => {
    const { data, error } = await supabase
      .from('registrodepersonal')
      .select('*')
      .order('created_at', { ascending: false });
    if (!error) setPersonal(data);
  };

  const obtenerReportes = async () => {
    const { data, error } = await supabase
      .from('reportesdiarios')
      .select('*');
    if (!error) setReportes(data);
  };

const handleGuardarReporte = async () => {
  if (!trabajadorEnEdicion) {
    alert("No hay trabajador seleccionado");
    return;
  }

  const nuevoReporte = {
    nombretrabajador: trabajadorEnEdicion.nombrecompleto,
    sepresentoatrabajar: sePresento,
    cantidad: parseInt(cantidad),
    viaticos: parseFloat(viaticos),
    numerogrupo: grupo,
    comentario,
    fechareporte: new Date().toISOString().slice(0, 10),
    proyecto: trabajadorEnEdicion.proyecto,
    puesto: trabajadorEnEdicion.puesto,
    modalidad: trabajadorEnEdicion.modalidad,
    salariopordia: trabajadorEnEdicion.salariopordia,
    precioporcantidad: trabajadorEnEdicion.precioporcantidad,
    metaestablecida: trabajadorEnEdicion.metaestablecida,
    bonificacion: trabajadorEnEdicion.bonificacion,
  };

  const { error } = await supabase.from("reportesdiarios").insert([nuevoReporte]);
  if (error) {
    alert("Error al guardar el reporte");
    console.error(error);
  } else {
    alert("Reporte guardado exitosamente");
    setVista("planilla");
    // Limpia el formulario si querés
    setCantidad("");
    setViaticos("");
    setComentario("");
    setGrupo("");
  }
};

  // --------------------- UI PRINCIPAL ---------------------

  return (
    <div className="mt-6 max-w-7xl mx-auto bg-white/10 backdrop-blur-xl p-6 rounded-2xl">

      {/* Tarjetas flotantes para cambiar de vista */}
      <div className="flex flex-wrap justify-center gap-6 mb-6">
        <div
          onClick={() => setVista('formulario')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">➕ Agregar Personal</h3>
          <p className="text-gray-600 text-sm">Formulario para ingresar trabajadores</p>
        </div>

        <div
          onClick={() => setVista('planilla')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">📋 Ver Planilla</h3>
          <p className="text-gray-600 text-sm">Visualizar el personal registrado</p>
        </div>
      </div>
      
      {/* Formulario para agregar nuevo personal */}
      {vista === 'formulario' && (
        <div className="bg-white/60 p-6 rounded-xl shadow-md space-y-4">
          <h3 className="text-lg font-semibold text-gray-800 mb-2">Formulario de Registro</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input type="text" placeholder="Nombre completo" value={nuevoPersonal.nombre} onChange={e => setNuevoPersonal({ ...nuevoPersonal, nombre: e.target.value })} className="border p-2 rounded" />
            <select value={nuevoPersonal.puesto} onChange={e => setNuevoPersonal({ ...nuevoPersonal, puesto: e.target.value })} className="border p-2 rounded">
              <option value="">Seleccionar puesto...</option>
              {puestosList.map((item, i) => (
                <option key={i} value={item.nombre}>{item.nombre}</option>
              ))}
            </select>
            <select value={nuevoPersonal.proyecto} onChange={e => setNuevoPersonal({ ...nuevoPersonal, proyecto: e.target.value })} className="border p-2 rounded">
              <option value="">Seleccionar proyecto...</option>
              {proyectosList.map((item, i) => (
                <option key={i} value={item.nombre}>{item.nombre}</option>
              ))}
            </select>
            <select value={nuevoPersonal.modalidad} onChange={e => setNuevoPersonal({ ...nuevoPersonal, modalidad: e.target.value })} className="border p-2 rounded">
              <option value="día">Día</option>
              <option value="cantidad">Cantidad</option>
            </select>
            {nuevoPersonal.modalidad === 'día' && <input type="number" placeholder="Salario por día" value={nuevoPersonal.salario} onChange={e => setNuevoPersonal({ ...nuevoPersonal, salario: e.target.value })} className="border p-2 rounded" />}
            {nuevoPersonal.modalidad === 'cantidad' && <input type="number" placeholder="Precio por unidad" value={nuevoPersonal.precioporcantidad} onChange={e => setNuevoPersonal({ ...nuevoPersonal, precioporcantidad: e.target.value })} className="border p-2 rounded" />}
            <input type="number" placeholder="Meta establecida" value={nuevoPersonal.meta} onChange={e => setNuevoPersonal({ ...nuevoPersonal, meta: e.target.value })} className="border p-2 rounded" />
            <input type="number" placeholder="Bonificación por unidad extra" value={nuevoPersonal.bonificacion} onChange={e => setNuevoPersonal({ ...nuevoPersonal, bonificacion: e.target.value })} className="border p-2 rounded" />
            <input type="text" placeholder="Trabajo asignado" value={nuevoPersonal.trabajometa} onChange={e => setNuevoPersonal({ ...nuevoPersonal, trabajometa: e.target.value })} className="border p-2 rounded" />
            <input type="date" value={nuevoPersonal.fecha} onChange={e => setNuevoPersonal({ ...nuevoPersonal, fecha: e.target.value })} className="border p-2 rounded" />
            <select value={nuevoPersonal.cajaChica} onChange={e => setNuevoPersonal({ ...nuevoPersonal, cajaChica: e.target.value })} className="border p-2 rounded">
              <option value="no">Caja chica: No</option>
              <option value="si">Caja chica: Sí</option>
            </select>
            {nuevoPersonal.cajaChica === 'si' && <input type="number" placeholder="Monto de caja chica" value={nuevoPersonal.montoCajaChica} onChange={e => setNuevoPersonal({ ...nuevoPersonal, montoCajaChica: e.target.value })} className="border p-2 rounded" />}
            <input type="number" placeholder="Viáticos diarios" value={nuevoPersonal.viaticosDiarios} onChange={e => setNuevoPersonal({ ...nuevoPersonal, viaticosDiarios: e.target.value })} className="border p-2 rounded" />
          </div>
          <button className="mt-4 bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700 transition">Guardar personal</button>
        </div>
      )}
          {/*form de repor dia */}
                                      {vista === 'reportar_dia' && (
                              <div className="bg-white/60 p-6 rounded-xl shadow-md space-y-4">
                                <h3 className="text-xl font-bold text-gray-800 mb-4">📅 Reportar Día Trabajado</h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                              {/* Se presentó */}
                              <div>
                                <label className="text-sm font-medium text-gray-700 mb-1 block">¿Se presentó a trabajar?</label>
                                <select
                                  value={sePresento}
                                  onChange={(e) => setSePresento(e.target.value)}
                                  className="border p-2 rounded w-full"
                                >
                                  <option value="Sí">Sí</option>
                                  <option value="No">No</option>
                                </select>
                              </div>

                              {/* Cantidad */}
                              <div>
                                <label className="text-sm font-medium text-gray-700 mb-1 block">Cantidad de unidades</label>
                                <input
                                  type="number"
                                  value={cantidad}
                                  onChange={(e) => setCantidad(e.target.value)}
                                  placeholder="Ej: 120"
                                  className="border p-2 rounded w-full"
                                />
                              </div>

                              {/* Viáticos */}
                              <div>
                                <label className="text-sm font-medium text-gray-700 mb-1 block">Viáticos</label>
                                <input
                                  type="number"
                                  value={viaticos}
                                  onChange={(e) => setViaticos(e.target.value)}
                                  placeholder="Ej: 50"
                                  className="border p-2 rounded w-full"
                                />
                              </div>

                              {/* Grupo */}
                              <div>
                                <label className="text-sm font-medium text-gray-700 mb-1 block">Grupo</label>
                                <input
                                  type="number"
                                  value={grupo}
                                  onChange={(e) => setGrupo(e.target.value)}
                                  placeholder="Ej: 1"
                                  className="border p-2 rounded w-full"
                                />
                              </div>

                              {/* Comentario */}
                              <div className="md:col-span-2">
                                <label className="text-sm font-medium text-gray-700 mb-1 block">Comentario</label>
                                <input
                                  type="text"
                                  value={comentario}
                                  onChange={(e) => setComentario(e.target.value)}
                                  placeholder="Comentario u observación"
                                  className="border p-2 rounded w-full"
                                />
                              </div>
                            </div>


                                <div className="flex justify-between mt-6">
                                  <button className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700" onClick={handleGuardarReporte}>
                                    Guardar Reporte
                                  </button>
                                  <button className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600" onClick={() => setVista('planilla')}>
                                    Cancelar
                                  </button>
                                </div>
                              </div>
                            )}

                {/* Formulario para ver planilla */}
                {vista === 'planilla' && (
          <div className="overflow-x-auto bg-white/70 backdrop-blur-md p-6 rounded-xl shadow-xl">
            <h3 className="text-xl font-bold mb-4 text-gray-800">📋 Planilla de Personal</h3>
            <table className="min-w-full border border-gray-300 text-sm text-center">
              <thead className="bg-gray-200 text-gray-700">
                <tr>
                  <th className="px-4 py-2 border">Trabajador</th>
                  <th className="px-4 py-2 border">Puesto</th>
                  <th className="px-4 py-2 border">Proyecto</th>
                  <th className="px-4 py-2 border">Modalidad</th>
                  <th className="px-4 py-2 border">Meta asignada</th>
                  <th className="px-4 py-2 border">Bonificación</th>
                  <th className="px-4 py-2 border">Salario/Día o Precio/U</th>
                  <th className="px-4 py-2 border">Pago Total</th>
                  <th className="px-4 py-2 border">Acciones</th> {/* Nueva columna */}
                </tr>
              </thead>
              <tbody>
                {personal.map((p, i) => {
                  const reportesPersona = reportes.filter(r => r.nombretrabajador === p.nombrecompleto);
                  const mostrarDetalle = historialVisible === p.nombrecompleto;

                  const total = reportesPersona.reduce((sum, r) => {
                    const cantidad = r.cantidad || 0;
                    const sePresento = r.sepresentoatrabajar === "Sí";
                    let salarioBase = 0;
                    let bonificacion = 0;
                    const viatico = sePresento ? (r.viaticos || 0) : 0;

                    if (sePresento) {
                      if (p.modalidad === 'cantidad') {
                        salarioBase = cantidad * (p.precioporcantidad || 0);
                      } else {
                        salarioBase = p.salariopordia || 0;
                        const meta = p.metaestablecida || 0;
                        if (cantidad > meta) {
                          const extra = cantidad - meta;
                          bonificacion = extra * (p.bonificacion || 0);
                        }
                      }
                    }

                    return sum + salarioBase + bonificacion + viatico;
                  }, 0);
                    
                  return (
                    <React.Fragment key={i}>
                      <tr className="bg-white/50 border-b hover:bg-white/80 transition">
                        <td className="px-4 py-2 border">{p.nombrecompleto}</td>
                        <td className="px-4 py-2 border">{p.puesto}</td>
                        <td className="px-4 py-2 border">{p.proyecto}</td>
                        <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
                        <td className="px-4 py-2 border">
                          <div>
                            <span className="block font-semibold">
                              {p.metaestablecida || '-'} unidades
                            </span>
                            <span className="text-xs text-gray-600">
                              {p.trabajometa || 'Sin detalle'}
                            </span>
                          </div>
                        </td>
                        <td className="px-4 py-2 border">Q{p.bonificacion?.toFixed(2) || '0.00'}</td>
                        <td className="px-4 py-2 border">
                          {p.modalidad === 'día'
                            ? `Q${p.salariopordia?.toFixed(2) || '0.00'}`
                            : `Q${p.precioporcantidad?.toFixed(2) || '0.00'}`}
                        </td>
                        <td className="px-4 py-2 border font-semibold text-green-700">
                          Q{total.toFixed(2)}
                        </td>
                        <td className="px-4 py-2 border">
                                          
                          <div className="flex flex-col gap-2 items-center">
                            <button
                              onClick={() => setHistorialVisible(mostrarDetalle ? null : p.nombrecompleto)}
                              className="bg-blue-100 hover:bg-blue-200 text-blue-800 font-medium px-3 py-1 rounded-full shadow-sm transition text-xs"
                            >
                              {mostrarDetalle ? 'Ocultar' : 'Ver Detalle'}
                            </button>

                            <button
                              onClick={() =>
                                setTrabajadorEnEdicion(
                                  trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto ? null : p
                                )
                              }
                              className={`${
                                trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto
                                  ? 'bg-gray-200 text-gray-700'
                                  : 'bg-yellow-100 hover:bg-yellow-200 text-yellow-800'
                              } font-medium px-3 py-1 rounded-full shadow-sm transition text-xs`}
                            >
                              {trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto
                                ? 'Ocultar'
                                : 'Editar Datos'}
                            </button>
                          </div>
                                  
                            
                        </td>
                      </tr>


          {/*  👇 Tarjetas de edición que se muestran debajo de la fila del trabajador seleccionado*/}
                  {trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto && (
                    <tr>
                    <td colSpan="9" className="bg-white p-4 border-t">
                      <div className="text-center mb-4">
                        <h4 className="text-xl font-bold text-gray-800 mb-2 flex items-center justify-center gap-2">
                          🛠 Opciones de edición para {p.nombrecompleto}
                        </h4>
                      </div>

                      <div className="flex flex-wrap justify-center gap-6">
                        {/* Reportar Día */}
                        <div
                          onClick={() => {
                            setVista('reportar_dia');
                            setHistorialVisible(null);
                          }}
                          className="bg-blue-50 border border-blue-200 p-4 rounded-xl shadow cursor-pointer hover:bg-blue-100 transition w-72 text-center"
                        >
                          <h5 className="text-blue-800 font-semibold text-lg mb-1 flex items-center justify-center gap-2">
                            📅 Reportar Día
                          </h5>
                          <p className="text-sm text-blue-700">Ingresar nuevo reporte para hoy</p>
                        </div>

                      
                        {/* Modificar Trabajo Actual */}
                        <div
                          onClick={() => {
                            setVista('modificar_trabajo');
                            setHistorialVisible(null);
                          }}
                          className="bg-purple-50 border border-purple-200 p-4 rounded-xl shadow cursor-pointer hover:bg-purple-100 transition w-72 text-center"
                        >
                          <h5 className="text-purple-800 font-semibold text-lg mb-1 flex items-center justify-center gap-2">
                            ⚙️ Modificar Trabajo Actual
                          </h5>
                          <p className="text-sm text-purple-700">Editar datos sin afectar reportes anteriores</p>
                        </div>
                      </div>
                    </td>
                  </tr>
                  )}
                          
{trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto && (
  <>
    {/* Tarjetas de edición */}
    <tr>
      <td colSpan="9" className="bg-white p-4 border-t">
        <div className="text-center mb-4">
          <h4 className="text-xl font-bold text-gray-800 mb-2 flex items-center justify-center gap-2">
            🛠 Opciones de edición para {p.nombrecompleto}
          </h4>
        </div>

        <div className="flex flex-wrap justify-center gap-6">
          {/* Reportar Día */}
          <div
            onClick={() => {
              setVista('reportar_dia');
              setHistorialVisible(null);
            }}
            className="bg-blue-50 border border-blue-200 p-4 rounded-xl shadow cursor-pointer hover:bg-blue-100 transition w-72 text-center"
          >
            <h5 className="text-blue-800 font-semibold text-lg mb-1 flex items-center justify-center gap-2">
              📅 Reportar Día
            </h5>
            <p className="text-sm text-blue-700">Ingresar nuevo reporte para hoy</p>
          </div>

          {/* Modificar Trabajo Actual */}
          <div
            onClick={() => {
              setVista('modificar_trabajo');
              setHistorialVisible(null);
            }}
            className="bg-purple-50 border border-purple-200 p-4 rounded-xl shadow cursor-pointer hover:bg-purple-100 transition w-72 text-center"
          >
            <h5 className="text-purple-800 font-semibold text-lg mb-1 flex items-center justify-center gap-2">
              ⚙️ Modificar Trabajo Actual
            </h5>
            <p className="text-sm text-purple-700">Editar datos sin afectar reportes anteriores</p>
          </div>
        </div>
      </td>
    </tr>

    {/* Formulario Modificar Trabajo Actual */}
    {vista === 'modificar_trabajo' && (
      <tr>
        <td colSpan="9" className="bg-white p-6 rounded-xl shadow border border-purple-200 mt-4">
          <h3 className="text-xl font-bold text-purple-800 mb-4 flex items-center gap-2">
            ⚙️ Modificar Trabajo Actual de {trabajadorEnEdicion.nombrecompleto}
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Proyecto */}
            <div>
              <label className="text-sm font-medium text-gray-700 mb-1 block">Proyecto</label>
              <select
                value={trabajadorEnEdicion.proyecto}
                onChange={(e) =>
                  setTrabajadorEnEdicion({ ...trabajadorEnEdicion, proyecto: e.target.value })
                }
                className="border p-2 rounded w-full"
              >
                <option value="">Seleccione un proyecto</option>
                {proyectosList.map((p, idx) => (
                  <option key={idx} value={p.nombre}>
                    {p.nombre}
                  </option>
                ))}
              </select>
            </div>

            {/* Puesto */}
            <div>
              <label className="text-sm font-medium text-gray-700 mb-1 block">Puesto</label>
              <select
                value={trabajadorEnEdicion.puesto}
                onChange={(e) =>
                  setTrabajadorEnEdicion({ ...trabajadorEnEdicion, puesto: e.target.value })
                }
                className="border p-2 rounded w-full"
              >
                <option value="">Seleccione un puesto</option>
                {puestosList.map((p, idx) => (
                  <option key={idx} value={p.nombre}>
                    {p.nombre}
                  </option>
                ))}
              </select>
            </div>

            {/* Modalidad */}
            <div>
              <label className="text-sm font-medium text-gray-700 mb-1 block">Modalidad</label>
              <select
                value={trabajadorEnEdicion.modalidad}
                onChange={(e) =>
                  setTrabajadorEnEdicion({ ...trabajadorEnEdicion, modalidad: e.target.value })
                }
                className="border p-2 rounded w-full"
              >
                <option value="día">Día</option>
                <option value="cantidad">Cantidad</option>
              </select>
            </div>

            {/* Salario o precio */}
            <div>
              <label className="text-sm font-medium text-gray-700 mb-1 block">
                {trabajadorEnEdicion.modalidad === 'día' ? 'Salario por día' : 'Precio por unidad'}
              </label>
              <input
                type="number"
                value={
                  trabajadorEnEdicion.modalidad === 'día'
                    ? trabajadorEnEdicion.salariopordia
                    : trabajadorEnEdicion.precioporcantidad
                }
                onChange={(e) => {
                  const valor = parseFloat(e.target.value);
                  if (trabajadorEnEdicion.modalidad === 'día') {
                    setTrabajadorEnEdicion({ ...trabajadorEnEdicion, salariopordia: valor });
                  } else {
                    setTrabajadorEnEdicion({ ...trabajadorEnEdicion, precioporcantidad: valor });
                  }
                }}
                className="border p-2 rounded w-full"
              />
            </div>

            {/* Meta */}
            <div>
              <label className="text-sm font-medium text-gray-700 mb-1 block">Meta establecida</label>
              <input
                type="number"
                value={trabajadorEnEdicion.metaestablecida || ''}
                onChange={(e) =>
                  setTrabajadorEnEdicion({
                    ...trabajadorEnEdicion,
                    metaestablecida: parseInt(e.target.value),
                  })
                }
                className="border p-2 rounded w-full"
              />
            </div>

            {/* Bonificación */}
            <div>
              <label className="text-sm font-medium text-gray-700 mb-1 block">
                Bonificación por unidad extra
              </label>
              <input
                type="number"
                value={trabajadorEnEdicion.bonificacion || ''}
                onChange={(e) =>
                  setTrabajadorEnEdicion({
                    ...trabajadorEnEdicion,
                    bonificacion: parseFloat(e.target.value),
                  })
                }
                className="border p-2 rounded w-full"
              />
            </div>

            {/* Trabajo meta */}
            <div className="md:col-span-2">
              <label className="text-sm font-medium text-gray-700 mb-1 block">
                Trabajo asignado como meta
              </label>
              <input
                type="text"
                value={trabajadorEnEdicion.trabajometa || ''}
                onChange={(e) =>
                  setTrabajadorEnEdicion({ ...trabajadorEnEdicion, trabajometa: e.target.value })
                }
                className="border p-2 rounded w-full"
              />
            </div>
          </div>

          {/* Botones */}
          <div className="flex gap-4 mt-6 justify-end">
            <button
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
              onClick={async () => {
                const actualizado = {
                  proyecto: trabajadorEnEdicion.proyecto,
                  puesto: trabajadorEnEdicion.puesto,
                  modalidad: trabajadorEnEdicion.modalidad,
                  salariopordia: parseFloat(trabajadorEnEdicion.salariopordia || 0),
                  precioporcantidad: parseFloat(trabajadorEnEdicion.precioporcantidad || 0),
                  metaestablecida: parseInt(trabajadorEnEdicion.metaestablecida || 0),
                  bonificacion: parseFloat(trabajadorEnEdicion.bonificacion || 0),
                  trabajometa: trabajadorEnEdicion.trabajometa || '',
                };

                const { error } = await supabase
                  .from('registrodepersonal')
                  .update(actualizado)
                  .eq('nombrecompleto', trabajadorEnEdicion.nombrecompleto);

                if (!error) {
                  alert('Datos del trabajador actualizados correctamente');
                  obtenerPersonal();
                  setVista('planilla');
                } else {
                  alert('Error al actualizar');
                  console.error(error);
                }
              }}
            >
              Guardar cambios
            </button>

            <button
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
              onClick={() => setVista('planilla')}
            >
              Cancelar
            </button>
          </div>
        </td>
      </tr>
    )}
  </>
)}



             
              {/* Mostrar detalles estéticos de los reportes diarios del trabajador seleccionado */}
                {mostrarDetalle && (
                  <tr>
                    <td colSpan="9" className="bg-white p-4 border-b rounded-xl">
                      <h4 className="text-lg font-bold text-gray-800 mb-4">📋 Reportes diarios de {p.nombrecompleto}</h4>

                      <div className="grid gap-4">
                        {reportesPersona.map((r, idx) => {
                          const cantidad = r.cantidad || 0;
                          const viaticos = r.viaticos || 0;
                          const salario = r.modalidad === "día" ? (r.salariopordia || 0) : cantidad * (r.precioporcantidad || 0);
                          const bonificacion = r.modalidad === "cantidad" && cantidad > r.metaestablecida
                            ? (cantidad - r.metaestablecida) * (r.bonificacion || 0)
                            : 0;
                          const total = salario + bonificacion + viaticos;

                          return (
                            <div key={idx} className="bg-gray-50 p-4 rounded-lg shadow-sm border border-gray-200">
                              <div className="flex justify-between items-center mb-2">
                                <span className="text-lg font-semibold text-gray-700">{r.fechareporte}</span>
                                <span className="bg-gray-200 text-gray-700 px-3 py-1 rounded-full text-sm">Grupo {r.numerogrupo || 'N/D'}</span>
                              </div>
                              <p className="text-gray-600 mb-1">{cantidad} unidades — Viáticos: Q{viaticos.toFixed(2)}</p>
                              <p className="text-sm text-gray-500 italic mb-2">{r.comentario || "--"}</p>
                              <div className="text-sm text-gray-700 grid md:grid-cols-3 gap-2">
                                <p><strong>Proyecto:</strong> {r.proyecto || 'N/D'}</p>
                                <p><strong>Puesto:</strong> {p.puesto || 'N/D'}</p>
                                <p><strong>Modalidad:</strong> {r.modalidad || 'día'}</p>
                                <p><strong>Salario del día:</strong> Q{salario.toFixed(2)}</p>
                                <p><strong>Bonificación:</strong> Q{bonificacion.toFixed(2)}</p>
                                <p className="font-semibold text-green-700"><strong>Total:</strong> Q{total.toFixed(2)}</p>
                              </div>
                                  <button
                                    onClick={() => setReporteEnEdicion(r)}
                                    className="mt-3 inline-block bg-blue-100 hover:bg-blue-200 text-blue-800 px-4 py-1 rounded-full text-sm shadow-sm transition"
                                  >
                                    ✏️ Modificar reporte
                                  </button>
                                   {/* codigo para modificar el reporte ya hecho  */}
                                 {reporteEnEdicion?.id === r.id && (
                              <div className="mt-4 bg-yellow-50 p-4 rounded shadow border border-yellow-300">
                                <h4 className="font-semibold mb-4 text-lg flex items-center gap-2">✏️ Editar reporte del {r.fechareporte}</h4>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                  {/* Cantidad */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Cantidad de unidades</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.cantidad}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, cantidad: parseInt(e.target.value) })
                                      }
                                      placeholder="Ej: 120"
                                    />
                                  </div>

                                  {/* Viáticos */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Viáticos</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.viaticos}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, viaticos: parseFloat(e.target.value) })
                                      }
                                      placeholder="Ej: 50"
                                    />
                                  </div>

                                 {/* Proyecto */}
                                  <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-1">Proyecto</label>
                                    <select
                                      value={reporteEnEdicion.proyecto}
                                      onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, proyecto: e.target.value })}
                                      className="w-full border border-gray-300 p-2 rounded"
                                    >
                                      <option value="">Seleccione un proyecto</option>
                                      {proyectosList.map((p, idx) => (
                                        <option key={idx} value={p.nombre}>{p.nombre}</option>
                                      ))}
                                    </select>

                                  </div>

                                  {/* Puesto */}
                                  <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-1">Puesto</label>
                                    <select
                                      value={reporteEnEdicion.puesto}
                                      onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, puesto: e.target.value })}
                                      className="w-full border border-gray-300 p-2 rounded"
                                    >
                                      <option value="">Seleccione un puesto</option>
                                      {puestosList.map((p, idx) => (
                                        <option key={idx} value={p.nombre}>{p.nombre}</option>
                                      ))}
                                    </select>

                                  </div>

                                  {/* Modalidad */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Modalidad</label>
                                    <select
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.modalidad}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, modalidad: e.target.value })
                                      }
                                    >
                                      <option value="día">Día</option>
                                      <option value="cantidad">Cantidad</option>
                                    </select>
                                  </div>

                                  {/* Precio por unidad o salario por día */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">
                                      {reporteEnEdicion.modalidad === 'día' ? 'Salario del día' : 'Precio por unidad'}
                                    </label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={
                                        reporteEnEdicion.modalidad === 'día'
                                          ? reporteEnEdicion.salariopordia
                                          : reporteEnEdicion.precioporcantidad
                                      }
                                      onChange={(e) => {
                                        const valor = parseFloat(e.target.value);
                                        if (reporteEnEdicion.modalidad === 'día') {
                                          setReporteEnEdicion({ ...reporteEnEdicion, salariopordia: valor });
                                        } else {
                                          setReporteEnEdicion({ ...reporteEnEdicion, precioporcantidad: valor });
                                        }
                                      }}
                                    />
                                  </div>

                                  {/* Bonificación */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Bonificación</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.bonificacion}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, bonificacion: parseFloat(e.target.value) })
                                      }
                                    />
                                  </div>

                                  {/* Meta establecida */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Meta establecida</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.metaestablecida}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, metaestablecida: parseInt(e.target.value) })
                                      }
                                    />
                                  </div>

                                  {/* Comentario */}
                                  <div className="md:col-span-2">
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Comentario</label>
                                    <textarea
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.comentario}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, comentario: e.target.value })
                                      }
                                      placeholder="Observaciones (opcional)"
                                    />
                                  </div>
                                </div>

                                {/* Botones */}
                                <div className="flex gap-4 mt-4">
                                  <button
                                    className="bg-green-600 text-white px-4 py-2 rounded"
                                    onClick={async () => {
                                      if (!reporteEnEdicion?.id) {
                                        alert("No se puede actualizar: falta el ID del reporte.");
                                        return;
                                      }

                                      const actualizado = {
                                        cantidad: parseInt(reporteEnEdicion.cantidad),
                                        viaticos: parseFloat(reporteEnEdicion.viaticos),
                                        comentario: reporteEnEdicion.comentario || "--",
                                        sepresentoatrabajar: reporteEnEdicion.sepresetentoatrabajar || "Sí",
                                        numerogrupo: reporteEnEdicion.numerogrupo || null,
                                        salariopordia: parseFloat(reporteEnEdicion.salariopordia || 0),
                                        metaestablecida: parseInt(reporteEnEdicion.metaestablecida || 0),
                                        bonificacion: parseFloat(reporteEnEdicion.bonificacion || 0),
                                        modalidad: reporteEnEdicion.modalidad || "día",
                                        precioporcantidad: parseFloat(reporteEnEdicion.precioporcantidad || 0),
                                        proyecto: reporteEnEdicion.proyecto || "",
                                        puesto: reporteEnEdicion.puesto || ""
                                      };

                                      const { error } = await supabase
                                        .from("reportesdiarios")
                                        .update(actualizado)
                                        .eq("id", reporteEnEdicion.id);

                                      if (!error) {
                                        alert("Reporte actualizado correctamente");
                                        setReporteEnEdicion(null);
                                        obtenerReportes();
                                      } else {
                                        console.error("Error al actualizar:", error);
                                        alert("Ocurrió un error al actualizar el reporte");
                                      }
                                    }}
                                  >
                                    Guardar cambios
                                  </button>

                                  <button
                                    onClick={() => setReporteEnEdicion(null)}
                                    className="bg-gray-600 text-white px-4 py-2 rounded"
                                  >
                                    Cancelar
                                  </button>
                                </div>
                              </div>
                            )}

                                                            

                        </div>
                        
                          );
                        })}
                      </div>
                    </td>
                  </tr>
                )}


                            </React.Fragment>
                          );
                        })}
  

                      </tbody>
                    </table>
                  </div>
                )}


    </div>
  );
}
