import React, { useState, useEffect } from "react";
import { supabase } from "../supabase";

export default function ModificarTrabajos({ persona, onCerrar }) {
  const [modo, setModo] = useState("planillaActual"); // planillaActual o fechaAnterior
  const [fechaSeleccionada, setFechaSeleccionada] = useState("");
  const [datosFormulario, setDatosFormulario] = useState({
    horasExtras: "",
    unidadesEstablecidas: "",
    unidadesHechas: "",
    bonificacionExtra: "",
    pagoDomingo: "",
    grupo: "",
  });

  // Cargar datos de la planilla actual
  useEffect(() => {
    if (persona && modo === "planillaActual") {
      setDatosFormulario({
        horasExtras: persona.horas_extras || "",
        unidadesEstablecidas: persona.metaestablecida || "",
        unidadesHechas: persona.cantidad || "",
        bonificacionExtra: persona.extra || "",
        pagoDomingo: persona.pagoextraordinario || "",
        grupo: persona.numerogrupo || "",
      });
    }
  }, [persona, modo]);

  // Cargar datos de la fecha seleccionada
  const handleSeleccionarFecha = async () => {
    if (!fechaSeleccionada) {
      alert("Selecciona una fecha primero.");
      return;
    }

    const { data, error } = await supabase
      .from("reportesdiarios")
      .select("*")
      .eq("nombretrabajador", persona.nombrecompleto)
      .eq("fechareporte", fechaSeleccionada)
      .single();

    if (error) {
      console.error(error);
      alert("Error al obtener datos de esa fecha.");
    } else {
      setDatosFormulario({
        horasExtras: data.horas_extras || "",
        unidadesEstablecidas: data.metaestablecida || "",
        unidadesHechas: data.cantidad || "",
        bonificacionExtra: data.extra || "",
        pagoDomingo: data.pagoextraordinario || "",
        grupo: data.numerogrupo || "",
      });
    }
  };

  // Guardar cambios
  const handleGuardar = async () => {
    if (modo === "planillaActual") {
      // Actualiza la tabla de personal
      const { error } = await supabase
        .from("registrodepersonal")
        .update({
          horas_extras: parseInt(datosFormulario.horasExtras, 10),
          metaestablecida: parseInt(datosFormulario.unidadesEstablecidas, 10),
          cantidad: parseInt(datosFormulario.unidadesHechas, 10),
          extra: parseFloat(datosFormulario.bonificacionExtra),
          pagoextraordinario: parseFloat(datosFormulario.pagoDomingo),
          numerogrupo: datosFormulario.grupo,
        })
        .eq("id", persona.id);

      if (error) {
        console.error(error);
        alert("Error al guardar los cambios.");
      } else {
        alert("¡Planilla actual modificada exitosamente!");
        onCerrar();
      }
    } else {
      // Actualiza reporte diario de la fecha seleccionada
      const { error } = await supabase
        .from("reportesdiarios")
        .update({
          horas_extras: parseInt(datosFormulario.horasExtras, 10),
          metaestablecida: parseInt(datosFormulario.unidadesEstablecidas, 10),
          cantidad: parseInt(datosFormulario.unidadesHechas, 10),
          extra: parseFloat(datosFormulario.bonificacionExtra),
          pagoextraordinario: parseFloat(datosFormulario.pagoDomingo),
          numerogrupo: datosFormulario.grupo,
        })
        .eq("nombretrabajador", persona.nombrecompleto)
        .eq("fechareporte", fechaSeleccionada);

      if (error) {
        console.error(error);
        alert("Error al guardar los cambios de la fecha seleccionada.");
      } else {
        alert("¡Datos de la fecha seleccionada modificados exitosamente!");
        onCerrar();
      }
    }
  };

  return (
    <div className="bg-white rounded-xl shadow p-4 mt-4">
      <h4 className="text-lg font-bold mb-4 flex items-center gap-2">
        ⚙️ Modificar Trabajos
      </h4>

      <div className="flex gap-2 mb-4">
        <button
          onClick={() => setModo("planillaActual")}
          className={`px-4 py-2 rounded font-semibold ${
            modo === "planillaActual" ? "bg-green-600 text-white" : "bg-gray-200"
          }`}
        >
          Modificar planilla actual
        </button>
        <button
          onClick={() => setModo("fechaAnterior")}
          className={`px-4 py-2 rounded font-semibold ${
            modo === "fechaAnterior" ? "bg-green-600 text-white" : "bg-gray-200"
          }`}
        >
          Modificar fecha anterior
        </button>
      </div>

      {modo === "fechaAnterior" && (
        <div className="mb-4 flex gap-2 items-center">
          <input
            type="date"
            value={fechaSeleccionada}
            onChange={(e) => setFechaSeleccionada(e.target.value)}
            className="border p-2 rounded"
          />
          <button
            onClick={handleSeleccionarFecha}
            className="bg-blue-600 text-white px-4 py-1 rounded"
          >
            Buscar datos
          </button>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-2 mb-4">
        <input
          type="number"
          placeholder="Horas extras"
          value={datosFormulario.horasExtras}
          onChange={(e) =>
            setDatosFormulario({ ...datosFormulario, horasExtras: e.target.value })
          }
          className="border p-2 rounded"
        />
        <input
          type="number"
          placeholder="Unidades establecidas"
          value={datosFormulario.unidadesEstablecidas}
          onChange={(e) =>
            setDatosFormulario({ ...datosFormulario, unidadesEstablecidas: e.target.value })
          }
          className="border p-2 rounded"
        />
        <input
          type="number"
          placeholder="Unidades hechas"
          value={datosFormulario.unidadesHechas}
          onChange={(e) =>
            setDatosFormulario({ ...datosFormulario, unidadesHechas: e.target.value })
          }
          className="border p-2 rounded"
        />
        <input
          type="number"
          placeholder="Bonificación por unidades extras (Q)"
          value={datosFormulario.bonificacionExtra}
          onChange={(e) =>
            setDatosFormulario({ ...datosFormulario, bonificacionExtra: e.target.value })
          }
          className="border p-2 rounded"
        />
        <input
          type="number"
          placeholder="Pago extraordinario (domingo)"
          value={datosFormulario.pagoDomingo}
          onChange={(e) =>
            setDatosFormulario({ ...datosFormulario, pagoDomingo: e.target.value })
          }
          className="border p-2 rounded"
        />
        <input
          type="text"
          placeholder="Número de grupo"
          value={datosFormulario.grupo}
          onChange={(e) =>
            setDatosFormulario({ ...datosFormulario, grupo: e.target.value })
          }
          className="border p-2 rounded"
        />
      </div>

      <div className="flex gap-2 justify-center">
        <button
          onClick={handleGuardar}
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:bg-green-700"
        >
          Guardar
        </button>
        <button
          onClick={onCerrar}
          className="bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600"
        >
          Cerrar
        </button>
      </div>
    </div>
  );
}
