import { useState } from 'react';
import Login from './Login';

function App() {
  const [usuario, setUsuario] = useState(null);
  const [tab, setTab] = useState("personal");

  if (!usuario) return <Login onLogin={setUsuario} />;

  const esAdmin = usuario.rol === 'admin';

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-2xl font-bold text-center mt-4">
        Bienvenido {esAdmin ? 'Administrador' : usuario.rol === 'contabilidad' ? 'Usuario de Contabilidad' : usuario.rol === 'reportador' ? 'Reportador' : 'Usuario'}
      </h1>

      {/* MEN√ö SOLO PARA ADMIN */}
      {esAdmin && (
        <div className="flex flex-wrap gap-4 justify-center mt-6 bg-white p-4 rounded-xl shadow">
          <button onClick={() => setTab("personal")} className={tab === "personal" ? "font-bold underline" : ""}>Personal</button>
          <button onClick={() => setTab("precios")} className={tab === "precios" ? "font-bold underline" : ""}>Precios de trabajos</button>
          <button onClick={() => setTab("proyectos")} className={tab === "proyectos" ? "font-bold underline" : ""}>Proyectos</button>
          <button onClick={() => setTab("contabilidad")} className={tab === "contabilidad" ? "font-bold underline" : ""}>Contabilidad</button>
        </div>
      )}

      {/* CONTENIDO SEG√öN LA PESTA√ëA */}
      {esAdmin && tab === "personal" && <div className="mt-6 text-center">üë§ Gesti√≥n de personal aqu√≠...</div>}
      {esAdmin && tab === "precios" && <div className="mt-6 text-center">üí∞ Tabla de precios de trabajos...</div>}
      {esAdmin && tab === "proyectos" && <div className="mt-6 text-center">üìÅ Proyectos activos y finalizados...</div>}
      {esAdmin && tab === "contabilidad" && <div className="mt-6 text-center">üìä Registros contables aqu√≠...</div>}

      {/* CONTENIDO PARA OTROS ROLES */}
      {usuario.rol === 'contabilidad' && <div className="mt-6 text-center">Aqu√≠ ir√°n las funciones de contabilidad.</div>}
      {usuario.rol === 'reportador' && <div className="mt-6 text-center">Aqu√≠ ir√°n las funciones del reportador.</div>}
    </div>
  );
}

export default App;
