// VerPlanilla.jsx
import React, { useState } from "react";
import { PlusCircle } from "lucide-react";

export default function VerPlanilla({ personal }) {
  const [personaSeleccionada, setPersonaSeleccionada] = useState(null);
  const [verResumen, setVerResumen] = useState(false);

  const handleVerResumen = (p) => {
    setPersonaSeleccionada(p);
    setVerResumen(true);
  };

  return (
    <div className="overflow-x-auto bg-white/70 backdrop-blur-md p-6 rounded-xl shadow-xl mt-6">
      <h3 className="text-xl font-bold mb-4 text-gray-800">üìã Planilla de Personal</h3>

      <table className="min-w-full border border-gray-300 text-sm text-center">
        <thead className="bg-gray-200 text-gray-700">
          <tr>
            <th className="px-4 py-2 border"></th> {/* Columna para el bot√≥n "+" */}
            <th className="px-4 py-2 border">Nombre</th>
            <th className="px-4 py-2 border">Modalidad</th>
            <th className="px-4 py-2 border">Salario</th>
            <th className="px-4 py-2 border">Bono por d√≠a trabajado</th>
            <th className="px-4 py-2 border">Hora extra</th>
            <th className="px-4 py-2 border">Vi√°ticos</th>
            <th className="px-4 py-2 border font-semibold text-green-800">Total</th>
          </tr>
        </thead>
        <tbody>
          {personal.map((p) => {
            const salario =
              p.modalidad === "fijo"
                ? p.salarioporquincena || 0
                : p.salariopordia || 0;
            const bono = p.bonificacion || 0;
            const horaExtra = p.pagoporhoraextra || 0;
            const viaticos = p.viaticos_diarios || 0;
            const total = salario + bono + horaExtra + viaticos;

            return (
              <React.Fragment key={p.id}>
                <tr className="border-b">
                  <td className="px-2 py-2 border">
                    <button
                      onClick={() => handleVerResumen(p)}
                      className="text-green-600 hover:text-green-800"
                    >
                      <PlusCircle size={20} />
                    </button>
                  </td>
                  <td className="px-4 py-2 border">{p.nombrecompleto}</td>
                  <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
                  <td className="px-4 py-2 border">Q{salario}</td>
                  <td className="px-4 py-2 border">Q{bono}</td>
                  <td className="px-4 py-2 border">Q{horaExtra}</td>
                  <td className="px-4 py-2 border">Q{viaticos}</td>
                  <td className="px-4 py-2 border font-bold text-green-700">Q{total}</td>
                </tr>

                {/* Si el resumen est√° visible y es para esta persona */}
                {verResumen && personaSeleccionada?.id === p.id && (
                  <tr>
                    <td colSpan="8" className="px-4 py-2 bg-gray-50">
                      <h4 className="font-bold text-gray-800 mb-2">üìÖ Resumen diario de {p.nombrecompleto}</h4>

                      <div className="flex gap-4 mb-2">
                        <button
                          onClick={() => console.log("Modificar trabajos")}
                          className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 transition"
                        >
                          Modificar trabajos
                        </button>
                        <button
                          onClick={() => console.log("Reportar d√≠a")}
                          className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700 transition"
                        >
                          Reportar d√≠a
                        </button>
                      </div>

                      {/* Aqu√≠ podr√≠as insertar el formulario "Reportar d√≠a" condicionalmente */}
                      {/* Ejemplo: */}
                      {/* {mostrarFormulario && <FormularioReportarDia persona={p} />} */}
                    </td>
                  </tr>
                )}
              </React.Fragment>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
