      precioporcantidad: '',
      cajaChica: 'no',
      montoCajaChica: '',
      viaticosDiarios: ''
    });
    setVista('planilla');
    obtenerPersonal();
  } else {
    console.error(error);
    alert("Ocurrió un error al guardar el personal.");
  }
};



  // --------------------- UI PRINCIPAL ---------------------
