import { useState, useEffect } from 'react';
import { supabase } from '../supabase';
import React from 'react';
import AgregarReporte from './AgregarReporte';
export default function Personal({ usuario }) {
  const [vista, setVista] = useState('planilla');
  const [personal, setPersonal] = useState([]);
  const [reportes, setReportes] = useState([]);
  const [puestosList, setPuestosList] = useState([]);
  const [proyectosList, setProyectosList] = useState([]);
  const [historialVisible, setHistorialVisible] = useState(null);
  const [nuevoPuesto, setNuevoPuesto] = useState('');
  const [nuevoProyecto, setNuevoProyecto] = useState('');
  const [gastosCajaChica, setGastosCajaChica] = useState([]);
  const [reporteEnEdicion, setReporteEnEdicion] = useState(null);
  const [trabajadorEnEdicion, setTrabajadorEnEdicion] = useState(null);
  const [nuevoPersonal, setNuevoPersonal] = useState({
    
    nombre: '',
    puesto: '',
    proyecto: '',
    salario: '',
    meta: '',
    bonificacion: '',
    trabajometa: '',
    fecha: '',
    modalidad: 'día',
    precioporcantidad: '',
    cajaChica: 'no',
    montoCajaChica: '',
    viaticosDiarios: ''
  });

  useEffect(() => {
  const fetchGastos = async () => {
    const { data, error } = await supabase
      .from("gastos_cajachica")
      .select("*")
      .order("fecha", { ascending: false });
    if (!error) setGastosCajaChica(data);
  };
  fetchGastos();
}, []);

  useEffect(() => {
    fetchPuestos();
    fetchProyectos();
    obtenerPersonal();
    obtenerReportes();
  }, []);

const totalAcumulado = (reportesPersona) => {
  return reportesPersona.reduce((sum, r) => {
    const cantidad = r.cantidad || 0;
    const sePresento = r.sepresentoatrabajar === "Sí";
    let salarioBase = 0;
    let bonificacion = 0;
    const viatico = sePresento ? (r.viaticos || 0) : 0;

    if (sePresento) {
      if (p.modalidad === 'cantidad') {
        salarioBase = cantidad * (p.precioporcantidad || 0);
      } else {
        salarioBase = p.salariopordia || 0;
        const meta = p.metaestablecida || 0;
        if (cantidad > meta) {
          const extra = cantidad - meta;
          bonificacion = extra * (p.bonificacion || 0);
        }
      }
    }

    return sum + salarioBase + bonificacion + viatico;
  }, 0);
};
  
  const fetchPuestos = async () => {
    const { data, error } = await supabase.from('puestos').select('*').order('nombre', { ascending: true });
    if (!error) setPuestosList(data);
  };

  const fetchProyectos = async () => {
    const { data, error } = await supabase.from('proyectos').select('*').order('nombre', { ascending: true });
    if (!error) setProyectosList(data);
  };

  const agregarPuesto = async () => {
  if (!nuevoPersonal.puesto) return;

  const { data, error } = await supabase
    .from('puestos')
    .insert([{ nombre: nuevoPersonal.puesto }])
    .select(); 

  if (error) {
    console.error('Error al guardar puesto:', error.message);
    alert('No se pudo guardar el puesto');
    return;
  }

  if (data && data.length > 0) {
    setNuevoPersonal(p => ({ ...p, puesto: data[0].nombre }));
  }

  setNuevoPuesto('');
  fetchPuestos();
};

  const agregarProyecto = async () => {
  if (!nuevoPersonal.proyecto) return;

  const { data, error } = await supabase
    .from('proyectos')
    .insert([{ nombre: nuevoPersonal.proyecto }])
    .select(); // Esto hace que Supabase devuelva los datos insertados

  if (error) {
    console.error('Error al guardar proyecto:', error.message);
    alert('No se pudo guardar el proyecto');
    return;
  }

  if (data && data.length > 0) {
    setNuevoPersonal(p => ({ ...p, proyecto: data[0].nombre }));
  }

  setNuevoProyecto('');
  fetchProyectos();
};

   const obtenerPersonal = async () => {
    const { data, error } = await supabase
      .from('registrodepersonal')
      .select('*')
      .order('created_at', { ascending: false });
    if (!error) setPersonal(data);
  };

  const obtenerReportes = async () => {
    const { data, error } = await supabase
      .from('reportesdiarios')
      .select('*');
    if (!error) setReportes(data);
  };


  const handleAgregar = async () => {
    if (!nuevoPersonal.nombre || !nuevoPersonal.puesto) {
      alert('El nombre y puesto son obligatorios');
      return;
    }
    if (nuevoPersonal.modalidad === 'día' && !nuevoPersonal.salario) {
      alert('El salario por día es obligatorio');
      return;
    }
    if (nuevoPersonal.modalidad === 'cantidad' && !nuevoPersonal.precioporcantidad) {
      alert('El precio por unidad es obligatorio');
      return;
    }
    if (nuevoPersonal.cajaChica === 'si' && !nuevoPersonal.montoCajaChica) {
      alert('El monto de caja chica es obligatorio si está asignada');
      return;
    }

    const { data: existente } = await supabase
      .from('registrodepersonal')
      .select('*')
      .eq('nombrecompleto', nuevoPersonal.nombre)
      .single();

    const registro = {
      proyecto: nuevoPersonal.proyecto,
      metaestablecida: parseInt(nuevoPersonal.meta) || null,
      modalidad: nuevoPersonal.modalidad,
      bonificacion: parseFloat(nuevoPersonal.bonificacion) || 0,
      fechadeingreso: nuevoPersonal.fecha || new Date().toISOString(),
      precioporcantidad: nuevoPersonal.modalidad === 'cantidad' ? parseFloat(nuevoPersonal.precioporcantidad) : null,
      salariopordia: nuevoPersonal.modalidad === 'día' ? parseFloat(nuevoPersonal.salario) : null,
      trabajometa: nuevoPersonal.trabajometa,
      caja_chica_asignada: nuevoPersonal.cajaChica === 'si',
      monto_caja_chica: nuevoPersonal.cajaChica === 'si' ? parseFloat(nuevoPersonal.montoCajaChica) : null,
      viaticos_diarios: parseFloat(nuevoPersonal.viaticosDiarios) || 0
    };

    if (existente) {
      await supabase.from('registrodepersonal').update(registro).eq('id', existente.id);
    } else {
      await supabase.from('registrodepersonal').insert([{ nombrecompleto: nuevoPersonal.nombre, puesto: nuevoPersonal.puesto, ...registro }]);
    }

    setNuevoPersonal({
      nombre: '', puesto: '', proyecto: '', salario: '', meta: '', bonificacion: '', trabajometa: '', fecha: '',
      modalidad: 'día', precioporcantidad: '', cajaChica: 'no', montoCajaChica: '', viaticosDiarios: ''
    });
    setVista('planilla');
    obtenerPersonal();
  };

  const calcularRealizado = nombre =>
    reportes.filter(r => r.nombretrabajador === nombre).reduce((sum, r) => sum + (r.cantidad || 0), 0);

  const calcularViaticos = nombre =>
    reportes.filter(r => r.nombretrabajador === nombre).reduce((sum, r) => sum + (r.viaticos || 0), 0);

  const calcularPago = (p) => {
  // Filtrar reportes por trabajador
  const reportesTrabajador = reportes.filter(r => r.nombretrabajador === p.nombrecompleto);

  let realizadoTotal = 0;
  let bonif = 0;

  if (p.modalidad === 'día' && p.metaestablecida) {
    // Agrupar por fecha y calcular extra diario
    const agrupadosPorDia = {};
    for (const r of reportesTrabajador) {
      if (!agrupadosPorDia[r.fechareporte]) agrupadosPorDia[r.fechareporte] = 0;
      agrupadosPorDia[r.fechareporte] += r.cantidad || 0;
    }
    for (const dia in agrupadosPorDia) {
      const totalDia = agrupadosPorDia[dia];
      realizadoTotal += totalDia;
      if (totalDia > p.metaestablecida) {
        bonif += (totalDia - p.metaestablecida) * (p.bonificacion || 0);
      }
    }
  } else {
    realizadoTotal = reportesTrabajador.reduce((sum, r) => sum + (r.cantidad || 0), 0);
    if (p.modalidad === 'cantidad') {
      bonif = realizadoTotal >= (p.metaestablecida || 0) ? p.bonificacion : 0;
    }
  }

  const pagoBase = p.modalidad === 'cantidad' ? realizadoTotal * p.precioporcantidad : p.salariopordia || 0;
  const total = pagoBase + bonif;

  return { realizado: realizadoTotal, pago: pagoBase, bonif, total };
};


  return (
    <div className="mt-6 max-w-7xl mx-auto bg-white/10 backdrop-blur-xl p-6 rounded-2xl">
      <h2 className="text-2xl font-bold mb-6 text-center">👷 Gestión de Personal</h2>
      <div className="flex justify-center gap-4 mb-6">
        <button
          onClick={() => setVista('formulario')}
          className={`px-4 py-2 rounded ${vista === 'formulario' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
        >Agregar personal</button>
        <button
          onClick={() => setVista('planilla')}
          className={`px-4 py-2 rounded ${vista === 'planilla' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
        >Planilla completa</button>
      </div>

      {vista === 'formulario' && (
  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
    <input
      type="text"
      placeholder="Nombre completo"
      value={nuevoPersonal.nombre}
      onChange={e => setNuevoPersonal({ ...nuevoPersonal, nombre: e.target.value })}
      className="border p-2 rounded"
    />

    {/* Puesto con opción de agregar */}
    {nuevoPuesto === '' ? (
      <div className="flex gap-2">
        <select
          value={nuevoPersonal.puesto}
          onChange={e => setNuevoPersonal({ ...nuevoPersonal, puesto: e.target.value })}
          className="border p-2 rounded flex-1"
        >
          <option value="">Seleccionar puesto...</option>
          {puestosList.map((item, i) => (
            <option key={i} value={item.nombre}>{item.nombre}</option>
          ))}
        </select>
        <button onClick={() => setNuevoPuesto('nuevo')} className="bg-green-600 text-white px-3 py-2 rounded">+ Puesto</button>
      </div>
    ) : (
      <div className="flex gap-2">
        <input
          type="text"
          placeholder="Nuevo puesto"
          value={nuevoPersonal.puesto}
          onChange={e => setNuevoPersonal({ ...nuevoPersonal, puesto: e.target.value })}
          className="border p-2 rounded flex-1"
        />
        <button onClick={agregarPuesto} className="bg-blue-600 text-white px-3 py-2 rounded">Guardar</button>
        <button onClick={() => { setNuevoPuesto(''); setNuevoPersonal({ ...nuevoPersonal, puesto: '' }); }} className="bg-gray-400 text-white px-3 py-2 rounded">Cancelar</button>
      </div>
    )}


    {/* Proyecto con opción de agregar */}
    {nuevoProyecto === '' ? (
      <div className="flex gap-2">
        <select
          value={nuevoPersonal.proyecto}
          onChange={e => setNuevoPersonal({ ...nuevoPersonal, proyecto: e.target.value })}
          className="border p-2 rounded flex-1"
        >
          <option value="">Seleccionar proyecto...</option>
          {proyectosList.map((item, i) => (
            <option key={i} value={item.nombre}>{item.nombre}</option>
          ))}
        </select>
        <button onClick={() => setNuevoProyecto('nuevo')} className="bg-green-600 text-white px-3 py-2 rounded">+ Proyecto</button>
      </div>
    ) : (
      <div className="flex gap-2">
        <input
          type="text"
          placeholder="Nuevo proyecto"
          value={nuevoPersonal.proyecto}
          onChange={e => setNuevoPersonal({ ...nuevoPersonal, proyecto: e.target.value })}
          className="border p-2 rounded flex-1"
        />
        <button onClick={agregarProyecto} className="bg-blue-600 text-white px-3 py-2 rounded">Guardar</button>
        <button onClick={() => { setNuevoProyecto(''); setNuevoPersonal({ ...nuevoPersonal, proyecto: '' }); }} className="bg-gray-400 text-white px-3 py-2 rounded">Cancelar</button>
      </div>
    )}

    {/* Modalidad */}
    <select
      value={nuevoPersonal.modalidad}
      onChange={e => setNuevoPersonal({ ...nuevoPersonal, modalidad: e.target.value })}
      className="border p-2 rounded"
    >
      <option value="día">Día</option>
      <option value="cantidad">Cantidad</option>
    </select>

          {nuevoPersonal.modalidad === 'día' && <input type="number" placeholder="Salario por día" value={nuevoPersonal.salario} onChange={e => setNuevoPersonal({ ...nuevoPersonal, salario: e.target.value })} className="border p-2 rounded" />}          
          {nuevoPersonal.modalidad === 'cantidad' && <input type="number" placeholder="Precio por unidad" value={nuevoPersonal.precioporcantidad} onChange={e => setNuevoPersonal({ ...nuevoPersonal, precioporcantidad: e.target.value })} className="border p-2 rounded" />}          
            
          <input type="number" placeholder="Meta establecida" value={nuevoPersonal.meta} onChange={e => setNuevoPersonal({ ...nuevoPersonal, meta: e.target.value })} className="border p-2 rounded" />
          <input type="number" placeholder="Bonificación meta (Q por unidad extra)" value={nuevoPersonal.bonificacion} onChange={e => setNuevoPersonal({ ...nuevoPersonal, bonificacion: e.target.value })} className="border p-2 rounded" />
          <input type="text" placeholder="Trabajo asignado (meta)" value={nuevoPersonal.trabajometa} onChange={e => setNuevoPersonal({ ...nuevoPersonal, trabajometa: e.target.value })} className="border p-2 rounded" />
          <input type="date" value={nuevoPersonal.fecha} onChange={e => setNuevoPersonal({ ...nuevoPersonal, fecha: e.target.value })} className="border p-2 rounded" />

          <select value={nuevoPersonal.cajaChica} onChange={e => setNuevoPersonal({ ...nuevoPersonal, cajaChica: e.target.value })} className="border p-2 rounded">
            <option value="no">Caja chica: No</option>
            <option value="si">Caja chica: Sí</option>
          </select>
          {nuevoPersonal.cajaChica === 'si' && <input type="number" placeholder="Monto de caja chica" value={nuevoPersonal.montoCajaChica} onChange={e => setNuevoPersonal({ ...nuevoPersonal, montoCajaChica: e.target.value })} className="border p-2 rounded" />}          

          <input type="number" placeholder="Viáticos diarios" value={nuevoPersonal.viaticosDiarios} onChange={e => setNuevoPersonal({ ...nuevoPersonal, viaticosDiarios: e.target.value })} className="border p-2 rounded" />

          <button onClick={handleAgregar} className="bg-green-600 text-white py-2 rounded col-span-2">
      Guardar Personal
    </button>
  </div>
)}

      {/* Planilla */}
      {vista === 'planilla' && (
        <div className="w-full flex justify-center">
          <div className="overflow-x-auto">
            <table className="min-w-max border border-gray-300 text-sm text-center">
              <thead className="bg-gray-100">
                <tr>
                  <th className="px-4 py-2 border">Trabajador</th>
                  <th className="px-4 py-2 border">Puesto</th>
                  <th className="px-4 py-2 border">Proyecto</th>
                  <th className="px-4 py-2 border">Salario por día</th>
                  <th className="px-4 py-2 border">Meta establecida</th>
                  <th className="px-4 py-2 border">Pago por unidad extra</th>
                  <th className="px-4 py-2 border">Trabajo asignado</th>
                  <th className="px-4 py-2 border">No. de grupo</th>
                  <th className="px-4 py-2 border">Caja chica</th>
                  <th className="px-4 py-2 border">Ver detalle</th>
                </tr>
              </thead>
              <tbody>
          {personal.map((p, i) => {
  const reportesPersona = reportes.filter(r => r.nombretrabajador === p.nombrecompleto);
  const mostrarDetalle = historialVisible === p.nombrecompleto;
  const ultimoGrupo = reportesPersona.length > 0 ? reportesPersona[reportesPersona.length - 1].numerogrupo || '-' : '-';

  const totalAcumulado = reportesPersona.reduce((sum, r) => {
    const cantidad = r.cantidad || 0;
    const sePresento = r.sepresentoatrabajar === "Sí";
    let salarioBase = 0;
    let bonificacion = 0;
    const viatico = sePresento ? (r.viaticos || 0) : 0;

    if (sePresento) {
      if (p.modalidad === 'cantidad') {
        salarioBase = cantidad * (p.precioporcantidad || 0);
      } else {
        salarioBase = p.salariopordia || 0;
        const meta = p.metaestablecida || 0;
        if (cantidad > meta) {
          const extra = cantidad - meta;
          bonificacion = extra * (p.bonificacion || 0);
        }
      }
    }

    return sum + salarioBase + bonificacion + viatico;
  }, 0);

  return (
    <React.Fragment key={i}>
      <tr className="bg-white">
        <td className="border p-2">{p.nombrecompleto}</td>
        <td className="border p-2">{p.puesto}</td>
        <td className="border p-2">{p.proyecto}</td>
        <td className="border p-2">Q{p.salariopordia?.toFixed(2) || '-'}</td>
        <td className="border p-2">{p.metaestablecida || '-'}</td>
        <td className="border p-2">Q{p.bonificacion?.toFixed(2) || '-'}</td>
        <td className="border p-2">{p.trabajometa || '-'}</td>
        <td className="border p-2">{ultimoGrupo}</td>
        <td className="border p-2">{p.caja_chica_asignada ? 'Sí' : 'No'}</td>
        <td className="border p-2">
          <div className="flex flex-col gap-1 items-center">
            <button
              onClick={() => setHistorialVisible(mostrarDetalle ? null : p.nombrecompleto)}
              className="text-blue-600 font-medium hover:text-blue-800 underline"
            >{mostrarDetalle ? 'Ocultar' : 'Ver detalle'}</button>
            <button
              onClick={() => setTrabajadorEnEdicion(p)}
              className="text-blue-600 font-medium hover:text-blue-800 underline"
            >Editar datos</button>
          </div>
        </td>
      </tr>

      {mostrarDetalle && (
        <>
          <tr>
            <td colSpan="10" className="p-4 text-center align-top">
              <div className="inline-block text-left w-full max-w-4xl bg-gray-100 p-4 rounded shadow border border-gray-300">
                <h4 className="font-semibold mb-2">📋 Reportes diarios de {p.nombrecompleto}</h4>
                {/* Aquí puedes seguir con tu tabla de reportes */}
              </div>
            </td>
          </tr>

          <tr className="bg-gray-100 font-bold">
            <td className="border p-2 text-right" colSpan={7}>Total acumulado</td>
            <td className="border p-2 text-right">
              Q{totalAcumulado.toFixed(2)}
            </td>
          </tr>

          <tr>
            <td colSpan="10" className="p-4">
              <AgregarReporte
                trabajador={p}
                usuario={usuario}
                onGuardar={obtenerReportes}
              />
            </td>
          </tr>
        </>
      )}
    </React.Fragment>
  );
})}

      </tbody>
    </table>
  </div>
</div>



            
        
  )}




 <>
  {/* TOTAL ACUMULADO */}
  <tr className="bg-gray-100 font-bold">
    <td className="border p-2 text-right" colSpan={7}>Total acumulado</td>
    <td className="border p-2 text-right">
      Q{reportesPersona.reduce((sum, r) => {
        const cantidad = r.cantidad || 0;
        const sePresento = r.sepresentoatrabajar === "Sí";
        let salarioBase = 0;
        let bonificacion = 0;
        const viatico = sePresento ? (r.viaticos || 0) : 0;

        if (sePresento) {
          if (p.modalidad === 'cantidad') {
            salarioBase = cantidad * (p.precioporcantidad || 0);
          } else {
            salarioBase = p.salariopordia || 0;
            const meta = p.metaestablecida || 0;
            if (cantidad > meta) {
              const extra = cantidad - meta;
              bonificacion = extra * (p.bonificacion || 0);
            }
          }
        }

        return sum + salarioBase + bonificacion + viatico;
      }, 0).toFixed(2)}
    </td>
  </tr>

  {/* Formulario para agregar reporte */}
  <tr>
    <td colSpan="10" className="p-4">
      <AgregarReporte
        trabajador={p}
        usuario={usuario}
        onGuardar={obtenerReportes}
      />
    </td>
  </tr>
</>

            {reporteEnEdicion && (
  <div className="mt-4 bg-yellow-50 p-4 rounded shadow border border-yellow-300">
    <h4 className="font-semibold mb-2">✏️ Editar reporte del {reporteEnEdicion.fechareporte}</h4>

    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <input
        type="number"
        className="border p-2 rounded"
        value={reporteEnEdicion.cantidad}
        onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, cantidad: parseInt(e.target.value) })}
        placeholder="Cantidad"
      />

      <input
        type="number"
        className="border p-2 rounded"
        value={reporteEnEdicion.viaticos}
        onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, viaticos: parseFloat(e.target.value) })}
        placeholder="Viáticos"
      />

      <input
        type="text"
        className="border p-2 rounded col-span-2"
        value={reporteEnEdicion.comentario}
        onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, comentario: e.target.value })}
        placeholder="Comentario"
      />
    </div>

    <div className="flex gap-4 mt-4">
  <button
    className="bg-green-600 text-white px-4 py-2 rounded"
    onClick={async () => {
      if (!reporteEnEdicion?.id) {
        alert("No se puede actualizar: falta el ID del reporte.");
        return;
      }

      const actualizado = {
        cantidad: parseInt(reporteEnEdicion.cantidad),
        viaticos: parseFloat(reporteEnEdicion.viaticos),
        comentario: reporteEnEdicion.comentario || "--",
        sepresentoatrabajar: reporteEnEdicion.sepresetentoatrabajar || "Sí",
        numerogrupo: reporteEnEdicion.numerogrupo || null,
        salariopordia: parseFloat(reporteEnEdicion.salariopordia || 0),
        metaestablecida: parseInt(reporteEnEdicion.metaestablecida || 0),
        bonificacion: parseFloat(reporteEnEdicion.bonificacion || 0),
        modalidad: reporteEnEdicion.modalidad || "día",
        precioporcantidad: parseFloat(reporteEnEdicion.precioporcantidad || 0),
        proyecto: reporteEnEdicion.proyecto || "",
      };

      const { error } = await supabase
        .from("reportesdiarios")
        .update(actualizado)
        .eq("id", reporteEnEdicion.id);

      if (!error) {
        alert("Reporte actualizado correctamente");
        setReporteEnEdicion(null); // salir del modo edición
        obtenerReportes();         // refrescar tabla
      } else {
        console.error("Error al actualizar:", error);
        alert("Ocurrió un error al actualizar el reporte");
      }
    }}
  >
    Guardar cambios
  </button>

  <button
    onClick={() => setReporteEnEdicion(null)}
    className="bg-gray-500 text-white px-4 py-2 rounded"
  >
    Cancelar
    </button>
</div>
</div>
)}

    {vista === 'planilla' && historialVisible && (
        <div className="mt-6 bg-gray-100 p-4 rounded">
          <h3 className="font-bold text-lg mb-2">
            📘 Detalle de reportes de {historialVisible}
          </h3>
          {reportes
            .filter(r => r.nombretrabajador === historialVisible)
            .map((r, i) => (
              <p key={i}>📅 {r.fechareporte} — {r.cantidad} unidades — Viáticos: Q{(r.viaticos || 0).toFixed(2)} — {r.comentario || 'sin comentario'}</p>
            ))}
          <button
            onClick={() => setHistorialVisible(null)}
            className="mt-4 text-red-600 underline"
          >Cerrar</button>
        </div>
      )}
    </div>
 );
}
