                                  placeholder="Ej: 120"
                                  className="border p-2 rounded w-full"
                                />
                              </div>

                              {/* Viáticos */}
                              <div>
                                <label className="text-sm font-medium text-gray-700 mb-1 block">Viáticos</label>
                                <input
                                  type="number"
                                  value={viaticos}
                                  onChange={(e) => setViaticos(e.target.value)}
                                  placeholder="Ej: 50"
                                  className="border p-2 rounded w-full"
                                />
                              </div>

                              {/* Grupo */}
                              <div>
                                <label className="text-sm font-medium text-gray-700 mb-1 block">Grupo</label>
                                <input
                                  type="number"
                                  value={grupo}
                                  onChange={(e) => setGrupo(e.target.value)}
