            <table className="min-w-full border border-gray-300 text-sm text-center">
              <thead className="bg-gray-200 text-gray-700">
                <tr>
                  <th className="px-4 py-2 border">Trabajador</th>
                  <th className="px-4 py-2 border">Puesto</th>
                  <th className="px-4 py-2 border">Proyecto</th>
                  <th className="px-4 py-2 border">Modalidad</th>
                  <th className="px-4 py-2 border">Meta asignada</th>
                  <th className="px-4 py-2 border">Bonificación</th>
                  <th className="px-4 py-2 border">Salario/Día o Precio/U</th>
                  <th className="px-4 py-2 border">Pago Total</th>
                  <th className="px-4 py-2 border">Acciones</th> {/* Nueva columna */}
                </tr>
              </thead>
              <tbody>
                {personal.map((p, i) => {
                  const reportesPersona = reportes.filter(r => r.nombretrabajador === p.nombrecompleto);
                  const mostrarDetalle = historialVisible === p.nombrecompleto;

                  const total = reportesPersona.reduce((sum, r) => {
                    const cantidad = r.cantidad || 0;
                    const sePresento = r.sepresentoatrabajar === "Sí";
                    let salarioBase = 0;
                    let bonificacion = 0;
                    const viatico = sePresento ? (r.viaticos || 0) : 0;

                    if (sePresento) {
                      if (p.modalidad === 'cantidad') {
                        salarioBase = cantidad * (p.precioporcantidad || 0);
                      } else {
                        salarioBase = p.salariopordia || 0;
                        const meta = p.metaestablecida || 0;
                        if (cantidad > meta) {
                          const extra = cantidad - meta;
                          bonificacion = extra * (p.bonificacion || 0);
                        }
                      }
                    }

                    return sum + salarioBase + bonificacion + viatico;
                  }, 0);
                    
                  return (
                    <React.Fragment key={i}>
                      <tr className="bg-white/50 border-b hover:bg-white/80 transition">
                        <td className="px-4 py-2 border">{p.nombrecompleto}</td>
                        <td className="px-4 py-2 border">{p.puesto}</td>
                        <td className="px-4 py-2 border">{p.proyecto}</td>
                        <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
                        <td className="px-4 py-2 border">
                          <div>
                            <span className="block font-semibold">
                              {p.metaestablecida || '-'} unidades
                            </span>
                            <span className="text-xs text-gray-600">
                              {p.trabajometa || 'Sin detalle'}
                            </span>
                          </div>
                        </td>
                        <td className="px-4 py-2 border">Q{p.bonificacion?.toFixed(2) || '0.00'}</td>
                        <td className="px-4 py-2 border">
                          {p.modalidad === 'día'
                            ? `Q${p.salariopordia?.toFixed(2) || '0.00'}`
                            : `Q${p.precioporcantidad?.toFixed(2) || '0.00'}`}
                        </td>
                        <td className="px-4 py-2 border font-semibold text-green-700">
                          Q{total.toFixed(2)}
                        </td>
                        <td className="px-4 py-2 border">
                                          
                          <div className="flex flex-col gap-2 items-center">
                            <button
                              onClick={() => setHistorialVisible(mostrarDetalle ? null : p.nombrecompleto)}
                              className="bg-blue-100 hover:bg-blue-200 text-blue-800 font-medium px-3 py-1 rounded-full shadow-sm transition text-xs"
                            >
                              {mostrarDetalle ? 'Ocultar' : 'Ver Detalle'}
                            </button>

                            <button
                              onClick={() =>
                                setTrabajadorEnEdicion(
                                  trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto ? null : p
                                )
                              }
                              className={`${
                                trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto
                                  ? 'bg-gray-200 text-gray-700'
                                  : 'bg-yellow-100 hover:bg-yellow-200 text-yellow-800'
                              } font-medium px-3 py-1 rounded-full shadow-sm transition text-xs`}
                            >
                              {trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto
                                ? 'Ocultar'
                                : 'Editar Datos'}
                            </button>
                          </div>
                                  
                            
                        </td>
                      </tr>


          {/*  👇 Tarjetas de edición que se muestran debajo de la fila del trabajador seleccionado*/}
                  {trabajadorEnEdicion?.nombrecompleto === p.nombrecompleto && (
                    <tr>
                    <td colSpan="9" className="bg-white p-4 border-t">
                      <div className="text-center mb-4">
                        <h4 className="text-xl font-bold text-gray-800 mb-2 flex items-center justify-center gap-2">
                          🛠 Opciones de edición para {p.nombrecompleto}
                        </h4>
                      </div>

                      <div className="flex flex-wrap justify-center gap-6">
                        {/* Reportar Día */}
                        <div
                          onClick={() => {
                            setVista('reportar_dia');
                            setHistorialVisible(null);
                          }}
                          className="bg-blue-50 border border-blue-200 p-4 rounded-xl shadow cursor-pointer hover:bg-blue-100 transition w-72 text-center"
                        >
                          <h5 className="text-blue-800 font-semibold text-lg mb-1 flex items-center justify-center gap-2">
                            📅 Reportar Día
                          </h5>
                          <p className="text-sm text-blue-700">Ingresar nuevo reporte para hoy</p>
                        </div>

                      
                        {/* Modificar Trabajo Actual */}
                        <div
                          onClick={() => {
                           setTrabajadorEnEdicion(p); 
                            setVista('modificar_trabajo');
                            setHistorialVisible(null);
                          }}
                          className="bg-purple-50 border border-purple-200 p-4 rounded-xl shadow cursor-pointer hover:bg-purple-100 transition w-72 text-center"
                        >
                          <h5 className="text-purple-800 font-semibold text-lg mb-1 flex items-center justify-center gap-2">
                            ⚙️ Modificar Trabajo Actual
                          </h5>
                          <p className="text-sm text-purple-700">Editar datos sin afectar reportes anteriores</p>
                        </div>
                      </div>
                    </td>
                  </tr>
                  )}
                          

             
              {/* Mostrar detalles estéticos de los reportes diarios del trabajador seleccionado */}
                {mostrarDetalle && (
                  <tr>
                    <td colSpan="9" className="bg-white p-4 border-b rounded-xl">
                      <h4 className="text-lg font-bold text-gray-800 mb-4">📋 Reportes diarios de {p.nombrecompleto}</h4>

                      <div className="grid gap-4">
                        {reportesPersona.map((r, idx) => {
                          const cantidad = r.cantidad || 0;
                          const viaticos = r.viaticos || 0;
                          const salario = r.modalidad === "día" ? (r.salariopordia || 0) : cantidad * (r.precioporcantidad || 0);
                          const bonificacion = r.modalidad === "cantidad" && cantidad > r.metaestablecida
                            ? (cantidad - r.metaestablecida) * (r.bonificacion || 0)
                            : 0;
                          const total = salario + bonificacion + viaticos;

                          return (
                            <div key={idx} className="bg-gray-50 p-4 rounded-lg shadow-sm border border-gray-200">
                              <div className="flex justify-between items-center mb-2">
                                <span className="text-lg font-semibold text-gray-700">{r.fechareporte}</span>
                                <span className="bg-gray-200 text-gray-700 px-3 py-1 rounded-full text-sm">Grupo {r.numerogrupo || 'N/D'}</span>
                              </div>
                              <p className="text-gray-600 mb-1">{cantidad} unidades — Viáticos: Q{viaticos.toFixed(2)}</p>
                              <p className="text-sm text-gray-500 italic mb-2">{r.comentario || "--"}</p>
                              <div className="text-sm text-gray-700 grid md:grid-cols-3 gap-2">
                                <p><strong>Proyecto:</strong> {r.proyecto || 'N/D'}</p>
                                <p><strong>Puesto:</strong> {p.puesto || 'N/D'}</p>
                                <p><strong>Modalidad:</strong> {r.modalidad || 'día'}</p>
                                <p><strong>Salario del día:</strong> Q{salario.toFixed(2)}</p>
                                <p><strong>Bonificación:</strong> Q{bonificacion.toFixed(2)}</p>
                                <p className="font-semibold text-green-700"><strong>Total:</strong> Q{total.toFixed(2)}</p>
                              </div>
                                  <button
                                    onClick={() => setReporteEnEdicion(r)}
                                    className="mt-3 inline-block bg-blue-100 hover:bg-blue-200 text-blue-800 px-4 py-1 rounded-full text-sm shadow-sm transition"
                                  >
                                    ✏️ Modificar reporte
                                  </button>
                                   {/* codigo para modificar el reporte ya hecho  */}
                                 {reporteEnEdicion?.id === r.id && (
                              <div className="mt-4 bg-yellow-50 p-4 rounded shadow border border-yellow-300">
                                <h4 className="font-semibold mb-4 text-lg flex items-center gap-2">✏️ Editar reporte del {r.fechareporte}</h4>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                  {/* Cantidad */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Cantidad de unidades</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.cantidad}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, cantidad: parseInt(e.target.value) })
                                      }
                                      placeholder="Ej: 120"
                                    />
                                  </div>

                                  {/* Viáticos */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Viáticos</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.viaticos}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, viaticos: parseFloat(e.target.value) })
                                      }
                                      placeholder="Ej: 50"
                                    />
                                  </div>

                                 {/* Proyecto */}
                                  <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-1">Proyecto</label>
                                    <select
                                      value={reporteEnEdicion.proyecto}
                                      onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, proyecto: e.target.value })}
                                      className="w-full border border-gray-300 p-2 rounded"
                                    >
                                      <option value="">Seleccione un proyecto</option>
                                      {proyectosList.map((p, idx) => (
                                        <option key={idx} value={p.nombre}>{p.nombre}</option>
                                      ))}
                                    </select>

                                  </div>

                                  {/* Puesto */}
                                  <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-1">Puesto</label>
                                    <select
                                      value={reporteEnEdicion.puesto}
                                      onChange={(e) => setReporteEnEdicion({ ...reporteEnEdicion, puesto: e.target.value })}
                                      className="w-full border border-gray-300 p-2 rounded"
                                    >
                                      <option value="">Seleccione un puesto</option>
                                      {puestosList.map((p, idx) => (
                                        <option key={idx} value={p.nombre}>{p.nombre}</option>
                                      ))}
                                    </select>

                                  </div>

                                  {/* Modalidad */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Modalidad</label>
                                    <select
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.modalidad}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, modalidad: e.target.value })
                                      }
                                    >
                                      <option value="día">Día</option>
                                      <option value="cantidad">Cantidad</option>
                                    </select>
                                  </div>

                                  {/* Precio por unidad o salario por día */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">
                                      {reporteEnEdicion.modalidad === 'día' ? 'Salario del día' : 'Precio por unidad'}
                                    </label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={
                                        reporteEnEdicion.modalidad === 'día'
                                          ? reporteEnEdicion.salariopordia
                                          : reporteEnEdicion.precioporcantidad
                                      }
                                      onChange={(e) => {
                                        const valor = parseFloat(e.target.value);
                                        if (reporteEnEdicion.modalidad === 'día') {
                                          setReporteEnEdicion({ ...reporteEnEdicion, salariopordia: valor });
                                        } else {
                                          setReporteEnEdicion({ ...reporteEnEdicion, precioporcantidad: valor });
                                        }
                                      }}
                                    />
                                  </div>

                                  {/* Bonificación */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Bonificación</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.bonificacion}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, bonificacion: parseFloat(e.target.value) })
                                      }
                                    />
                                  </div>

                                  {/* Meta establecida */}
                                  <div>
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Meta establecida</label>
                                    <input
                                      type="number"
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.metaestablecida}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, metaestablecida: parseInt(e.target.value) })
                                      }
                                    />
                                  </div>

                                  {/* Comentario */}
                                  <div className="md:col-span-2">
                                    <label className="text-sm font-medium text-gray-700 mb-1 block">Comentario</label>
                                    <textarea
                                      className="border p-2 rounded w-full"
                                      value={reporteEnEdicion.comentario}
                                      onChange={(e) =>
                                        setReporteEnEdicion({ ...reporteEnEdicion, comentario: e.target.value })
                                      }
                                      placeholder="Observaciones (opcional)"
                                    />
                                  </div>
                                </div>

                                {/* Botones */}
                                <div className="flex gap-4 mt-4">
                                  <button
                                    className="bg-green-600 text-white px-4 py-2 rounded"
                                    onClick={async () => {
                                      if (!reporteEnEdicion?.id) {
                                        alert("No se puede actualizar: falta el ID del reporte.");
                                        return;
                                      }

                                      const actualizado = {
                                        cantidad: parseInt(reporteEnEdicion.cantidad),
                                        viaticos: parseFloat(reporteEnEdicion.viaticos),
                                        comentario: reporteEnEdicion.comentario || "--",
                                        sepresentoatrabajar: reporteEnEdicion.sepresetentoatrabajar || "Sí",
                                        numerogrupo: reporteEnEdicion.numerogrupo || null,
                                        salariopordia: parseFloat(reporteEnEdicion.salariopordia || 0),
                                        metaestablecida: parseInt(reporteEnEdicion.metaestablecida || 0),
                                        bonificacion: parseFloat(reporteEnEdicion.bonificacion || 0),
                                        modalidad: reporteEnEdicion.modalidad || "día",
                                        precioporcantidad: parseFloat(reporteEnEdicion.precioporcantidad || 0),
                                        proyecto: reporteEnEdicion.proyecto || "",
                                        puesto: reporteEnEdicion.puesto || ""
                                      };

                                      const { error } = await supabase
                                        .from("reportesdiarios")
                                        .update(actualizado)
                                        .eq("id", reporteEnEdicion.id);

                                      if (!error) {
                                        alert("Reporte actualizado correctamente");
                                        setReporteEnEdicion(null);
                                        obtenerReportes();
                                      } else {
                                        console.error("Error al actualizar:", error);
                                        alert("Ocurrió un error al actualizar el reporte");
                                      }
                                    }}
                                  >
                                    Guardar cambios
                                  </button>

                                  <button
                                    onClick={() => setReporteEnEdicion(null)}
                                    className="bg-gray-600 text-white px-4 py-2 rounded"
                                  >
                                    Cancelar
                                  </button>
                                </div>
                              </div>
                            )}

                                                            

                        </div>
                        
                          );
                        })}
                      </div>
                    </td>
                  </tr>
                )}


                            </React.Fragment>
                          );
                        })}
  

                      </tbody>
                    </table>
                  </div>
                )}

{/* Formulario de mofificacion actual */}
{vista === 'modificar_trabajo' && (
  <div className="bg-white/60 mt-6 p-6 rounded-xl shadow-md">
    <h3 className="text-xl font-bold text-purple-800 mb-4">⚙️ Modificar Trabajo Actual de {trabajadorEnEdicion.nombrecompleto}</h3>

    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      {/* Proyecto */}
      <div>
        <label className="text-sm font-medium text-gray-700 mb-1 block">Proyecto</label>
        <select
          value={trabajadorEnEdicion.proyecto}
          onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, proyecto: e.target.value })}
          className="border p-2 rounded w-full"
        >
          <option value="">Seleccione un proyecto</option>
          {proyectosList.map((p, idx) => (
            <option key={idx} value={p.nombre}>{p.nombre}</option>
          ))}
        </select>
      </div>

      {/* Puesto */}
      <div>
        <label className="text-sm font-medium text-gray-700 mb-1 block">Puesto</label>
        <select
          value={trabajadorEnEdicion.puesto}
          onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, puesto: e.target.value })}
          className="border p-2 rounded w-full"
        >
          <option value="">Seleccione un puesto</option>
          {puestosList.map((p, idx) => (
            <option key={idx} value={p.nombre}>{p.nombre}</option>
          ))}
        </select>
      </div>

      {/* Modalidad */}
      <div>
        <label className="text-sm font-medium text-gray-700 mb-1 block">Modalidad</label>
        <select
          value={trabajadorEnEdicion.modalidad}
          onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, modalidad: e.target.value })}
          className="border p-2 rounded w-full"
        >
          <option value="día">Día</option>
          <option value="cantidad">Cantidad</option>
        </select>
      </div>

      {/* Salario o Precio */}
      <div>
        <label className="text-sm font-medium text-gray-700 mb-1 block">
          {trabajadorEnEdicion.modalidad === 'día' ? 'Salario por día' : 'Precio por unidad'}
        </label>
        <input
          type="number"
          className="border p-2 rounded w-full"
          value={
            trabajadorEnEdicion.modalidad === 'día'
              ? trabajadorEnEdicion.salariopordia
              : trabajadorEnEdicion.precioporcantidad
          }
          onChange={(e) => {
            const val = parseFloat(e.target.value);
            if (trabajadorEnEdicion.modalidad === 'día') {
              setTrabajadorEnEdicion({ ...trabajadorEnEdicion, salariopordia: val });
            } else {
              setTrabajadorEnEdicion({ ...trabajadorEnEdicion, precioporcantidad: val });
            }
          }}
        />
      </div>

      {/* Meta */}
      <div>
        <label className="text-sm font-medium text-gray-700 mb-1 block">Meta establecida</label>
        <input
          type="number"
          className="border p-2 rounded w-full"
          value={trabajadorEnEdicion.metaestablecida || ''}
          onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, metaestablecida: parseInt(e.target.value) })}
        />
      </div>

      {/* Bonificación */}
      <div>
        <label className="text-sm font-medium text-gray-700 mb-1 block">Bonificación</label>
        <input
          type="number"
          className="border p-2 rounded w-full"
          value={trabajadorEnEdicion.bonificacion || ''}
          onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, bonificacion: parseFloat(e.target.value) })}
        />
      </div>

      {/* Trabajo asignado */}
      <div className="md:col-span-2">
        <label className="text-sm font-medium text-gray-700 mb-1 block">Trabajo asignado</label>
        <input
          type="text"
          className="border p-2 rounded w-full"
          value={trabajadorEnEdicion.trabajometa || ''}
          onChange={(e) => setTrabajadorEnEdicion({ ...trabajadorEnEdicion, trabajometa: e.target.value })}
        />
      </div>
    </div>

    <div className="flex justify-end gap-4 mt-6">
      <button
        onClick={async () => {
          const actualizado = {
            proyecto: trabajadorEnEdicion.proyecto,
            puesto: trabajadorEnEdicion.puesto,
            modalidad: trabajadorEnEdicion.modalidad,
            salariopordia: parseFloat(trabajadorEnEdicion.salariopordia || 0),
            precioporcantidad: parseFloat(trabajadorEnEdicion.precioporcantidad || 0),
            metaestablecida: parseInt(trabajadorEnEdicion.metaestablecida || 0),
            bonificacion: parseFloat(trabajadorEnEdicion.bonificacion || 0),
            trabajometa: trabajadorEnEdicion.trabajometa || '',
          };

          const { error } = await supabase
            .from("registrodepersonal")
            .update(actualizado)
            .eq("nombrecompleto", trabajadorEnEdicion.nombrecompleto);

          if (!error) {
            alert("Datos actualizados correctamente");
            setVista("planilla");
            obtenerPersonal();
          } else {
            alert("Error al actualizar");
            console.error(error);
          }
        }}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Guardar cambios
      </button>

      <button
        onClick={() => setVista('planilla')}
        className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
      >
        Cancelar
      </button>
    </div>
  </div>
)}

  



    </div>
  );
}
