import { useState, useEffect } from 'react';
import { supabase } from '../supabase'; 
import { decode } from 'base64-arraybuffer';

export default function RegistroPersonal() {
  const [vista, setVista] = useState('formulario');
  const [tipoPersonal, setTipoPersonal] = useState('');
  const [datos, setDatos] = useState({
    nombre: '',
    salarioQuincena: '',
    bonoAsistencia: '',
    horaExtra: '',
    viaticos: '',
    fechaIngreso: '',
    dpi: '',
    telefono: '',
    fotoPapeleria: null,
  });
  const [personal, setPersonal] = useState([]);
  const [fotoAmpliada, setFotoAmpliada] = useState(null);

  const usuario = {
  nombre: 'Pedro',
  rol: 'admin' // o 'temporal', seg√∫n qui√©n est√© logueado
};
    const handleChange = (e) => {
    const { name, value } = e.target;
    setDatos((prev) => ({ ...prev, [name]: value }));
  };

  const handleFoto = (e) => {
    setDatos((prev) => ({ ...prev, fotoPapeleria: e.target.files[0] }));
  };

  const handleGuardar = async () => {
    if (!datos.nombre.trim() || !tipoPersonal) {
      alert('Por favor, completa el nombre y selecciona el tipo de personal.');
      return;
    }

    let urlPapeleria = null;

    if (datos.fotoPapeleria) {
      const archivo = datos.fotoPapeleria;
      const extension = archivo.name.split('.').pop();
      const nombreArchivo = `${Date.now()}_${archivo.name}`;
      const rutaArchivo = `${nombreArchivo}`;

      const { error: errorUpload } = await supabase.storage
        .from('papeleria')
        .upload(rutaArchivo, archivo);

      if (errorUpload) {
        console.error(errorUpload);
        alert('Error al subir la papeler√≠a.');
        return;
      }

      const { data: { publicUrl } } = supabase
        .storage
        .from('papeleria')
        .getPublicUrl(rutaArchivo);

      urlPapeleria = publicUrl;
    }

    const nuevoPersonal = {
      nombrecompleto: datos.nombre.trim(),
      modalidad: tipoPersonal,
      salariopordia: tipoPersonal === 'temporal' ? parseFloat(datos.salarioQuincena || 0) : null,
      salarioporquincena: tipoPersonal === 'fijo' ? parseFloat(datos.salarioQuincena || 0) : null,
      bonoasistencia: tipoPersonal === 'fijo' ? parseFloat(datos.bonoAsistencia || 0) : null,
      pagoporhoraextra: parseFloat(datos.horaExtra || 0),
      viaticos: tipoPersonal === 'fijo' ? parseFloat(datos.viaticos || 0) : null,
      fechadeingreso: datos.fechaIngreso || new Date().toISOString().slice(0, 10),
      dpi: datos.dpi || '',
      telefono: datos.telefono || '',
      urlpapeleria: urlPapeleria,
    };

    const { error } = await supabase.from('registrodepersonal').insert([nuevoPersonal]);

    if (!error) {
      alert('¬°Personal agregado correctamente!');
      setDatos({
        nombre: '',
        salarioQuincena: '',
        bonoAsistencia: '',
        horaExtra: '',
        viaticos: '',
        fechaIngreso: '',
        dpi: '',
        telefono: '',
        fotoPapeleria: null,
      });
      setTipoPersonal('');
    } else {
      console.error(error);
      alert('Ocurri√≥ un error al guardar el personal.');
    }
  };

  const obtenerPersonal = async () => {
    let consulta = supabase.from('registrodepersonal').select('*');

        if (usuario.rol === 'temporal') {
        consulta = consulta.eq('modalidad', 'temporal');
      }

    const { data, error } = await consulta;
    if (!error) {
      setPersonal(data);
    } else {
      console.error(error);
    }
  };

  useEffect(() => {
    if (vista === 'planilla' || vista === 'ver_personal') {
      obtenerPersonal();
    }
  }, [vista]);

  return (
    <div className="mt-6 max-w-7xl mx-auto bg-white/10 backdrop-blur-xl p-6 rounded-2xl">
      {/* Tarjetas flotantes */}
      <div className="flex flex-wrap justify-center gap-6 mb-6">
        <div
          onClick={() => setVista('formulario')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">‚ûï Agregar Personal</h3>
          <p className="text-gray-600 text-sm">Formulario para ingresar trabajadores</p>
        </div>
        <div
          onClick={() => setVista('planilla')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">üìã Ver Planilla</h3>
          <p className="text-gray-600 text-sm">Visualizar el personal registrado</p>
        </div>
        <div
          onClick={() => setVista('ver_personal')}
          className="bg-white/70 hover:bg-white/90 transition-all duration-200 rounded-2xl shadow-md p-8 w-72 cursor-pointer text-center"
        >
          <h3 className="text-xl font-bold text-gray-800 mb-2">üë§ Ver Personal</h3>
          <p className="text-gray-600 text-sm">Listado completo de trabajadores</p>
        </div>
      </div>

      {vista === 'formulario' && (
        <div className="bg-white/60 p-6 rounded-xl shadow-md max-w-3xl mx-auto">
          <h2 className="text-xl font-bold mb-2 text-center text-gray-800">üìù Registro de Personal</h2>

          {tipoPersonal && (
            <h3 className="text-center text-lg font-semibold text-blue-800 mb-4">
              {tipoPersonal === 'fijo' ? 'Formulario de Personal Fijo' : 'Formulario de Personal Temporal'}
            </h3>
          )}

          {!tipoPersonal && (
            <div className="text-center">
              <p className="text-gray-700 font-medium mb-4">¬øQu√© tipo de personal deseas registrar?</p>
              <div className="flex justify-center gap-6">
                <button
                  onClick={() => setTipoPersonal('fijo')}
                  className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
                >
                  Personal Fijo
                </button>
                <button
                  onClick={() => setTipoPersonal('temporal')}
                  className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
                >
                  Personal Temporal
                </button>
              </div>
            </div>
          )}

         {tipoPersonal && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
              <input
                type="text"
                placeholder="Nombre completo"
                name="nombre"
                value={datos.nombre}
                onChange={handleChange}
                className="border p-2 rounded"
              />

              <input
                type="number"
                placeholder={tipoPersonal === 'fijo' ? 'Salario por quincena' : 'Salario por d√≠a'}
                name="salarioQuincena"
                value={datos.salarioQuincena}
                onChange={handleChange}
                className="border p-2 rounded"
              />

              {tipoPersonal === 'fijo' && (
                <>
                  <input
                    type="number"
                    placeholder="Bono asistencia por d√≠a"
                    name="bonoAsistencia"
                    value={datos.bonoAsistencia}
                    onChange={handleChange}
                    className="border p-2 rounded"
                  />
                  <input
                    type="number"
                    placeholder="Vi√°ticos"
                    name="viaticos"
                    value={datos.viaticos}
                    onChange={handleChange}
                    className="border p-2 rounded"
                  />
                </>
              )}

              <input
                type="number"
                placeholder="Pago por hora extra"
                name="horaExtra"
                value={datos.horaExtra}
                onChange={handleChange}
                className="border p-2 rounded"
              />

              <input
                type="date"
                name="fechaIngreso"
                value={datos.fechaIngreso}
                onChange={handleChange}
                className="border p-2 rounded"
              />
              <input
                type="text"
                placeholder="No. DPI"
                name="dpi"
                value={datos.dpi}
                onChange={handleChange}
                className="border p-2 rounded"
              />
              <input
                type="text"
                placeholder="Tel√©fono"
                name="telefono"
                value={datos.telefono}
                onChange={handleChange}
                className="border p-2 rounded"
              />
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Papeler√≠a</label>
                <input
                  type="file"
                  onChange={handleFoto}
                  className="border p-2 rounded w-full"
                />
              </div>
            </div>
          )}
          {tipoPersonal && (
            <div className="flex justify-between mt-6">
              <button
                onClick={() => setTipoPersonal('')}
                className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 transition"
              >
                Volver
              </button>
              <button
                onClick={handleGuardar}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
              >
                Guardar personal
              </button>
            </div>
          )}
        </div>
      )}

      {vista === 'planilla' && (
          <div className="overflow-x-auto bg-white/70 backdrop-blur-md p-6 rounded-xl shadow-xl">
            <h3 className="text-xl font-bold mb-4 text-gray-800">üìã Planilla de Personal</h3>
            <table className="min-w-full border border-gray-300 text-sm text-center">
              <thead className="bg-gray-200 text-gray-700">
                <tr>
                  <th className="px-4 py-2 border">Nombre</th>
                  <th className="px-4 py-2 border">Modalidad</th>
                  <th className="px-4 py-2 border">Salario/Quincena o por d√≠a</th>
                  <th className="px-4 py-2 border">Bono Asistencia</th>
                  <th className="px-4 py-2 border">Pago por hora extra</th>
                  <th className="px-4 py-2 border">Vi√°ticos</th>
                  <th className="px-4 py-2 border font-semibold text-green-800">Total</th>
                </tr>
              </thead>
              <tbody>
                {personal.map((p) => {
                  const salario = p.modalidad === 'fijo' ? p.salarioporquincena || 0 : p.salariopordia || 0;
                  const bono = p.bonoasistencia || 0;
                  const horaExtra = p.pagoporhoraextra || 0;
                  const viaticos = p.viaticos || 0;
                  const total = salario + bono + horaExtra + viaticos;

                  return (
                    <tr key={p.id} className="border-b">
                      <td className="px-4 py-2 border">{p.nombrecompleto}</td>
                      <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
                      <td className="px-4 py-2 border">Q{salario}</td>
                      <td className="px-4 py-2 border">Q{bono}</td>
                      <td className="px-4 py-2 border">Q{horaExtra}</td>
                      <td className="px-4 py-2 border">Q{viaticos}</td>
                      <td className="px-4 py-2 border font-bold text-green-700">Q{total}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}

      {vista === 'ver_personal' && (
  <div className="overflow-x-auto bg-white/70 backdrop-blur-md p-6 rounded-xl shadow-xl">
    <h3 className="text-xl font-bold mb-4 text-gray-800">üë§ Todos los Trabajadores</h3>
    <table className="min-w-full border border-gray-300 text-sm text-center">
      <thead className="bg-gray-200 text-gray-700">
        <tr>
          <th className="px-4 py-2 border">Nombre</th>
          <th className="px-4 py-2 border">Modalidad</th>
          <th className="px-4 py-2 border">Salario</th>
          <th className="px-4 py-2 border">Bono Asistencia</th>
          <th className="px-4 py-2 border">Pago por hora extra</th>
          <th className="px-4 py-2 border">Vi√°ticos</th>
          <th className="px-4 py-2 border">Papeles</th>
          <th className="px-4 py-2 border">Detalles</th>
        </tr>
      </thead>
      <tbody>
        {personal.map((p, idx) => (
          <React.Fragment key={p.id}>
            <tr className="border-b">
              <td className="px-4 py-2 border">{p.nombrecompleto}</td>
              <td className="px-4 py-2 border capitalize">{p.modalidad}</td>
              <td className="px-4 py-2 border">
                {p.modalidad === 'fijo' ? `Q${p.salarioporquincena || 0}` : `Q${p.salariopordia || 0}`}
              </td>
              <td className="px-4 py-2 border">{p.bonoasistencia || '-'}</td>
              <td className="px-4 py-2 border">{p.pagoporhoraextra || '-'}</td>
              <td className="px-4 py-2 border">{p.viaticos || '-'}</td>
              <td className="px-4 py-2 border">
                {p.urlpapeleria ? (
                  <button
                    onClick={() => window.open(p.urlpapeleria, '_blank')}
                    className="bg-blue-100 hover:bg-blue-200 text-blue-800 font-medium px-2 py-1 rounded-full text-xs transition"
                  >
                    Ver Papeles
                  </button>
                ) : (
                  '-'
                )}
              </td>
              <td className="px-4 py-2 border">
                <button
                  onClick={() =>
                    setPersonal((prev) =>
                      prev.map((item, i) =>
                        i === idx ? { ...item, mostrarDetalles: !item.mostrarDetalles } : item
                      )
                    )
                  }
                  className="text-blue-600 hover:text-blue-800 font-medium"
                >
                  {p.mostrarDetalles ? 'Ocultar' : 'Ver'}
                </button>
              </td>
            </tr>
            {p.mostrarDetalles && (
              <tr className="border-b bg-gray-50">
                <td colSpan="8" className="px-4 py-2 text-left">
                  <p><strong>üìå Nombre completo:</strong> {p.nombrecompleto}</p>
                  <p><strong>üìå Modalidad:</strong> {p.modalidad}</p>
                  <p><strong>üìå Salario por quincena o d√≠a:</strong> Q{p.salarioporquincena || p.salariopordia || 0}</p>
                  <p><strong>üìå Bono de asistencia:</strong> Q{p.bonoasistencia || 0}</p>
                  <p><strong>üìå Pago por hora extra:</strong> Q{p.pagoporhoraextra || 0}</p>
                  <p><strong>üìå Vi√°ticos:</strong> Q{p.viaticos || 0}</p>
                  <p><strong>üìå DPI:</strong> {p.dpi}</p>
                  <p><strong>üìå Tel√©fono:</strong> {p.telefono}</p>
                  <p><strong>üìå Fecha de ingreso:</strong> {p.fechadeingreso}</p>
                </td>
              </tr>
            )}
          </React.Fragment>
        ))}
      </tbody>
    </table>
  </div>
)}

    </div>
  );
}
