import { useState, useEffect } from 'react';
import { supabase } from '../supabase';

export default function Personal() {
  const [vista, setVista] = useState('planilla');
  const [personal, setPersonal] = useState([]);
  const [historialVisible, setHistorialVisible] = useState(null);
  const [nuevoPersonal, setNuevoPersonal] = useState({
    nombre: '',
    puesto: '',
    proyecto: '',
    salario: '',
    meta: '',
    fecha: '',
    modalidad: 'día',
    realizado: '',
  });

  const obtenerPersonal = async () => {
    const { data, error } = await supabase
      .from('registrodepersonal')
      .select('*')
      .order('created_at', { ascending: false });

    if (error) {
      console.error('Error al obtener personal:', error);
      return;
    }

    setPersonal(data);
  };

  useEffect(() => {
    obtenerPersonal();
  }, []);

  const handleAgregar = async () => {
    if (!nuevoPersonal.nombre || !nuevoPersonal.puesto || !nuevoPersonal.salario) {
      alert('El nombre, puesto y salario son obligatorios');
      return;
    }

    const { data: existente } = await supabase
      .from('registrodepersonal')
      .select('*')
      .eq('nombrecompleto', nuevoPersonal.nombre)
      .single();

    // Calcular bonificación si aplica
    let bonificacion = 0;
    const meta = parseInt(nuevoPersonal.meta);
    const realizado = parseInt(nuevoPersonal.realizado);

    if (nuevoPersonal.modalidad === 'cantidad' && realizado >= meta && meta > 0) {
      bonificacion = 150; // Valor fijo, podrías cambiarlo según condiciones
    }

    if (existente) {
      const { error: errorUpdate } = await supabase
        .from('registrodepersonal')
        .update({
          proyecto: nuevoPersonal.proyecto,
          metaestablecida: meta,
          modalidad: nuevoPersonal.modalidad,
          bonificacion: bonificacion,
          fechadeingreso: nuevoPersonal.fecha || existente.fechadeingreso
        })
        .eq('id', existente.id);

      if (errorUpdate) {
        alert('Error al actualizar el personal');
        return;
      }
    } else {
      const { error: errorInsert } = await supabase
        .from('registrodepersonal')
        .insert([{
          nombrecompleto: nuevoPersonal.nombre,
          puesto: nuevoPersonal.puesto,
          proyecto: nuevoPersonal.proyecto,
          salariopordia: parseInt(nuevoPersonal.salario),
          metaestablecida: meta,
          modalidad: nuevoPersonal.modalidad,
          bonificacion: bonificacion,
          fechadeingreso: nuevoPersonal.fecha || new Date().toISOString()
        }]);

      if (errorInsert) {
        alert('Error al agregar nuevo personal');
        return;
      }
    }

    setNuevoPersonal({
      nombre: '',
      puesto: '',
      proyecto: '',
      salario: '',
      meta: '',
      fecha: '',
      modalidad: 'día',
      realizado: ''
    });

    setVista('planilla');
    obtenerPersonal();
  };

  return (
    <div className="mt-6 max-w-6xl mx-auto bg-white p-6 rounded-xl shadow">
      <h2 className="text-2xl font-bold mb-6 text-center">👷 Gestión de Personal</h2>

      <div className="flex justify-center gap-4 mb-6">
        <button
          onClick={() => setVista('formulario')}
          className={`px-4 py-2 rounded-md ${vista === 'formulario' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
        >
          Agregar personal
        </button>
        <button
          onClick={() => setVista('planilla')}
          className={`px-4 py-2 rounded-md ${vista === 'planilla' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
        >
          Planilla completa
        </button>
      </div>

      {vista === 'formulario' && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Nombre completo"
            value={nuevoPersonal.nombre}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, nombre: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          />
          <input
            type="text"
            placeholder="Puesto"
            value={nuevoPersonal.puesto}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, puesto: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          />
          <input
            type="text"
            placeholder="Proyecto"
            value={nuevoPersonal.proyecto}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, proyecto: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          />
          <input
            type="number"
            placeholder="Salario por día"
            value={nuevoPersonal.salario}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, salario: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          />

          <select
            value={nuevoPersonal.modalidad}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, modalidad: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          >
            <option value="día">Modalidad: Día</option>
            <option value="cantidad">Modalidad: Cantidad</option>
          </select>

          <input
            type="number"
            placeholder="Meta establecida"
            value={nuevoPersonal.meta}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, meta: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          />

          {nuevoPersonal.modalidad === 'cantidad' && (
            <input
              type="number"
              placeholder="Cantidad realizada"
              value={nuevoPersonal.realizado}
              onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, realizado: e.target.value })}
              className="w-full px-4 py-2 border rounded-md"
            />
          )}

          <input
            type="date"
            placeholder="Fecha de ingreso"
            value={nuevoPersonal.fecha}
            onChange={(e) => setNuevoPersonal({ ...nuevoPersonal, fecha: e.target.value })}
            className="w-full px-4 py-2 border rounded-md"
          />

          <button
            onClick={handleAgregar}
            className="bg-green-600 text-white py-2 rounded-md hover:bg-green-700 transition col-span-1 md:col-span-2"
          >
            Guardar Personal
          </button>
        </div>
      )}

      {vista === 'planilla' && (
        <>
          {personal.length > 0 ? (
            <table className="w-full text-left border text-sm md:text-base">
              <thead>
                <tr className="bg-gray-200">
                  <th className="py-2 px-4 border">Nombre</th>
                  <th className="py-2 px-4 border">Puesto</th>
                  <th className="py-2 px-4 border">Proyecto</th>
                  <th className="py-2 px-4 border">Salario</th>
                  <th className="py-2 px-4 border">Modalidad</th>
                  <th className="py-2 px-4 border">Meta</th>
                  <th className="py-2 px-4 border">Bonificación</th>
                  <th className="py-2 px-4 border">Ingreso</th>
                  <th className="py-2 px-4 border">Acción</th>
                </tr>
              </thead>
              <tbody>
                {personal.map((p, i) => (
                  <tr key={i} className="hover:bg-gray-100">
                    <td className="py-2 px-4 border">{p.nombrecompleto}</td>
                    <td className="py-2 px-4 border">{p.puesto}</td>
                    <td className="py-2 px-4 border">{p.proyecto}</td>
                    <td className="py-2 px-4 border">Q{p.salariopordia}</td>
                    <td className="py-2 px-4 border capitalize">{p.modalidad}</td>
                    <td className="py-2 px-4 border">{p.metaestablecida || '-'}</td>
                    <td className="py-2 px-4 border">{p.bonificacion ? `Q${p.bonificacion}` : '-'}</td>
                    <td className="py-2 px-4 border">{p.fechadeingreso ? new Date(p.fechadeingreso).toLocaleDateString() : '-'}</td>
                    <td className="py-2 px-4 border text-center">
                      <button
                        className="text-blue-600 underline"
                        onClick={() => setHistorialVisible(i)}
                      >
                        📄 Ver historial
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p className="text-center text-gray-500 mt-6">No hay personal registrado aún.</p>
          )}

          {historialVisible !== null && (
            <div className="mt-6 p-4 bg-gray-100 rounded-md">
              <h3 className="text-lg font-semibold mb-2">
                📘 Historial de {personal[historialVisible].nombrecompleto}
              </h3>
              <p><strong>Proyecto actual:</strong> {personal[historialVisible].proyecto}</p>
              <p><strong>Modalidad:</strong> {personal[historialVisible].modalidad}</p>
              <p><strong>Meta:</strong> {personal[historialVisible].metaestablecida}</p>
              <p><strong>Bonificación:</strong> {personal[historialVisible].bonificacion ? `Q${personal[historialVisible].bonificacion}` : '—'}</p>
              <p><strong>Fecha de ingreso:</strong> {new Date(personal[historialVisible].fechadeingreso).toLocaleDateString()}</p>
              <button
                onClick={() => setHistorialVisible(null)}
                className="mt-4 text-red-600 underline"
              >
                Cerrar historial
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}
