import { useState, useEffect } from "react";
import { FilePlus, PlusCircle, ArrowLeft } from "lucide-react";
import { supabase } from "../../supabase";
import BalanceGeneral from "./BalanceGeneral";
import TotalIngresos from "./TotalIngresos";
import TotalEgresos from "./TotalEgresos";
import CalculosFinancieros from "./CalculosFinancieros";
import PrecioTrabajos from "./PrecioTrabajos";

export default function Contabilidad() {
  const [vista, setVista] = useState("general");
  const [formularioActivo, setFormularioActivo] = useState(null);
  const [tipoGastoNuevo, setTipoGastoNuevo] = useState("");
  const [medioAmpliacion, setMedioAmpliacion] = useState("");
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState(null);
  const [descripcion, setDescripcion] = useState("");
  const [fecha, setFecha] = useState("");
  const [monto, setMonto] = useState("");
  const [documento, setDocumento] = useState("");
  const [categoriasUsadas, setCategoriasUsadas] = useState([
    "Combustible",
    "Transporte",
    "Personal",
  ]);
  const [gastos, setGastos] = useState([]);
  const [ampliaciones, setAmpliaciones] = useState([]);
  const [proyectos, setProyectos] = useState([]);
  const [vistaDetalle, setVistaDetalle] = useState(null);
  const [mostrarBalance, setMostrarBalance] = useState(false);

  const [totales, setTotales] = useState({
    ingresos: 0,
    egresos: 0,
    cajaChica: 0,
    balance: 0,
  });

  const fetchProyectos = async () => {
    const { data, error } = await supabase.from("proyectos").select("*");
    if (!error) {
      setProyectos(data.map((p) => ({ ...p, detalleVisible: false })));
    }
  };

  const fetchMovimientos = async () => {
    const { data: gastosData } = await supabase.from("gastos_proyectos").select("*");
    const { data: ampliacionesData } = await supabase.from("ampliaciones_proyectos").select("*");
    setGastos(gastosData || []);
    setAmpliaciones(ampliacionesData || []);
  };

  useEffect(() => {
    fetchProyectos();
    fetchMovimientos();
  }, []);

  useEffect(() => {
    const activosIds = proyectos.filter((p) => p.estado !== "finalizado").map((p) => p.id);
    const ampliacionesActivas = ampliaciones.filter((a) => activosIds.includes(a.proyecto_id));
    const gastosActivos = gastos.filter((g) => activosIds.includes(g.proyecto_id));

    const ingresos = ampliacionesActivas.reduce((sum, a) => sum + (a.monto || 0), 0);
    const egresos = gastosActivos.reduce((sum, g) => sum + (g.monto || 0), 0);

    setTotales({
      ingresos,
      egresos,
      cajaChica: 0,
      balance: ingresos - egresos,
    });
  }, [gastos, ampliaciones, proyectos]);

  const guardarGasto = async () => {
    if (!proyectoSeleccionado || !tipoGastoNuevo || !descripcion || !fecha || !monto) return;
    const { error } = await supabase.from("gastos_proyectos").insert([
      {
        proyecto_id: proyectoSeleccionado,
        categoria: tipoGastoNuevo,
        descripcion,
        fecha,
        monto: parseFloat(monto),
      },
    ]);
    if (!error) {
      fetchProyectos();
      if (!categoriasUsadas.includes(tipoGastoNuevo)) {
        setCategoriasUsadas([...categoriasUsadas, tipoGastoNuevo]);
      }
      setFormularioActivo(null);
      setProyectoSeleccionado(null);
      setTipoGastoNuevo("");
      setDescripcion("");
      setFecha("");
      setMonto("");
    } else {
      alert("Error al guardar gasto");
    }
  };

  const guardarAmpliacion = async () => {
    if (!proyectoSeleccionado || !medioAmpliacion || !documento || !fecha || !monto) return;
    const { error } = await supabase.from("ampliaciones_proyectos").insert([
      {
        proyecto_id: proyectoSeleccionado,
        medio: medioAmpliacion,
        documento,
        fecha,
        monto: parseFloat(monto),
      },
    ]);
    if (!error) {
      fetchProyectos();
      setFormularioActivo(null);
      setProyectoSeleccionado(null);
      setMedioAmpliacion("");
      setDocumento("");
      setFecha("");
      setMonto("");
    } else {
      alert("Error al guardar la ampliaciÃ³n");
    }
  };

  return (
    <div className="p-6 space-y-6">
      {vista === "general" && (
        <>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center">
            <div onClick={() => setVistaDetalle("ingresos")} className="bg-green-100 p-4 rounded-2xl shadow cursor-pointer">
              <p className="text-sm text-gray-600">Total ingresos</p>
              <h2 className="text-xl font-bold text-green-700">Q{totales.ingresos.toFixed(2)}</h2>
            </div>
            <div onClick={() => setVistaDetalle("egresos")} className="bg-red-100 p-4 rounded-2xl shadow cursor-pointer">
              <p className="text-sm text-gray-600">Total egresos</p>
              <h2 className="text-xl font-bold text-red-700">Q{totales.egresos.toFixed(2)}</h2>
            </div>
            <div onClick={() => setMostrarBalance(true)} className="bg-blue-100 p-4 rounded-2xl shadow cursor-pointer">
              <p className="text-sm text-gray-600">Balance general</p>
              <h2 className="text-xl font-bold text-blue-700">Q{totales.balance.toFixed(2)}</h2>
            </div>
          </div>

          {mostrarBalance && (
            <div className="mt-6">
              <BalanceGeneral ampliaciones={ampliaciones} gastos={gastos} />
              <button onClick={() => setMostrarBalance(false)} className="mt-4 bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 rounded-xl shadow">Cerrar Balance</button>
            </div>
          )}

          {vistaDetalle === "ingresos" && <TotalIngresos ampliaciones={ampliaciones} onCerrar={() => setVistaDetalle(null)} />}
          {vistaDetalle === "egresos" && <TotalEgresos gastos={gastos} onCerrar={() => setVistaDetalle(null)} />}

          <div className="flex justify-center gap-4 mt-8">
            <div onClick={() => setVista("proyectos")} className="cursor-pointer bg-white shadow-lg rounded-2xl p-6 text-center max-w-xs hover:shadow-xl">
              <p className="text-xl font-bold text-purple-700">ð¦ Proyectos en curso</p>
              <p className="text-sm text-gray-500 mt-2">Haz clic para ver el resumen por proyecto</p>
            </div>
            <div onClick={() => setVista("preciosTrabajos")} className="cursor-pointer bg-white shadow-lg rounded-2xl p-6 text-center max-w-xs hover:shadow-xl">
              <p className="text-xl font-bold text-green-700">ð° Precios de Trabajos</p>
              <p className="text-sm text-gray-500 mt-2">Consulta los precios de cada trabajo aquÃ­ mismo.</p>
            </div>
          </div>

          {vista === "preciosTrabajos" && (
            <div className="mt-8">
              <PrecioTrabajos />
            </div>
          )}
        </>
      )}
      {formularioActivo === "gasto" && (
      <div className="mt-4 bg-white rounded-xl shadow-lg p-6 max-w-3xl mx-auto">
        <h3 className="text-xl font-bold text-gray-800 mb-4">
          ð§¾ Ingresar Gasto
        </h3>
        <div className="grid md:grid-cols-2 gap-4">
          <select
            className="p-2 border rounded"
            value={proyectoSeleccionado || ""}
            onChange={(e) => setProyectoSeleccionado(e.target.value)}
          >
            <option value="">Selecciona un proyecto</option>
            {proyectos.map((p) => (
              <option key={p.id} value={p.id}>
                {p.nombre}
              </option>
            ))}
          </select>
          <input
            list="categorias"
            placeholder="Tipo de gasto (ej: Combustible)"
            className="p-2 border rounded"
            value={tipoGastoNuevo}
            onChange={(e) => setTipoGastoNuevo(e.target.value)}
          />
          <datalist id="categorias">
            {categoriasUsadas.map((c, i) => (
              <option key={i} value={c} />
            ))}
          </datalist>
          <input
            type="text"
            placeholder="DescripciÃ³n"
            className="p-2 border rounded"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
          <input
            type="date"
            className="p-2 border rounded"
            value={fecha}
            onChange={(e) => setFecha(e.target.value)}
          />
          <input
            type="number"
            placeholder="Monto"
            className="p-2 border rounded"
            value={monto}
            onChange={(e) => setMonto(e.target.value)}
          />
          <input type="file" className="p-2 border rounded col-span-2" />
          <button
            onClick={guardarGasto}
            className="col-span-2 mt-4 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded-xl shadow"
          >
            Guardar Gasto
          </button>
          <button
            onClick={() => setFormularioActivo(null)}
            className="col-span-2 mt-2 bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 rounded-xl shadow"
          >
            Cancelar
          </button>
        </div>
      </div>
    )}
  

<div className="bg-white mt-10 p-6 rounded-xl shadow">
  <h3 className="text-xl font-bold text-gray-800 mb-4">
    ð¹ Resumen de Ampliaciones
  </h3>
  <div className="overflow-x-auto">
    <table className="min-w-full text-sm border border-gray-200 rounded-xl">
      <thead className="bg-gray-100 text-gray-700">
        <tr>
          <th className="py-2 px-4 text-left">Proyecto</th>
