import React, { useState, useEffect } from "react";

export default function ModificarTrabajos({ persona, onCerrar }) {
  const [modoEdicion, setModoEdicion] = useState("actual"); // "actual" o "anterior"
  const [fechaAnterior, setFechaAnterior] = useState(""); // para seleccionar fecha anterior
  const [datosFormulario, setDatosFormulario] = useState({
    salarioBase: persona.salarioporquincena || "",
    horasExtras: "",
    unidadesEstablecidas: "",
    unidadesHechas: "",
    bonificacionExtra: "",
    pagoDomingo: "",
    grupo: "",
  });

  // Maneja cambios en los inputs del formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDatosFormulario((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  useEffect(() => {
    if (modoEdicion === "actual") {
      // Cargar datos actuales de la persona
      setDatosFormulario({
        salarioBase: persona.modalidad === "fijo" ? persona.salarioporquincena || "" : "",
        horasExtras: persona.horas_extras || "",
        unidadesEstablecidas: persona.metaestablecida || "",
        unidadesHechas: persona.cantidad || "",
        bonificacionExtra: persona.extra || "",
        pagoDomingo: persona.pagoextraordinario || "",
        grupo: persona.numerogrupo || "",
      });
    } else if (modoEdicion === "anterior" && fechaAnterior) {
      // TODO: cargar datos de la fecha anterior seleccionada
      // Ejemplo:
      // fetchDatosFechaAnterior(fechaAnterior).then(datos => {
      //   setDatosFormulario(datos);
      // });
    } else {
      // Si no hay datos disponibles
      setDatosFormulario({
        salarioBase: "",
        horasExtras: "",
        unidadesEstablecidas: "",
        unidadesHechas: "",
        bonificacionExtra: "",
        pagoDomingo: "",
        grupo: "",
      });
    }
  }, [modoEdicion, fechaAnterior, persona]);

  return (
    <div className="p-4 rounded-xl shadow bg-white">
      <h3 className="font-semibold text-lg mb-4">⚙️ Modificar Trabajos</h3>

      {/* Botones modo de edición */}
      <div className="mb-4 flex gap-4">
        <button
          onClick={() => {
            setModoEdicion("actual");
            setDatosFormulario({
              salarioBase: persona.modalidad === "fijo" ? persona.salarioporquincena || "" : "",
              horasExtras: persona.horas_extras || "",
              unidadesEstablecidas: persona.metaestablecida || "",
              unidadesHechas: persona.cantidad || "",
              bonificacionExtra: persona.extra || "",
              pagoDomingo: persona.pagoextraordinario || "",
              grupo: persona.numerogrupo || "",
            });
          }}
          className={`px-4 py-2 rounded ${
            modoEdicion === "actual" ? "bg-green-600 text-white" : "bg-gray-300 text-black"
          }`}
        >
          Modificar planilla actual
        </button>
        <button
          onClick={() => setModoEdicion("anterior")}
          className={`px-4 py-2 rounded ${
            modoEdicion === "anterior" ? "bg-green-600 text-white" : "bg-gray-300 text-black"
          }`}
        >
          Modificar fecha anterior
        </button>
      </div>

      {modoEdicion === "anterior" && (
        <div className="mb-4">
          <label className="block font-medium mb-1">Seleccione fecha</label>
          <input
            type="date"
            value={fechaAnterior}
            onChange={(e) => setFechaAnterior(e.target.value)}
            className="border p-2 rounded w-full"
          />
        </div>
      )}

      {/* Salario base solo para modalidad fijo */}
      {persona.modalidad === "fijo" && (
        <div className="mb-4">
          <label className="block font-medium mb-1">Salario base (Q)</label>
          <input
            type="number"
            name="salarioBase"
            value={datosFormulario.salarioBase}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        <div>
          <label className="block font-medium mb-1">Horas extras</label>
          <input
            type="number"
            name="horasExtras"
            value={datosFormulario.horasExtras}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Unidades establecidas</label>
          <input
            type="number"
            name="unidadesEstablecidas"
            value={datosFormulario.unidadesEstablecidas}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Unidades hechas</label>
          <input
            type="number"
            name="unidadesHechas"
            value={datosFormulario.unidadesHechas}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Bonificación por unidades extras (Q)</label>
          <input
            type="number"
            name="bonificacionExtra"
            value={datosFormulario.bonificacionExtra}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Pago extraordinario (domingo)</label>
          <input
            type="number"
            name="pagoDomingo"
            value={datosFormulario.pagoDomingo}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>

        <div>
          <label className="block font-medium mb-1">Número de grupo</label>
          <input
            type="number"
            name="grupo"
            value={datosFormulario.grupo}
            onChange={handleInputChange}
            className="border p-2 rounded w-full"
          />
        </div>
      </div>

      <div className="flex gap-4 justify-center">
        <button
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:bg-green-700"
          onClick={() => {
            // Aquí va la lógica para guardar los cambios
            console.log("Guardar datos", datosFormulario);
          }}
        >
          Guardar
        </button>
        <button
          className="bg-red-600 text-white px-4 py-2 rounded shadow hover:bg-red-700"
          onClick={onCerrar}
        >
          Cerrar
        </button>
      </div>
    </div>
  );
}
