// VistaMovimiento.jsx
import { useEffect, useState } from "react";
import { supabase } from "../supabase";

export default function VistaMovimientos() {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const obtenerLogs = async () => {
      const { data, error } = await supabase
        .from("logs")
        .select("*")
        .order("fecha", { ascending: false });

      if (error) {
        console.error(error);
      } else {
        setLogs(data);
      }
    };

    obtenerLogs();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">ðŸ“‹ Vista de Movimiento</h1>
      <div className="overflow-x-auto">
        <table className="min-w-full text-sm text-left">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-2">Usuario</th>
              <th className="p-2">AcciÃ³n</th>
              <th className="p-2">DescripciÃ³n</th>
              <th className="p-2">Fecha</th>
            </tr>
          </thead>
          <tbody>
            {logs.map((log) => (
              <tr key={log.id} className="border-b">
                <td className="p-2">{log.usuario}</td>
                <td className="p-2">{log.accion}</td>
                <td className="p-2">{log.descripcion || "-"}</td>
                <td className="p-2">{new Date(log.fecha).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
