import { useState, useEffect } from "react";
import { supabase } from "../supabase";
import { Pencil, Trash2, Save, X, Plus } from "lucide-react";

export default function Proyectos() {
  const [proyectos, setProyectos] = useState([]);
  const [supervisoresPorProyecto, setSupervisoresPorProyecto] = useState({});
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [nuevoProyecto, setNuevoProyecto] = useState({ nombre: "", descripcion: "" });
  const [editando, setEditando] = useState(null);
  const [editado, setEditado] = useState({ nombre: "", descripcion: "" });

  const obtenerProyectos = async () => {
    const { data, error } = await supabase.from("proyectos").select("*").order("id");
    if (!error) {
      setProyectos(data);
      obtenerSupervisores(data);
    }
  };

  const obtenerSupervisores = async (proyectos) => {
    const { data, error } = await supabase
      .from("proyectos_personal")
      .select("proyecto_id, rol, trabajador_id, trabajadores (nombrecompleto)")
      .eq("rol", "supervisor");

    if (!error) {
      const mapa = {};
      data.forEach((entry) => {
        mapa[entry.proyecto_id] = entry.trabajadores?.nombrecompleto || "Sin supervisor";
      });
      setSupervisoresPorProyecto(mapa);
    } else {
      console.error("Error obteniendo supervisores", error);
    }
  };

  const agregarProyecto = async () => {
    if (!nuevoProyecto.nombre.trim()) return alert("El nombre es obligatorio.");
    const { error } = await supabase.from("proyectos").insert([nuevoProyecto]);
    if (!error) {
      setNuevoProyecto({ nombre: "", descripcion: "" });
      setMostrarFormulario(false);
      obtenerProyectos();
    } else {
      alert("Error al guardar el proyecto");
    }
  };

  const eliminarProyecto = async (id) => {
    if (!confirm("Â¿Eliminar este proyecto?")) return;
    const { error } = await supabase.from("proyectos").delete().eq("id", id);
    if (!error) obtenerProyectos();
  };

  const guardarEdicion = async (id) => {
    const { error } = await supabase.from("proyectos").update(editado).eq("id", id);
    if (!error) {
      setEditando(null);
      obtenerProyectos();
    } else alert("Error al guardar");
  };

  useEffect(() => {
    obtenerProyectos();
  }, []);

  return (
    <div className="p-6 bg-white/10 backdrop-blur-xl min-h-screen rounded-2xl">
     
     
     {/* Carta tipo botÃ³n para crear un nuevo proyecto */}
<div className="flex justify-center mb-6">
  <div
    onClick={() => setMostrarFormulario(!mostrarFormulario)}
    className="bg-white/30 backdrop-blur-md p-6 rounded-xl shadow-lg border border-white/20 hover:shadow-xl transition cursor-pointer w-full max-w-md text-center"
  >
    <h3 className="text-lg font-bold text-gray-800 mb-2">â Nuevo proyecto</h3>
    <p className="text-sm text-gray-600">Crea un nuevo proyecto y asÃ­gnale un supervisor.</p>
  </div>
</div>

      {mostrarFormulario && (
  <div className="bg-white/30 backdrop-blur-lg p-6 rounded-2xl shadow-xl border border-white/20 max-w-3xl mx-auto mb-10 space-y-4">
    
    <h2 className="text-xl font-bold text-purple-900">ð  Crear nuevo proyecto</h2>

    {/* Nombre */}
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">Nombre del proyecto</label>
      <input
        type="text"
        placeholder="Ej: Proyecto Las Pilas"
        value={nuevoProyecto.nombre}
        onChange={(e) => setNuevoProyecto({ ...nuevoProyecto, nombre: e.target.value })}
        className="w-full p-3 border rounded-xl"
      />
    </div>

    {/* DescripciÃ³n */}
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">DescripciÃ³n</label>
      <textarea
        placeholder="Ej: InstalaciÃ³n de sistema solar..."
        value={nuevoProyecto.descripcion}
        onChange={(e) => setNuevoProyecto({ ...nuevoProyecto, descripcion: e.target.value })}
        className="w-full p-3 border rounded-xl"
      />
    </div>

    {/* Supervisores */}
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">Supervisores</label>
      <select
        multiple
        value={supervisoresSeleccionados}
        onChange={(e) =>
          setSupervisoresSeleccionados(Array.from(e.target.selectedOptions, (opt) => opt.value))
        }
        className="w-full p-3 border rounded-xl"
      >
        {personalDisponible.map((p) => (
          <option key={p.id} value={p.id}>
            {p.nombrecompleto}
          </option>
        ))}
      </select>
      <p className="text-xs text-gray-500 mt-1">Puedes seleccionar uno o varios supervisores.</p>
    </div>

    {/* Trabajadores */}
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">Trabajadores asignados</label>
      <select
        multiple
        value={trabajadoresSeleccionados}
        onChange={(e) =>
          setTrabajadoresSeleccionados(Array.from(e.target.selectedOptions, (opt) => opt.value))
        }
        className="w-full p-3 border rounded-xl"
      >
        {personalDisponible.map((p) => (
          <option key={p.id} value={p.id}>
            {p.nombrecompleto}
          </option>
        ))}
      </select>
      <p className="text-xs text-gray-500 mt-1">Puedes asignar varios trabajadores.</p>
    </div>

    {/* Trabajos asignados */}
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-2">Trabajos por realizar</label>
      {trabajos.map((trabajo, index) => (
        <div key={index} className="flex flex-col md:flex-row gap-2 mb-2">
          <input
            type="text"
            placeholder="Nombre del trabajo (ej: Instalar paneles)"
