import { useState, useEffect } from "react";
import { supabase } from "../../supabase";

export default function PrecioTrabajos() {
  const [proyectos, setProyectos] = useState([]);
  const [trabajosPorProyecto, setTrabajosPorProyecto] = useState({});

  useEffect(() => {
    const cargarProyectosYTrabajos = async () => {
      // Obtener proyectos
      const { data: proyectosData } = await supabase.from("proyectos").select("*");
      setProyectos(proyectosData);

      // Para cada proyecto, obtener sus trabajos y sumar instalados
      const trabajosData = {};
      for (const proyecto of proyectosData) {
        const { data: trabajos } = await supabase
          .from("trabajos_proyecto")
          .select("*")
          .eq("proyecto_id", proyecto.id);

        const trabajosConInstaladas = await Promise.all(
          trabajos.map(async (trabajo) => {
            const { data: reportes } = await supabase
              .from("reportes_diarios")
              .select("cantidad_instalada")
              .eq("proyecto_id", proyecto.id)
              .eq("nombre_trabajo", trabajo.nombre_trabajo);

            const unidadesInstaladas = reportes
              ? reportes.reduce((sum, r) => sum + (r.cantidad_instalada || 0), 0)
              : 0;

            return {
              ...trabajo,
              unidades_instaladas: unidadesInstaladas,
              total_a_instalar: trabajo.unidades_a_instalar * trabajo.precio_unitario,
              total_instalado: unidadesInstaladas * trabajo.precio_unitario,
            };
          })
        );

        trabajosData[proyecto.id] = trabajosConInstaladas;
      }
      setTrabajosPorProyecto(trabajosData);
    };

    cargarProyectosYTrabajos();
  }, []);

return (
  <div className="p-4">
    <h2 className="text-xl font-bold text-purple-600 mb-4">ðŸ’° Precios por Proyecto</h2>

    {proyectos && proyectos.length > 0 ? (
      proyectos.map((proyecto) => (
        <div key={proyecto.id} className="bg-white rounded-lg shadow p-4 mb-4">
          <h3 className="text-lg font-bold text-center text-purple-700">{proyecto.nombre}</h3>

          {trabajosPorProyecto[proyecto.id]?.length ? (
            trabajosPorProyecto[proyecto.id].map((trabajo) => (
              <div key={trabajo.id} className="mt-2 border-b py-1">
                <p className="font-bold text-gray-700">{trabajo.nombre_trabajo}</p>
                <p>ðŸ”¹ Unidades a Instalar: {trabajo.unidades_a_instalar}</p>
                <p>ðŸ”¹ Precio por Unidad: Q{trabajo.precio_unitario.toFixed(2)}</p>
                <p>ðŸ”¹ Total a Instalar: Q{trabajo.total_a_instalar.toFixed(2)}</p>
                <p>ðŸ”¹ Unidades Instaladas: {trabajo.unidades_instaladas}</p>
                <p>ðŸ”¹ Total Instalado: Q{trabajo.total_instalado.toFixed(2)}</p>
              </div>
            ))
          ) : (
            <p className="text-gray-500 text-sm mt-2">No hay trabajos para este proyecto.</p>
          )}
        </div>
      ))
    ) : (
      <p className="text-gray-500 text-sm mt-4">No hay proyectos disponibles.</p>
    )}
  </div>
);
}
