import { useState } from "react";
import { FilePlus, PlusCircle, ArrowLeft } from "lucide-react";

export default function Contabilidad() {
  const [vista, setVista] = useState("general");
  const [formularioActivo, setFormularioActivo] = useState(null);
  const [tipoGastoNuevo, setTipoGastoNuevo] = useState("");
  const [medioAmpliacion, setMedioAmpliacion] = useState("");
  const [categoriasUsadas, setCategoriasUsadas] = useState(["Combustible", "Transporte", "Personal"]);
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState(null);
  const [descripcion, setDescripcion] = useState("");
  const [fecha, setFecha] = useState("");
  const [monto, setMonto] = useState("");
const toggleDetalle = (id) => {
  setProyectos(prev =>
    prev.map(p =>
      p.id === id ? { ...p, detalleVisible: !p.detalleVisible } : p
    )
  );
};

const finalizarProyecto = (id) => {
  setProyectos(prev =>
    prev.map(p =>
      p.id === id ? { ...p, estado: 'finalizado', detalleVisible: false } : p
    )
  );
};

  const [proyectos, setProyectos] = useState([
    {
      id: 1,
      nombre: "Las Pilas",
      destinado: 50000,
      gastado: 33400,
      estado: "activo",
      detalleVisible: false,
      gastos: [],
      aumentos: []
    },
    {
      id: 2,
      nombre: "Taxisco",
      destinado: 60000,
      gastado: 48500,
      estado: "activo",
      detalleVisible: false,
      gastos: [],
      aumentos: []
    }
  ]);

const crearProyecto = async () => {
  if (!nombre || !destinado) return;

  const { error } = await supabase
    .from("proyectos")
    .insert([{ nombre, destinado: parseFloat(destinado), estado: 'activo' }]);

  if (!error) {
    fetchProyectos(); // recarga lista
    setNombre("");
    setDestinado("");
  } else {
    alert("Error al guardar el proyecto");
  }
};


  const guardarGasto = () => {
    if (!proyectoSeleccionado || !tipoGastoNuevo || !descripcion || !fecha || !monto) return;

    const nuevoGasto = {
      fecha,
      descripcion,
      monto: parseFloat(monto),
      categoria: tipoGastoNuevo
    };

    setProyectos((prev) =>
      prev.map((p) =>
        p.nombre === proyectoSeleccionado
          ? {
              ...p,
              gastos: [...p.gastos, nuevoGasto],
              gastado: p.gastado + nuevoGasto.monto,
            }
          : p
      )
    );

    if (!categoriasUsadas.includes(tipoGastoNuevo)) {
      setCategoriasUsadas([...categoriasUsadas, tipoGastoNuevo]);
    }

    setFormularioActivo(null);
    setProyectoSeleccionado(null);
    setTipoGastoNuevo("");
    setDescripcion("");
    setFecha("");
    setMonto("");
  };

  return (
    <div className="p-6 space-y-6">
      {vista === "general" && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-green-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Total ingresos</p>
            <h2 className="text-xl font-bold text-green-700">Q0.00</h2>
          </div>
          <div className="bg-red-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Total egresos</p>
            <h2 className="text-xl font-bold text-red-700">Q0.00</h2>
          </div>
          <div className="bg-yellow-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Caja chica disponible</p>
            <h2 className="text-xl font-bold text-yellow-700">Q0.00</h2>
          </div>
          <div className="bg-blue-100 p-4 rounded-2xl shadow text-center">
            <p className="text-sm text-gray-600">Balance general</p>
            <h2 className="text-xl font-bold text-blue-700">Q0.00</h2>
          </div>
        </div>
      )}

      {vista === "general" && (
        <div className="flex justify-center mt-8">
          <div
            onClick={() => setVista("proyectos")}
            className="cursor-pointer bg-white shadow-lg rounded-2xl p-6 text-center max-w-xs hover:shadow-xl"
          >
            <p className="text-xl font-bold text-purple-700"> Proyectos en curso</p>
            <p className="text-sm text-gray-500 mt-2">Haz clic para ver el resumen por proyecto</p>
          </div>
        </div>
      )}

      {vista === "proyectos" && (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 my-6">
                <div
                    onClick={() => setFormularioActivo("gasto")}
                    className="cursor-pointer bg-blue-100 hover:bg-blue-200 text-blue-800 font-semibold px-6 py-4 rounded-2xl shadow flex items-center justify-center gap-2"
                >
                    <FilePlus className="w-5 h-5" /> Ingresar Gasto
                </div>

                <div
                    onClick={() => setFormularioActivo("ampliacion")}
                    className="cursor-pointer bg-green-100 hover:bg-green-200 text-green-800 font-semibold px-6 py-4 rounded-2xl shadow flex items-center justify-center gap-2"
                >
                    <PlusCircle className="w-5 h-5" /> Ampliar Presupuesto
                </div>

                <div
                    onClick={() => setVista("general")}
                    className="cursor-pointer bg-purple-100 hover:bg-purple-200 text-purple-800 font-semibold px-6 py-4 rounded-2xl shadow flex items-center justify-center gap-2"
                >
                    <ArrowLeft className="w-4 h-4" /> Volver al balance general
                </div>
                </div>


          {formularioActivo === "gasto" && (
            <div className="mt-4 bg-white rounded-xl shadow-lg p-6 max-w-3xl mx-auto">
              <h3 className="text-xl font-bold text-gray-800 mb-4">Ь Ingresar Gasto</h3>
              <div className="grid md:grid-cols-2 gap-4">
                <select
                  className="p-2 border rounded"
                  value={proyectoSeleccionado || ""}
                  onChange={(e) => setProyectoSeleccionado(e.target.value)}
                >
                  <option value="">Selecciona un proyecto</option>
                  {proyectos.map((p) => (
                    <option key={p.id} value={p.nombre}>{p.nombre}</option>
                  ))}
                </select>
                <input
                  list="categorias"
                  placeholder="Tipo de gasto (ej: Combustible)"
                  className="p-2 border rounded"
                  value={tipoGastoNuevo}
                  onChange={(e) => setTipoGastoNuevo(e.target.value)}
                />
                <datalist id="categorias">
                  {categoriasUsadas.map((c, i) => <option key={i} value={c} />)}
                </datalist>
                <input type="text" placeholder="Descripci贸n" className="p-2 border rounded" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} />
                <input type="date" className="p-2 border rounded" value={fecha} onChange={(e) => setFecha(e.target.value)} />
                <input type="number" placeholder="Monto" className="p-2 border rounded" value={monto} onChange={(e) => setMonto(e.target.value)} />
                <input type="file" className="p-2 border rounded col-span-2" />
                <button onClick={guardarGasto} className="col-span-2 mt-4 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded-xl shadow">
                  Guardar Gasto
                </button>
                <button
                    onClick={() => setFormularioActivo(null)}
                    className="col-span-2 mt-2 bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 rounded-xl shadow"
                    >
                    Cancelar
                </button>
              </div>
            </div>
          )}
          {formularioActivo === "ampliacion" && (
            <div className="mt-4 bg-white rounded-xl shadow-lg p-6 max-w-3xl mx-auto">
              <h3 className="text-xl font-bold text-gray-800 mb-4"> Ampliar Presupuesto</h3>
              <div className="grid md:grid-cols-2 gap-4">
                <select
                  className="p-2 border rounded"
                  value={proyectoSeleccionado || ""}
                  onChange={(e) => setProyectoSeleccionado(e.target.value)}
                >
                  <option value="">Selecciona un proyecto</option>
                  {proyectos.map((p) => (
                    <option key={p.id} value={p.nombre}>{p.nombre}</option>
                  ))}
                </select>
                <select
                  className="p-2 border rounded"
                  value={medioAmpliacion}
                  onChange={(e) => setMedioAmpliacion(e.target.value)}
                >
                  <option value="">Medio de ampliaci贸n</option>
                  <option value="transferencia">Transferencia</option>
                  <option value="deposito">Dep贸sito</option>
                  <option value="efectivo">Efectivo</option>
                </select>
                <input type="text" placeholder="No. de documento" className="p-2 border rounded" />
                <input type="date" className="p-2 border rounded" />
                <input type="number" placeholder="Q. Ampliaci贸n" className="p-2 border rounded" />
                <input type="file" className="p-2 border rounded col-span-2" />
                <button className="col-span-2 mt-4 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 rounded-xl shadow">
                  Guardar Ampliaci贸n
                </button>
                <button
                    onClick={() => setFormularioActivo(null)}
                    className="col-span-2 mt-2 bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 rounded-xl shadow"
                    >
                    Cancelar
                    </button>
              </div>
              

            </div>
            
            
          )}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6 items-start">
            {proyectos
                .sort((a, b) => (a.estado === 'finalizado' ? 1 : -1)) // Activos arriba, finalizados abajo
                .map((proyecto) => (
                <div
                    key={proyecto.id}
                    className={`rounded-2xl shadow p-4 text-center transition-all duration-300 ${
                    proyecto.estado === 'finalizado'
                        ? 'bg-gray-100 text-gray-500'
                        : 'bg-white text-black'
                    }`}
                >
                    <h2 className="text-lg font-bold mb-2"> {proyecto.nombre}</h2>
                    <p> Destinado: Q{proyecto.destinado.toFixed(2)}</p>
                    <p> Gastado: Q{proyecto.gastado.toFixed(2)}</p>
                    <p> Restante: Q{(proyecto.destinado - proyecto.gastado).toFixed(2)}</p>

                    <div className="flex justify-center gap-2 mt-4">
                    {proyecto.estado !== 'finalizado' && (
                        <>
                        <button
                            onClick={() => toggleDetalle(proyecto.id)}
                            className="bg-blue-100 hover:bg-blue-200 px-3 py-1 rounded text-blue-800"
                        >
                            {proyecto.detalleVisible ? "Ocultar Detalle" : "Ver Detalle"}
                        </button>
                        <button
                            onClick={() => finalizarProyecto(proyecto.id)}
                            className="bg-red-100 hover:bg-red-200 px-3 py-1 rounded text-red-700"
                        >
                            Finalizar
                        </button>
                        </>
                    )}
                    </div>

                    {proyecto.detalleVisible && proyecto.estado !== 'finalizado' && (
                    <div className="mt-4 text-left text-sm">
                        <h4 className="font-semibold text-blue-600 mb-2">Ь Gastos por categor铆a:</h4>
                        {[...new Set(proyecto.gastos.map(g => g.categoria))].map((cat) => (
                        <div key={cat} className="mb-2">
                            <strong>{cat}</strong>
                            <ul className="ml-4 list-disc">
                            {proyecto.gastos
                                .filter(g => g.categoria === cat)
                                .map((gasto, idx) => (
                                <li key={idx}>
                                    {gasto.fecha} - {gasto.descripcion}: Q{gasto.monto.toFixed(2)}
                                </li>
                                ))}
                            </ul>
                        </div>
                        ))}
                    </div>
                    )}
                </div>
                ))}
            </div>

        </>
      )}
    </div>
  );
}
