      <button
        className="absolute top-2 right-3 text-red-600 font-bold text-xl"
        onClick={() => {
          setProyectoSeleccionado(null);
          setModoEdicion(false);
        }}
      >
        ×
      </button>

      <h2 className="text-xl font-bold text-purple-900 mb-4">
