    const instaladas = trabajo.instaladas || 0;
    const faltan = trabajo.unidades - instaladas;

    return (
      <div key={index} className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-2">
        <input
          type="text"
          placeholder="Nombre del trabajo"
          value={trabajo.nombre}
          disabled={!modoEdicion}
          onChange={(e) => {
            const nuevos = [...trabajos];
            nuevos[index].nombre = e.target.value;
            setTrabajos(nuevos);
          }}
          className={`p-2 border rounded-xl ${!modoEdicion ? 'bg-gray-100 cursor-not-allowed' : ''}`}
        />
        <input
          type="number"
          placeholder="Unidades"
          value={trabajo.unidades}
          disabled={!modoEdicion}
          onChange={(e) => {
            const nuevos = [...trabajos];
            nuevos[index].unidades = e.target.value;
            setTrabajos(nuevos);
          }}
          className={`p-2 border rounded-xl ${!modoEdicion ? 'bg-gray-100 cursor-not-allowed' : ''}`}
        />
        <div className="p-2 bg-gray-100 rounded-xl text-center">{instaladas}</div>
        <div className="p-2 bg-gray-100 rounded-xl text-center">{faltan}</div>
      </div>
    );
  })}

  {modoEdicion && (
    <button
      onClick={() => setTrabajos([...trabajos, { nombre: "", unidades: "", instaladas: 0 }])}
      className="text-blue-700 text-sm hover:underline mt-1"
