</div>




      <div className="flex justify-end mt-4 gap-2">
        {modoEdicion ? (
          <>
            <button
              onClick={() => {
                setModoEdicion(false);
              }}
              className="bg-gray-300 px-4 py-2 rounded-xl"
            >
              Cancelar
            </button>
            <button
              onClick={async () => {
                const { error } = await supabase
                  .from("proyectos")
                  .update(nuevoProyecto)
                  .eq("id", proyectoSeleccionado.id);

                if (!error) {
                  await supabase.from("proyectos_personal").delete().eq("proyecto_id", proyectoSeleccionado.id);
                  await supabase.from("proyectos_trabajos").delete().eq("proyecto_id", proyectoSeleccionado.id);

                  await supabase.from("proyectos_personal").insert(
                    supervisoresSeleccionados.map((id) => ({
                      proyecto_id: proyectoSeleccionado.id,
                      trabajador_id: id,
                      rol: "supervisor"
                    }))
                  );

                  await supabase.from("proyectos_personal").insert(
                    trabajadoresSeleccionados.map((id) => ({
                      proyecto_id: proyectoSeleccionado.id,
                      trabajador_id: id,
                      rol: "trabajador"
                    }))
                  );

                  await supabase.from("proyectos_trabajos").insert(
                    trabajos.map((t) => ({
                      proyecto_id: proyectoSeleccionado.id,
                      nombre_trabajo: t.nombre,
                      unidades_totales: parseInt(t.unidades)
                    }))
                  );

                  setModoEdicion(false);
                  setProyectoSeleccionado(null);
                  obtenerProyectos();
                } else {
                  alert("Error al guardar cambios");
                }
              }}
              className="bg-blue-600 text-white px-4 py-2 rounded-xl"
            >
              Guardar cambios
            </button>
          </>
        ) : (
          <button
            onClick={async () => {
              setNuevoProyecto({
                nombre: proyectoSeleccionado.nombre,
                descripcion: proyectoSeleccionado.descripcion
              });
              setModoEdicion(true);
              const { data: sup } = await supabase
                .from("proyectos_personal")
                .select("trabajador_id")
                .eq("proyecto_id", proyectoSeleccionado.id)
                .eq("rol", "supervisor");
              setSupervisoresSeleccionados(sup.map((s) => s.trabajador_id));

              const { data: trab } = await supabase
                .from("proyectos_personal")
                .select("trabajador_id")
                .eq("proyecto_id", proyectoSeleccionado.id)
                .eq("rol", "trabajador");
              setTrabajadoresSeleccionados(trab.map((t) => t.trabajador_id));

              const { data: trabs } = await supabase
                .from("proyectos_trabajos")
                .select("nombre_trabajo, unidades_totales")
                .eq("proyecto_id", proyectoSeleccionado.id);
              setTrabajos(
                trabs.map((t) => ({
                  nombre: t.nombre_trabajo,
                  unidades: t.unidades_totales
                }))
              );
            }}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-xl shadow-md"
          >
            ✏️ Editar
          </button>
        )}
        <div className="mt-6 bg-white rounded-xl shadow border border-gray-200 p-4">
  <div className="flex justify-between items-center mb-4">
    <h3 className="text-lg font-bold text-gray-800">💸 Facturas registradas</h3>
    <button
      onClick={() => alert('Aquí se abrirá el formulario para ingresar una factura')}
      className="bg-blue-600 hover:bg-blue-700 text-white text-sm px-4 py-2 rounded-xl shadow"
    >
      ➕ Ingresar factura
    </button>
  </div>

  {facturas.length === 0 ? (
    <p className="text-sm text-gray-500 text-center py-4">No hay gastos registrados</p>
  ) : (
    <div className="overflow-auto rounded-xl">
      <table className="min-w-full text-sm text-left bg-white">
        <thead className="bg-gray-100 text-gray-700">
          <tr>
            <th className="px-4 py-2">Fecha</th>
            <th className="px-4 py-2">No. Factura</th>
            <th className="px-4 py-2">Proveedor</th>
            <th className="px-4 py-2">Se usó para</th>
            <th className="px-4 py-2 text-right">Costo (Q)</th>
          </tr>
        </thead>
        <tbody>
          {facturas.map((factura, i) => (
            <tr key={i} className="border-t">
              <td className="px-4 py-2">{factura.fecha}</td>
              <td className="px-4 py-2">{factura.nofactura}</td>
              <td className="px-4 py-2">{factura.proveedor}</td>
              <td className="px-4 py-2">{factura.usado}</td>
              <td className="px-4 py-2 text-right">Q{factura.costo.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )}
</div>

      </div>
    </div>
  </div>
)}


    </div>
  );
