import React, { useState } from 'react';
import Login from './Login';
import Personal from './componentes/Personal';
import PreciosTrabajos from './componentes/PreciosTrabajos';
import Proyectos from './componentes/Proyectos';
import Contabilidad from './componentes/Contabilidad';
import fondo from './assets/fondo.png';

function App() {
  const [usuario, setUsuario] = useState(null);
  const [tab, setTab] = useState("personal");

  if (!usuario) return <Login onLogin={setUsuario} />;

  const esAdmin = usuario.rol === 'admin';

  return (
    <div
      className="min-h-screen bg-cover bg-no-repeat bg-center p-4"
      style={{ backgroundImage: `url(${fondo})` }}

    >
      <div className="bg-white bg-opacity-90 rounded-xl shadow-lg p-6 max-w-7xl mx-auto">
        <h1 className="text-2xl font-bold text-center mt-4">
          Bienvenido {esAdmin ? 'Administrador' : usuario.rol === 'contabilidad' ? 'Usuario de Contabilidad' : usuario.rol === 'reportador' ? 'Reportador' : 'Usuario'}
        </h1>

        {esAdmin && (
          <div className="flex flex-wrap gap-4 justify-center mt-6">
            <button onClick={() => setTab("personal")} className={tab === "personal" ? "font-bold underline" : ""}>Personal</button>
            <button onClick={() => setTab("precios")} className={tab === "precios" ? "font-bold underline" : ""}>Precios de trabajos</button>
            <button onClick={() => setTab("proyectos")} className={tab === "proyectos" ? "font-bold underline" : ""}>Proyectos</button>
            <button onClick={() => setTab("contabilidad")} className={tab === "contabilidad" ? "font-bold underline" : ""}>Contabilidad</button>
          </div>
        )}

        {/* CONTENIDO SEGÚN LA PESTAÑA */}
        {esAdmin && tab === "personal" && <Personal usuario={usuario} />}
        {esAdmin && tab === "precios" && <PreciosTrabajos />}
        {esAdmin && tab === "proyectos" && <Proyectos />}
        {esAdmin && tab === "contabilidad" && <Contabilidad />}

        {usuario.rol === 'contabilidad' && <div className="mt-6 text-center">Aquí irán las funciones de contabilidad.</div>}
        {usuario.rol === 'reportador' && <div className="mt-6 text-center">Aquí irán las funciones del reportador.</div>}
      </div>
    </div>
  );
}

export default App;
